const updateGamesStatusFromRentals = () => {
  console.log('🔄 تحديث حالة الألعاب من التأجيرات النشطة - النسخة المحسنة');
  
  if (!games.length || !activeRentals.length) {
    console.log('⚠️ لا توجد بيانات كافية للتحديث');
    return;
  }
  
  const updatedGames = games.map(game => {
    // ⚡ **تحقق دقيق من تأجيرات اللعبة**
    const gameRentals = activeRentals.filter(rental => {
      // مطابقة بطرق متعددة
      const matchById = rental.game_id === game.id;
      const matchByName = rental.game_name === game.name;
      const matchByGameId = rental.game?.id === game.id;
      
      return matchById || matchByName || matchByGameId;
    });
    
    const isRented = gameRentals.length > 0;
    const correctStatus = isRented ? 'مؤجرة' : 'متاح';
    
    // تسجيل للتصحيح
    if (isRented && game.status === 'متاح') {
      console.log(`🔄 ${game.name} - مؤجرة (${gameRentals.length} تأجير) ولكن حالتها "متاح"`);
      console.log('📝 تأجيرات اللعبة:', 
        gameRentals.map(r => r.rental_number)
      );
    }
    
    if (game.status !== correctStatus) {
      return { ...game, status: correctStatus };
    }
    
    return game;
  });
  
  // التحقق من التغييرات
  const changedGames = updatedGames.filter((game, index) => 
    game.status !== games[index]?.status
  );
  
  if (changedGames.length > 0) {
    console.log(`✅ تم تحديث حالة ${changedGames.length} لعبة:`);
    changedGames.forEach(game => {
      const oldStatus = games.find(g => g.id === game.id)?.status || 'غير معروف';
      console.log(`   • ${game.name}: ${oldStatus} → ${game.status}`);
    });
    
    setGames(updatedGames);
  } else {
    console.log('✅ جميع الألعاب بحالة صحيحة');
  }
};
