              <div className="quick-stat-icon user-icon">👤</div>
              <div className="quick-stat-info">
                <div className="quick-stat-value">{user?.name}</div>
                <div className="quick-stat-label">
                  {user?.role === 'employee' ? '👷 موظف' : '👑 مدير'}
                </div>
              </div>
            </div>
            
            <div className="quick-stat-card">
              <div className="quick-stat-icon games-icon">🎮</div>
              <div className="quick-stat-info">
                <div className="quick-stat-value">{games.length}</div>
                <div className="quick-stat-label">ألعاب في النظام</div>
              </div>
            </div>
            
            <div className="quick-stat-card">
              <div className="quick-stat-icon time-icon">🕒</div>
              <div className="quick-stat-info">
                <div className="quick-stat-value">{currentEgyptTime}</div>
                <div className="quick-stat-label">التوقيت المصري</div>
              </div>
            </div>
          </div>
        </div>

        {/* الشيفتات الأخيرة */}
        <div className="recent-shifts-section">
          <div className="section-header">
            <h3 className="section-title">
              <span className="section-icon">📝</span>
              الشيفتات الأخيرة
            </h3>
            <button 
              className="refresh-shifts-btn"
              onClick={() => window.location.reload()}
              title="تحديث الصفحة"
            >
              🔄
            </button>
          </div>
          
          {loadingShifts ? (
            <div className="loading-shifts">
              <div className="loading-spinner"></div>
              <p>جاري تحميل الشيفتات الأخيرة...</p>
            </div>
          ) : recentShifts.length > 0 ? (
            <div className="recent-shifts-grid">
              {recentShifts.slice(0, 4).map((shift, index) => (
                <div 
                  key={shift.id} 
                  className={`recent-shift-card ${index === 0 ? 'latest-shift' : ''}`}
                >
                  <div className="shift-card-header">
                    <span className="shift-number">#{shift.id}</span>
                    <span className={`shift-status ${shift.status === 'منتهي' ? 'ended' : 'active'}`}>
                      {shift.status === 'منتهي' ? '✅ منتهي' : '🔥 نشط'}
                    </span>
                  </div>
                  
                  <div className="shift-card-body">
                    <div className="shift-info-row">
                      <span className="info-label">الموظف:</span>
                      <span className="info-value">{shift.employee_name}</span>
                    </div>
                    
                    <div className="shift-info-row">
                      <span className="info-label">بدأ:</span>
                      <span className="info-value">
                        {formatEgyptDateTime(shift.start_time)}
                      </span>
                    </div>
                    
                    {shift.end_time && (
                      <div className="shift-info-row">
                        <span className="info-label">انتهى:</span>
                        <span className="info-value">
                          {formatEgyptDateTime(shift.end_time)}
                        </span>
                      </div>
                    )}
                    
                    <div className="shift-info-row">
                      <span className="info-label">المدة:</span>
                      <span className="info-value">
                        {shift.duration_minutes 
                          ? `${shift.duration_minutes} دقيقة` 
                          : 'مستمر'}
                      </span>
                    </div>
                    
                    {shift.total_revenue && shift.total_revenue > 0 && (
                      <div className="shift-info-row revenue-row">
                        <span className="info-label">الإيرادات:</span>
                        <span className="info-value revenue-value">
                          {formatCurrency(shift.total_revenue)}
                        </span>
                      </div>
                    )}
                  </div>
                  
                  <div className="shift-card-footer">
                    <span className="shift-date">
                      {new Date(shift.start_time).toLocaleDateString('ar-EG', {
                        weekday: 'long',
                        day: 'numeric',
                        month: 'long'
                      })}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="no-recent-shifts">
              <div className="empty-icon">📭</div>
              <p>لا توجد شيفتات سابقة</p>
              <p className="empty-subtext">سيكون هذا أول شيفت لك!</p>
            </div>
          )}
        </div>

        {/* تعليمات سريعة */}
        <div className="quick-guide-section">
          <h3 className="section-title">
            <span className="section-icon">📋</span>
            دليل سريع للبدء
          </h3>
          
          <div className="guide-steps">
            <div className="guide-step">
              <div className="step-number">1</div>
              <div className="step-content">
  <h4>ابدأ الشيفت</h4>
  <p>انقر على &quot;بدء شيفت جديد&quot; لفتح شيفت نظيف</p>
</div>
            </div>
            
            <div className="guide-step">
              <div className="step-number">2</div>
              <div className="step-content">
                <h4>اختر الألعاب</h4>
                <p>انقر على الألعاب المتاحة لإضافتها للتأجير</p>
              </div>
            </div>
            
            <div className="guide-step">
              <div className="step-number">3</div>
              <div className="step-content">
                <h4>أدخل بيانات العميل</h4>
                <p>أدخل اسم العميل ورقم الهاتف</p>
              </div>
            </div>
            
            <div className="guide-step">
              <div className="step-number">4</div>
              <div className="step-content">
  <h4>ابدأ التأجير</h4>
  <p>انقر على &quot;بدء التأجير&quot; لبدء الشيفت</p>
</div>
            </div>
          </div>
        </div>

        {/* معلومات الدعم */}
        <div className="support-info">
          <div className="support-card">
            <div className="support-icon">🆘</div>
            <div className="support-content">
              <h4>بحاجة إلى مساعدة؟</h4>
              <p>للحصول على دعم فني أو استفسارات:</p>
              <div className="support-contacts">
                <a href="tel:0123456789" className="support-contact">
                  📞 0123456789
                </a>
                <span className="support-separator">|</span>
                <span className="support-email">
                  ✉️ support@l3bty.com
                </span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

