      const gamesResponse = await api.get('/games', {
        params: { 
          branch_id: user.branch_id,
          status: 'all'
        }
      });
      
      console.log('ğŸ” Ø§Ø³ØªØ¬Ø§Ø¨Ø© Ø¬Ù„Ø¨ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', gamesResponse);
      
      if (gamesResponse.success) {
        const modifiedGames = (gamesResponse.data || []).map(game => ({
          ...game,
          // Ø§Ù„ØªØ£ÙƒØ¯ Ù…Ù† Ø£Ù† Ø³Ø¹Ø± 15 Ø¯Ù‚ÙŠÙ‚Ø© Ù…ÙˆØ¬ÙˆØ¯
