          calculated_hour_price: (game.price_per_15min || 100) * 4
        }));
        
        console.log('âœ… ØªÙ… ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', modifiedGames.length);
        setGames(modifiedGames);
      } else {
        console.error('âŒ ÙØ´Ù„ ÙÙŠ Ø¬Ù„Ø¨ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', gamesResponse.message);
        setGames([]);
      }
    } catch (error) {
      console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', error);
      
      // Ø·Ø±ÙŠÙ‚Ø© Ø¨Ø¯ÙŠÙ„Ø©: Ø¹Ø±Ø¶ Ù‚Ø§Ø¦Ù…Ø© Ø§ÙØªØ±Ø§Ø¶ÙŠØ©
      const defaultGames = [
        {
          id: 1,
          name: 'Ù…ÙˆØªÙˆØ³ÙŠÙƒÙ„ ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠ',
          category: 'Ø¯Ø±Ø§Ø¬Ø§Øª Ù†Ø§Ø±ÙŠØ©',
          status: 'Ù…ØªØ§Ø­',
          price_per_15min: 50,
          calculated_hour_price: 200,
          image_url: 'electric-bike.jpg',
          description: 'Ù…ÙˆØªÙˆØ³ÙŠÙƒÙ„ ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠ Ø¹Ø§Ù„ÙŠ Ø§Ù„Ø³Ø±Ø¹Ø©'
        },
        {
          id: 2,
          name: 'Ø¯Ø±ÙŠÙØª ÙƒØ§Ø±',
          category: 'Ø³ÙŠØ§Ø±Ø§Øª',
          status: 'Ù…ØªØ§Ø­',
          price_per_15min: 70,
          calculated_hour_price: 280,
          image_url: 'drift-car.jpg',
          description: 'Ø³ÙŠØ§Ø±Ø© Ø¯Ø±ÙØª Ø±ÙŠØ§Ø¶ÙŠØ©'
        },
        {
          id: 3,
          name: 'Ø³ÙƒÙˆØªØ± ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠ',
          category: 'ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©',
          status: 'Ù…ØªØ§Ø­',
          price_per_15min: 40,
          calculated_hour_price: 160,
          image_url: 'electric-scooter.jpg',
          description: 'Ø³ÙƒÙˆØªØ± ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠ Ø®ÙÙŠÙ Ø§Ù„ÙˆØ²Ù†'
        }
      ];
      
      console.log('âš ï¸ Ø§Ø³ØªØ®Ø¯Ø§Ù… Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„Ø§ÙØªØ±Ø§Ø¶ÙŠØ© Ø¨Ø³Ø¨Ø¨ Ø§Ù„Ø®Ø·Ø£');
      setGames(defaultGames);
    }
  };

  const loadActiveRentals = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.get('/rentals', {
        params: { 
          branch_id: user.branch_id,
          status: 'Ù†Ø´Ø·'
        }
      });
      
      if (response.success) {
        setActiveRentals(response.data || []);
      }
    } catch (error) {
      console.error('Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø©:', error);
      setActiveRentals([]);
    }
  };

  // ğŸ® Ø¹Ù†Ø¯ Ø§Ù„Ù†Ù‚Ø± Ø¹Ù„Ù‰ Ù„Ø¹Ø¨Ø© (Ù…Ù† Ø§Ù„Ù‚Ø§Ø¦Ù…Ø© Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠØ©)
  const handleGameClick = (game) => {
    if (!currentShift) {
      showNotification('error', 'ÙŠØ¬Ø¨ Ø¨Ø¯Ø¡ Ø´ÙŠÙØª Ø£ÙˆÙ„Ø§Ù‹ Ù‚Ø¨Ù„ Ø¨Ø¯Ø¡ Ø£ÙŠ ØªØ£Ø¬ÙŠØ±Ø§Øª');
      return;
    }
    
