      setQuantities({
        ...quantities,
        [game.id]: 1
      });
    } else {
      setQuantities({
        ...quantities,
        [game.id]: (quantities[game.id] || 1) + 1
      });
    }
    
    setShowAddMoreGames(false);
    showNotification('success', `ØªÙ…Øª Ø¥Ø¶Ø§ÙØ© ${game.name} Ø¥Ù„Ù‰ Ù‚Ø§Ø¦Ù…Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ±`);
  };

  // â• Ø²ÙŠØ§Ø¯Ø© ÙƒÙ…ÙŠØ© Ù„Ø¹Ø¨Ø©
  const increaseQuantity = (gameId) => {
    const game = selectedGames.find(g => g.id === gameId);
    if (!game) return;
    
    const currentQuantity = quantities[gameId] || 1;
    setQuantities({
      ...quantities,
      [gameId]: currentQuantity + 1
    });
  };

  // â– ØªÙ‚Ù„ÙŠÙ„ ÙƒÙ…ÙŠØ© Ù„Ø¹Ø¨Ø©
  const decreaseQuantity = (gameId) => {
    const currentQuantity = quantities[gameId] || 1;
    if (currentQuantity > 1) {
