// src/AppRoutes.jsx - ÿßŸÑŸÜÿ≥ÿÆÿ© ÿßŸÑŸÖÿ≠ÿ≥ŸÜÿ©
import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './context/AuthContext';
import Layout from './components/Layout';
import Login from './pages/Login';

// ÿµŸÅÿ≠ÿßÿ™ ÿßŸÑŸÖÿØŸäÿ±
import AdminDashboard from './pages/admin/Dashboard';
import UsersManagement from './pages/admin/UsersManagement';
import BranchesManagement from './pages/admin/BranchesManagement';
import RentalsManagement from './pages/admin/RentalsManagement';
import RentalsRevenueAnalysis from './pages/admin/RentalsRevenueAnalysis';
import Settings from './pages/admin/Settings';
import GamesManagement from './pages/admin/GamesManagement';

// ÿµŸÅÿ≠ÿßÿ™ ÿßŸÑŸÖŸàÿ∏ŸÅ
import EmployeeDashboard from './pages/employee/Dashboard';
import Rentals from './pages/employee/Rentals';

// üî• ÿ•ÿ∂ÿßŸÅÿ© ÿµŸÅÿ≠ÿ© ÿßŸÑŸÖÿ§ÿ¥ÿ±ÿßÿ™ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ±ÿ©
import LiveMetrics from './pages/employee/LiveMetrics';

const ProtectedRoute = ({ children, allowedRoles = [] }) => {
  const { isAuthenticated, user, loading } = useAuth();
  
  if (loading) {
    return <div className="loading-screen">ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ...</div>;
  }
  
  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }
  
  if (allowedRoles.length > 0 && !allowedRoles.includes(user?.role)) {
    return <Navigate to="/unauthorized" />;
  }
  
  return children;
};

const AppRoutes = () => {
  return (
    <Routes>
      {/* ÿßŸÑÿµŸÅÿ≠ÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ© */}
      <Route path="/login" element={<Login />} />
      
      {/* Routes ŸÑŸÑŸÖÿØŸäÿ± */}
      <Route path="/admin/*" element={
        <ProtectedRoute allowedRoles={['admin']}>
          <Layout>
            <Routes>
              <Route index element={<Navigate to="dashboard" replace />} />
              <Route path="dashboard" element={<AdminDashboard />} />
              <Route path="users" element={<UsersManagement />} />
              <Route path="branches" element={<BranchesManagement />} />
              <Route path="games" element={<GamesManagement />} />
              <Route path="rentals" element={<RentalsManagement />} />
              <Route path="rentals/completed" element={<RentalsManagement showCompleted />} />
              <Route path="rentals/active" element={<RentalsManagement showActive />} />
              <Route path="revenue-analysis" element={<RentalsRevenueAnalysis />} />
              <Route path="settings" element={<Settings />} />
              <Route path="live-metrics" element={<LiveMetrics />} />
            </Routes>
          </Layout>
        </ProtectedRoute>
      } />
      
      {/* Routes ŸÑŸÑŸÖŸàÿ∏ŸÅ ŸàŸÖÿØŸäÿ± ÿßŸÑŸÅÿ±ÿπ */}
      <Route path="/employee/*" element={
        <ProtectedRoute allowedRoles={['employee', 'branch_manager']}>
          <Layout>
            <Routes>
              <Route index element={<Navigate to="dashboard" replace />} />
              <Route path="dashboard" element={<EmployeeDashboard />} />
              <Route path="rentals" element={<Rentals />} />
              <Route path="live-metrics" element={<LiveMetrics />} />
              {/* üî• ÿ•ÿ∂ÿßŸÅÿ© ÿµŸÅÿ≠ÿ© ÿ≥ÿ±Ÿäÿπÿ© ŸÑŸÑŸàÿµŸàŸÑ ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ± */}
              <Route path="quick-rental" element={
                <Navigate to="/employee/rentals?quick=true" replace />
              } />
            </Routes>
          </Layout>
        </ProtectedRoute>
      } />
      
      {/* ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™Ÿàÿ¨ŸäŸá ÿßŸÑÿ∞ŸÉŸä */}
      <Route path="/" element={
        <ProtectedRoute>
          <Navigate to="/employee/dashboard" replace />
        </ProtectedRoute>
      } />
      
      {/* ÿµŸÅÿ≠ÿ© ÿ¥ŸäŸÅÿ™ ÿ∫Ÿäÿ± ŸÜÿ¥ÿ∑ */}
      <Route path="/no-active-shift" element={
        <ProtectedRoute>
          <Layout>
            <div className="no-shift-page">
              <h2>‚ö†Ô∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑</h2>
              <p>Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ŸÑŸÑŸàÿµŸàŸÑ ÿ•ŸÑŸâ ÿµŸÅÿ≠ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±</p>
              <button 
                onClick={() => window.location.href = '/employee/dashboard'}
                className="btn-primary"
              >
                ÿßŸÑÿπŸàÿØÿ© ŸÑŸÑÿØÿßÿ¥ÿ®Ÿàÿ±ÿØ
              </button>
            </div>
          </Layout>
        </ProtectedRoute>
      } />
      
      {/* ÿµŸÅÿ≠ÿ© ÿ∫Ÿäÿ± ŸÖÿµÿ±ÿ≠ */}
      <Route path="/unauthorized" element={
        <div className="unauthorized-page">
          <h1>‚õî ÿ∫Ÿäÿ± ŸÖÿµÿ±ÿ≠ ÿ®ÿßŸÑŸàÿµŸàŸÑ</h1>
          <p>ŸÑŸäÿ≥ ŸÑÿØŸäŸÉ ÿßŸÑÿµŸÑÿßÿ≠Ÿäÿßÿ™ ÿßŸÑŸÉÿßŸÅŸäÿ© ŸÑŸÑŸàÿµŸàŸÑ ÿ•ŸÑŸâ Ÿáÿ∞Ÿá ÿßŸÑÿµŸÅÿ≠ÿ©</p>
          <button 
            onClick={() => window.location.href = '/employee/dashboard'}
            className="btn-primary"
          >
            ÿßŸÑÿπŸàÿØÿ© ŸÑŸÑÿØÿßÿ¥ÿ®Ÿàÿ±ÿØ
          </button>
        </div>
      } />
      
