import React, { useState, useEffect, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../../services/api';
import authService from '../../services/authService';
import './EmployeeDashboard.css';
import {
  Gamepad2, Zap, DollarSign, Clock, Users,
  CheckCircle, RefreshCw, Building,
  Loader2, Calendar, TrendingUp, User,
  LogOut, Bell, Package, Activity,
  ShoppingCart, ChevronRight
} from 'lucide-react';

const EmployeeDashboard = () => {
  const navigate = useNavigate();
  
  // ÿ≠ÿßŸÑÿ© ŸÖÿ®ÿ≥ÿ∑ÿ© ŸÑŸÑÿ∫ÿßŸäÿ©
  const [user, setUser] = useState(null);
  const [currentShift, setCurrentShift] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  // ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿ®ÿ≥Ÿäÿ∑ÿ©
  const [stats, setStats] = useState({
    availableGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

  // ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑÿπŸÖŸÑÿ©
  const formatCurrency = (amount) => {
    if (!amount && amount !== 0) return '0 ÿ¨.ŸÖ';
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0
    }).format(amount);
  };

  // ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑŸàŸÇÿ™
  const formatTime = (dateString) => {
    if (!dateString) return '--:--';
    try {
      const date = new Date(dateString);
      return date.toLocaleTimeString('ar-EG', {
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '--:--';
    }
  };

  // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ©
  const loadDashboardData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // 1. ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
      const userData = await authService.getCurrentUser();
      setUser(userData);
      
      // 2. ÿ¨ŸÑÿ® ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÜÿ¥ÿ∑
      const shiftResponse = await api.getActiveShift();
      const shift = shiftResponse.success ? shiftResponse.data : null;
      setCurrentShift(shift);
      
      if (!shift) {
        setStats({
          availableGames: 0,
          activeRentals: 0,
          todayRevenue: 0,
          todayRentals: 0
        });
        return;
      }
      
      // 3. ÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©
      let availableCount = 0;
      try {
        const gamesResponse = await api.getGames({ branch_id: userData?.branch_id });
        if (gamesResponse.success) {
          const games = gamesResponse.data || [];
          availableCount = games.filter(g => g.status === 'ŸÖÿ™ÿßÿ≠').length;
        }
      } catch (error) {
        console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑ ÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ®:', error.message);
      }
      
      // 4. ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
      let activeCount = 0;
      try {
        const activeResponse = await api.get('/rentals/active', {
          params: { branch_id: userData?.branch_id }
        });
        if (activeResponse.success) {
          const active = activeResponse.data || [];
          activeCount = active.filter(r => r.shift_id === shift.id).length;
        }
      } catch (error) {
        console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑ ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©:', error.message);
      }
      
      // 5. ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© ÿßŸÑŸäŸàŸÖ
      let todayRevenue = 0;
      let todayRentals = 0;
      
      try {
        const today = new Date().toISOString().split('T')[0];
        const completedResponse = await api.get('/rentals/completed', {
          params: { 
            shift_id: shift.id,
            date: today
          }
        });
        
        if (completedResponse.success) {
          const completed = completedResponse.data || [];
          todayRentals = completed.length;
          todayRevenue = completed.reduce((sum, r) => 
            sum + (parseFloat(r.final_amount) || 0), 0
          );
        }
      } catch (error) {
        console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑ ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©:', error.message);
      }
      
      // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
      setStats({
        availableGames: availableCount,
        activeRentals: activeCount,
        todayRevenue: todayRevenue,
        todayRentals: todayRentals
      });
      
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', error);
      setError('ŸÅÿ¥ŸÑ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™');
    } finally {
      setLoading(false);
    }
  };

  // ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØ
  const handleStartShift = async () => {
    try {
      setLoading(true);
      const response = await api.post('/shifts/start');
      
      if (response.success) {
        setCurrentShift(response.data);
        await loadDashboardData();
      } else {
        alert('‚ùå ŸÅÿ¥ŸÑ ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™');
      }
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£:', error);
      alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™');
    } finally {
      setLoading(false);
    }
  };

  // ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸàŸÑŸä
  useEffect(() => {
    loadDashboardData();
    
    // ÿ™ÿ≠ÿØŸäÿ´ ŸÉŸÑ 30 ÿ´ÿßŸÜŸäÿ©
    const interval = setInterval(loadDashboardData, 30000);
    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <div className="employee-dashboard-loading">
        <Loader2 className="spinner" size={48} />
        <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...</p>
      </div>
    );
  }

  return (
    <div className="employee-dashboard">
      {/* ÿ±ÿ£ÿ≥ ÿßŸÑÿµŸÅÿ≠ÿ© */}
      <div className="dashboard-header">
        <div>
          <h1 className="dashboard-title">
            <Activity size={32} />
            ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ
          </h1>
          <p className="dashboard-subtitle">
            ŸÖÿ±ÿ≠ÿ®ÿßŸã {user?.name || 'ŸÖŸàÿ∏ŸÅ'} 
            {user?.branch_name && ` - ${user.branch_name}`}
          </p>
        </div>
        
        <div className="header-actions">
          <button 
            onClick={loadDashboardData}
            className="btn-refresh"
            title="ÿ™ÿ≠ÿØŸäÿ´"
          >
            <RefreshCw size={20} />
          </button>
        </div>
      </div>

      {/* ÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑÿÆÿ∑ÿ£ */}
      {error && (
        <div className="error-message">
          <Bell size={20} />
          <span>{error}</span>
          <button onClick={() => setError(null)}>√ó</button>
        </div>
      )}

      {/* ÿ≠ÿßŸÑÿ© ÿßŸÑÿ¥ŸäŸÅÿ™ */}
      <div className="shift-status">
        {currentShift ? (
          <div className="shift-active">
            <div className="shift-info">
              <Clock size={20} />
              <div>
                <span className="shift-label">ÿßŸÑÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑</span>
                <span className="shift-time">
                  ÿ®ÿØÿ£ ŸÅŸä {formatTime(currentShift.start_time)}
                </span>
              </div>
            </div>
            <Link to="/employee/rentals" className="btn-primary">
              <ShoppingCart size={18} />
              ÿµŸÅÿ≠ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
            </Link>
          </div>
        ) : (
          <div className="shift-inactive">
            <span>ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑</span>
            <button onClick={handleStartShift} className="btn-start">
              <Zap size={18} />
              ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØ
            </button>
          </div>
        )}
      </div>

      {/* ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ© - 4 ÿ®ÿ∑ÿßŸÇÿßÿ™ ŸÅŸÇÿ∑ */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon games">
            <Gamepad2 size={24} />
          </div>
          <div className="stat-content">
            <span className="stat-value">{stats.availableGames}</span>
            <span className="stat-label">ÿ£ŸÑÿπÿßÿ® ŸÖÿ™ÿßÿ≠ÿ©</span>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon active">
            <Zap size={24} />
          </div>
          <div className="stat-content">
            <span className="stat-value">{stats.activeRentals}</span>
            <span className="stat-label">ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÜÿ¥ÿ∑ÿ©</span>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon completed">
            <CheckCircle size={24} />
          </div>
          <div className="stat-content">
            <span className="stat-value">{stats.todayRentals}</span>
            <span className="stat-label">ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸäŸàŸÖ</span>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon revenue">
            <DollarSign size={24} />
          </div>
          <div className="stat-content">
            <span className="stat-value">{formatCurrency(stats.todayRevenue)}</span>
            <span className="stat-label">ÿ•Ÿäÿ±ÿßÿØ ÿßŸÑŸäŸàŸÖ</span>
          </div>
        </div>
      </div>

      {/* ÿ£ÿ≤ÿ±ÿßÿ± ÿßŸÑÿ™ŸÜŸÇŸÑ ÿßŸÑÿ≥ÿ±Ÿäÿπ */}
      <div className="quick-actions">
        <Link 
          to="/employee/rentals" 
          className={`quick-action ${!currentShift ? 'disabled' : ''}`}
          onClick={(e) => {
            if (!currentShift) {
              e.preventDefault();
              alert('‚ö†Ô∏è Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ£ŸàŸÑÿßŸã');
            }
          }}
        >
          <ShoppingCart size={24} />
          <div>
            <h3>ÿ™ÿ£ÿ¨Ÿäÿ± ÿ¨ÿØŸäÿØ</h3>
            <p>ÿ®ÿØÿ° ÿ™ÿ£ÿ¨Ÿäÿ± ŸÑÿπÿ®ÿ©</p>
          </div>
          <ChevronRight size={20} />
        </Link>

        <Link 
          to="/employee/rentals?tab=active" 
          className={`quick-action ${!currentShift ? 'disabled' : ''}`}
          onClick={(e) => {
            if (!currentShift) {
              e.preventDefault();
              alert('‚ö†Ô∏è Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ£ŸàŸÑÿßŸã');
            }
          }}
        >
          <Clock size={24} />
          <div>
            <h3>ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÜÿ¥ÿ∑ÿ©</h3>
            <p>{stats.activeRentals} ÿ™ÿ£ÿ¨Ÿäÿ± ÿ¨ÿßÿ±Ÿä</p>
          </div>
          <ChevronRight size={20} />
        </Link>

        <Link 
          to="/employee/rentals?tab=completed" 
          className={`quick-action ${!currentShift ? 'disabled' : ''}`}
          onClick={(e) => {
            if (!currentShift) {
              e.preventDefault();
              alert('‚ö†Ô∏è Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ£ŸàŸÑÿßŸã');
            }
          }}
        >
          <CheckCircle size={24} />
          <div>
            <h3>ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÖŸÉÿ™ŸÖŸÑÿ©</h3>
            <p>{stats.todayRentals} ÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑŸäŸàŸÖ</p>
          </div>
          <ChevronRight size={20} />
        </Link>
      </div>

      {/* ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿ≥ÿ±Ÿäÿπÿ© */}
      <div className="info-card">
        <h3>ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿ≥ÿ±Ÿäÿπÿ©</h3>
        <div className="info-grid">
          <div>
            <Calendar size={16} />
            <span>{new Date().toLocaleDateString('ar-EG')}</span>
          </div>
          <div>
            <User size={16} />
            <span>{user?.name || 'ŸÖŸàÿ∏ŸÅ'}</span>
          </div>
          <div>
            <Building size={16} />
            <span>{user?.branch_name || 'ÿßŸÑŸÅÿ±ÿπ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä'}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmployeeDashboard;
