import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { 
  Search, Filter, RefreshCw, Eye, Edit, Trash2,
  CheckCircle, XCircle, Building, User, Calendar,
  DollarSign, Save, ChevronLeft, ChevronRight,
  AlertCircle, X, Clock, Smartphone, Plus,
  Download, BarChart3, MoreVertical, Shield,
  Play, Pause, StopCircle, Timer, Award,
  TrendingUp, Users, Gamepad2, Phone, Mail,
  MapPin, Printer, CreditCard, Wallet,
  History, Target, Activity, Zap, Coffee
} from 'lucide-react';
import api from '../../services/api';
import authService from '../../services/authService';
import './RentalsManagement.css';

const RentalsManagement = () => {
  // ==================== STATES ====================
  const [rentals, setRentals] = useState([]);
  const [filteredRentals, setFilteredRentals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  
  // User & Shift Data
  const [currentUser, setCurrentUser] = useState(null);
  const [currentShift, setCurrentShift] = useState(null);
  const [userBranch, setUserBranch] = useState(null);
  
  // Data States
  const [branches, setBranches] = useState([]);
  const [games, setGames] = useState([]);
  const [employees, setEmployees] = useState([]);
  
  // Modal States
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [showStatsModal, setShowStatsModal] = useState(false);
  const [showRentalDetailsModal, setShowRentalDetailsModal] = useState(false);
  
  // Selection & Editing
  const [selectedRental, setSelectedRental] = useState(null);
  const [editedRental, setEditedRental] = useState(null);
  const [rentalToDelete, setRentalToDelete] = useState(null);
  
  // Filter States
  const [filters, setFilters] = useState({
    status: 'all',
    branch_id: 'all',
    payment_status: 'all',
    dateFrom: '',
    dateTo: '',
    shift_only: true, // ÿπÿ±ÿ∂ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™ ŸÅŸÇÿ∑ ÿßŸÅÿ™ÿ±ÿßÿ∂ŸäÿßŸã
    employee_only: true // ÿπÿ±ÿ∂ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸàÿ∏ŸÅ ŸÅŸÇÿ∑
  });
  
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(20);
  
  // Statistics
  const [stats, setStats] = useState({
    total: 0,
    active: 0,
    completed: 0,
    cancelled: 0,
    totalRevenue: 0,
    averageRental: 0,
    todayRevenue: 0,
    shiftRevenue: 0,
    shiftRentals: 0,
    shiftActive: 0,
    shiftCompleted: 0,
    shiftDuration: 0
  });

  // ==================== LOAD USER & SHIFT DATA ====================
  const loadUserAndShift = useCallback(async () => {
    try {
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
      const user = authService.getCurrentUser();
      setCurrentUser(user);
      
      if (user?.branch_id) {
        // ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÅÿ±ÿπ
        const branchRes = await api.getBranch(user.branch_id);
        if (branchRes?.success) {
          setUserBranch(branchRes.data);
        }
      }
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä
      const shiftRes = await api.getCurrentShift();
      if (shiftRes?.success && shiftRes.data) {
        const shift = shiftRes.data;
        // ÿßŸÑÿ™ÿ£ŸÉÿØ ÿ£ŸÜ ÿßŸÑÿ¥ŸäŸÅÿ™ ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿ≠ÿßŸÑŸä
        if (shift.employee_id === user?.id || shift.employee_name === user?.name) {
          setCurrentShift(shift);
          console.log('‚úÖ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä:', shift);
        } else {
          console.log('‚ÑπÔ∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑ ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿ≠ÿßŸÑŸä');
        }
      } else {
        console.log('‚ÑπÔ∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑');
      }
      
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:', error);
    }
  }, []);

  // ==================== LOAD DATA ====================
  const loadAllData = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ£ŸàŸÑÿßŸã
      await loadUserAndShift();
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ
      const branchesRes = await api.getBranches();
      if (branchesRes?.success) {
        setBranches(branchesRes.data || []);
      }
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÖÿπ ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ£ÿÆÿ∑ÿßÿ°
      let rentalsRes;
      try {
        // ŸÖÿ≠ÿßŸàŸÑÿ© ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÖÿπ ÿßŸÑŸÅŸÑÿßÿ™ÿ±
        const params = {
          limit: 1000,
          include_game: true,
          include_branch: true,
          include_employee: true
        };
        
        // ÿ•ÿ∂ÿßŸÅÿ© ŸÅŸÑÿ™ÿ± ÿßŸÑŸÅÿ±ÿπ ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÑÿØŸäŸá ŸÅÿ±ÿπ ŸÖÿ≠ÿØÿØ
        if (currentUser?.branch_id && filters.branch_id === 'all') {
          params.branch_id = currentUser.branch_id;
        }
        
        // ÿ•ÿ∂ÿßŸÅÿ© ŸÅŸÑÿ™ÿ± ÿßŸÑÿ¥ŸäŸÅÿ™
        if (filters.shift_only && currentShift?.id) {
          params.shift_id = currentShift.id;
        }
        
        // ÿ•ÿ∂ÿßŸÅÿ© ŸÅŸÑÿ™ÿ± ÿßŸÑŸÖŸàÿ∏ŸÅ
        if (filters.employee_only && currentUser?.id) {
          params.employee_id = currentUser.id;
        }
        
        rentalsRes = await api.getRentals(params);
      } catch (apiError) {
        console.log('‚ö†Ô∏è ŸÖÿ≠ÿßŸàŸÑÿ© API ÿ®ÿØŸäŸÑÿ©...');
        rentalsRes = await api.getRentalsByPermission({ 
          limit: 1000,
          include_game: true
        });
      }
      
      if (rentalsRes?.success) {
        const rentalsData = rentalsRes.data || [];
        setRentals(rentalsData);
        calculateStats(rentalsData);
        
        console.log(`üìä ÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ${rentalsData.length} ÿ™ÿ£ÿ¨Ÿäÿ±`);
        
        // ÿπÿ±ÿ∂ ŸÖŸÑÿÆÿµ ÿ≥ÿ±Ÿäÿπ
        if (currentShift) {
          const shiftRentals = rentalsData.filter(r => r.shift_id === currentShift.id);
          console.log(`üìã ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä: ${shiftRentals.length}`);
        }
      }
      
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', error);
      setError(error.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™');
    } finally {
      setLoading(false);
    }
  }, [currentUser, currentShift, filters.shift_only, filters.employee_only, filters.branch_id, loadUserAndShift]);

  // ==================== STATISTICS ====================
  const calculateStats = (rentalsData) => {
    const active = rentalsData.filter(r => r.status === 'ŸÜÿ¥ÿ∑').length;
    const completed = rentalsData.filter(r => r.status === 'ŸÖŸÉÿ™ŸÖŸÑ').length;
    const cancelled = rentalsData.filter(r => r.status === 'ŸÖŸÑÿ∫Ÿä').length;
    
    const totalRevenue = rentalsData
      .filter(r => r.payment_status === 'ŸÖÿØŸÅŸàÿπ')
      .reduce((sum, r) => sum + (parseFloat(r.total_amount) || 0), 0);
    
    const today = new Date().toISOString().split('T')[0];
    const todayRevenue = rentalsData
      .filter(r => r.start_time && r.start_time.split('T')[0] === today && r.payment_status === 'ŸÖÿØŸÅŸàÿπ')
      .reduce((sum, r) => sum + (parseFloat(r.total_amount) || 0), 0);
    
    // ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä
    let shiftRevenue = 0;
    let shiftRentals = 0;
    let shiftActive = 0;
    let shiftCompleted = 0;
    
    if (currentShift) {
      const shiftRentalsData = rentalsData.filter(r => r.shift_id === currentShift.id);
      shiftRentals = shiftRentalsData.length;
      shiftActive = shiftRentalsData.filter(r => r.status === 'ŸÜÿ¥ÿ∑').length;
      shiftCompleted = shiftRentalsData.filter(r => r.status === 'ŸÖŸÉÿ™ŸÖŸÑ').length;
      shiftRevenue = shiftRentalsData
        .filter(r => r.payment_status === 'ŸÖÿØŸÅŸàÿπ')
        .reduce((sum, r) => sum + (parseFloat(r.total_amount) || 0), 0);
      
      // ÿ≠ÿ≥ÿßÿ® ŸÖÿØÿ© ÿßŸÑÿ¥ŸäŸÅÿ™
      if (currentShift.start_time) {
        const start = new Date(currentShift.start_time);
        const now = new Date();
        const diffMs = now - start;
        const diffHours = Math.floor(diffMs / 3600000);
        const diffMinutes = Math.floor((diffMs % 3600000) / 60000);
        setStats(prev => ({
          ...prev,
          shiftDuration: `${diffHours}:${diffMinutes.toString().padStart(2, '0')}`
        }));
      }
    }
    
    const paidRentals = rentalsData.filter(r => r.payment_status === 'ŸÖÿØŸÅŸàÿπ');
    const averageRental = paidRentals.length > 0 
      ? totalRevenue / paidRentals.length 
      : 0;
    
    setStats(prev => ({
      ...prev,
      total: rentalsData.length,
      active,
      completed,
      cancelled,
      totalRevenue,
      averageRental,
      todayRevenue,
      shiftRevenue,
      shiftRentals,
      shiftActive,
      shiftCompleted
    }));
  };

  // ==================== EFFECTS ====================
  useEffect(() => {
    loadAllData();
  }, []);

  useEffect(() => {
    applyFiltersAndSearch();
  }, [rentals, filters, searchTerm]);

  // ==================== FILTERS & SEARCH ====================
  const applyFiltersAndSearch = useCallback(() => {
    let result = [...rentals];
    
    // ÿßŸÑÿ®ÿ≠ÿ´
    if (searchTerm.trim()) {
      const term = searchTerm.toLowerCase();
      result = result.filter(rental => {
        return (
          (rental.customer_name?.toLowerCase().includes(term)) ||
          (rental.rental_number?.toLowerCase().includes(term)) ||
          (rental.customer_phone?.includes(term)) ||
          (rental.game_name?.toLowerCase().includes(term)) ||
          (rental.employee_name?.toLowerCase().includes(term)) ||
          (rental.branch_name?.toLowerCase().includes(term))
        );
      });
    }
    
    // ŸÅŸÑÿ™ÿ± ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä ŸÅŸÇÿ∑
    if (filters.shift_only && currentShift?.id) {
      result = result.filter(r => r.shift_id === currentShift.id);
    }
    
    // ŸÅŸÑÿ™ÿ± ÿßŸÑŸÖŸàÿ∏ŸÅ ÿßŸÑÿ≠ÿßŸÑŸä ŸÅŸÇÿ∑
    if (filters.employee_only && currentUser?.id) {
      result = result.filter(r => r.employee_id === currentUser.id || r.created_by === currentUser.id);
    }
    
    // ÿßŸÑŸÅŸÑÿßÿ™ÿ± ÿßŸÑÿ£ÿÆÿ±Ÿâ
    if (filters.status !== 'all') {
      result = result.filter(r => r.status === filters.status);
    }
    
    if (filters.branch_id !== 'all') {
      result = result.filter(r => r.branch_id == filters.branch_id);
    }
    
    if (filters.payment_status !== 'all') {
      result = result.filter(r => r.payment_status === filters.payment_status);
    }
    
    // ŸÅŸÑÿ™ÿ±ÿ© ÿßŸÑÿ™ÿßÿ±ŸäÿÆ
    if (filters.dateFrom) {
      result = result.filter(r => {
        if (!r.start_time) return false;
        return r.start_time.split('T')[0] >= filters.dateFrom;
      });
    }
    
    if (filters.dateTo) {
      result = result.filter(r => {
        if (!r.start_time) return false;
        return r.start_time.split('T')[0] <= filters.dateTo;
      });
    }
    
    setFilteredRentals(result);
    setCurrentPage(1);
  }, [rentals, filters, searchTerm, currentShift, currentUser]);

  const handleResetFilters = () => {
    setFilters({
      status: 'all',
      branch_id: currentUser?.branch_id ? 'all' : 'all',
      payment_status: 'all',
      dateFrom: '',
      dateTo: '',
      shift_only: true,
      employee_only: true
    });
    setSearchTerm('');
  };

  // ==================== RENTAL ACTIONS ====================
  const handleEditRental = (rental) => {
    setSelectedRental(rental);
    setEditedRental({ 
      ...rental,
      total_amount: parseFloat(rental.total_amount) || 0,
      paid_amount: parseFloat(rental.paid_amount) || 0,
      duration_minutes: parseInt(rental.duration_minutes) || 0
    });
    setShowEditModal(true);
  };

  const handleViewRentalDetails = (rental) => {
    setSelectedRental(rental);
    setShowRentalDetailsModal(true);
  };

  const handleSaveEdit = async () => {
    if (!editedRental) return;
    
    try {
      setSaving(true);
      setError(null);
      
      // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      if (!editedRental.customer_name?.trim()) {
        throw new Error('ÿßÿ≥ŸÖ ÿßŸÑÿπŸÖŸäŸÑ ŸÖÿ∑ŸÑŸàÿ®');
      }
      
      const updateData = {
        customer_name: editedRental.customer_name.trim(),
        customer_phone: editedRental.customer_phone?.trim() || '',
        total_amount: parseFloat(editedRental.total_amount) || 0,
        paid_amount: parseFloat(editedRental.paid_amount) || 0,
        duration_minutes: parseInt(editedRental.duration_minutes) || 0,
        payment_method: editedRental.payment_method || 'ŸÉÿßÿ¥',
        payment_status: editedRental.payment_status || 'ÿ∫Ÿäÿ± ŸÖÿØŸÅŸàÿπ',
        status: editedRental.status || 'ŸÜÿ¥ÿ∑',
        notes: editedRental.notes || ''
      };
      
      // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
      const response = await api.updateRental(editedRental.id, updateData);
      
      if (response?.success) {
        showNotification('ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠', 'success');
        setShowEditModal(false);
        loadAllData();
      } else {
        throw new Error(response?.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
      }
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ≠ŸÅÿ∏ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', error);
      setError(error.message);
      showNotification(error.message, 'error');
    } finally {
      setSaving(false);
    }
  };

  const handleDeleteRental = async () => {
    if (!rentalToDelete) return;
    
    try {
      setSaving(true);
      const response = await api.deleteRental(rentalToDelete.id);
      
      if (response?.success) {
        showNotification('ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠', 'success');
        setShowDeleteConfirm(false);
        loadAllData();
      } else {
        throw new Error(response?.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ≠ÿ∞ŸÅ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
      }
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ≠ÿ∞ŸÅ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', error);
      showNotification(error.message, 'error');
    } finally {
      setSaving(false);
    }
  };

  const handleCompleteRental = async (rental) => {
    if (!window.confirm(`ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± #${rental.rental_number}ÿü`)) return;
    
    try {
      const response = await api.completeRental(rental.id, {
        payment_method: rental.payment_method || 'ŸÉÿßÿ¥'
      });
      
      if (response?.success) {
        showNotification('‚úÖ ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠', 'success');
        loadAllData();
      } else {
        throw new Error(response?.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
      }
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', error);
      showNotification(error.message, 'error');
    }
  };

  const handleCancelRental = async (rental) => {
    if (!window.confirm(`ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± #${rental.rental_number}ÿü`)) return;
    
    try {
      const response = await api.cancelRental(rental.id, rental.paid_amount || 0, 'ÿ•ŸÑÿ∫ÿßÿ° ŸäÿØŸàŸä');
      
      if (response?.success) {
        showNotification('‚úÖ ÿ™ŸÖ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠', 'success');
        loadAllData();
      } else {
        throw new Error(response?.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
      }
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', error);
      showNotification(error.message, 'error');
    }
  };

  // ==================== UTILITIES ====================
  const formatCurrency = (amount) => {
    const num = parseFloat(amount) || 0;
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0,
      maximumFractionDigits: 2
    }).format(num);
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return '-';
    try {
      const date = new Date(dateString);
      return date.toLocaleString('ar-EG', {
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '-';
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('ar-EG', {
        year: 'numeric',
        month: 'short',
        day: 'numeric'
      });
    } catch {
      return '-';
    }
  };

  const formatTime = (dateString) => {
    if (!dateString) return '-';
    try {
      const date = new Date(dateString);
      return date.toLocaleTimeString('ar-EG', {
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '-';
    }
  };

  const getStatusBadge = (status) => {
    const config = {
      'ŸÜÿ¥ÿ∑': { class: 'active', icon: 'üî•', label: 'ŸÜÿ¥ÿ∑' },
      'ŸÖŸÉÿ™ŸÖŸÑ': { class: 'completed', icon: '‚úÖ', label: 'ŸÖŸÉÿ™ŸÖŸÑ' },
      'ŸÖŸÑÿ∫Ÿä': { class: 'cancelled', icon: '‚ùå', label: 'ŸÖŸÑÿ∫Ÿä' },
    }[status] || { class: 'default', icon: '‚ùì', label: status };
    
    return (
      <span className={`status-badge ${config.class}`}>
        <span className="badge-icon">{config.icon}</span>
        {config.label}
      </span>
    );
  };

  const getPaymentStatusBadge = (status) => {
    const config = {
      'ŸÖÿØŸÅŸàÿπ': { class: 'paid', icon: 'üí∞', label: 'ŸÖÿØŸÅŸàÿπ' },
      'ÿ∫Ÿäÿ± ŸÖÿØŸÅŸàÿπ': { class: 'unpaid', icon: '‚ö†Ô∏è', label: 'ÿ∫Ÿäÿ± ŸÖÿØŸÅŸàÿπ' },
      'ÿ¨ÿ≤ÿ¶Ÿä': { class: 'partial', icon: 'üìä', label: 'ÿ¨ÿ≤ÿ¶Ÿä' },
    }[status] || { class: 'default', icon: '‚ùì', label: status };
    
    return (
      <span className={`payment-badge ${config.class}`}>
        <span className="badge-icon">{config.icon}</span>
        {config.label}
      </span>
    );
  };

  const getRentalTypeBadge = (rental) => {
    const isOpenTime = rental.is_open_time === 1 || rental.rental_type === 'open';
    
    return (
      <span className={`type-badge ${isOpenTime ? 'open' : 'fixed'}`}>
        {isOpenTime ? 'üîì ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠' : '‚è∞ ŸàŸÇÿ™ ÿ´ÿßÿ®ÿ™'}
      </span>
    );
  };

  const showNotification = (message, type = 'info') => {
    const notification = document.createElement('div');
    notification.className = `notification ${type}`;
    notification.innerHTML = `
      <div class="notification-content">
        <span>${message}</span>
        <button class="notification-close">‚úï</button>
      </div>
    `;
    
    document.body.appendChild(notification);
    
    notification.querySelector('.notification-close').onclick = () => {
      notification.remove();
    };
    
    setTimeout(() => {
      if (notification.parentNode) {
        notification.remove();
      }
    }, 5000);
  };

  const calculateTimeElapsed = (startTime) => {
    if (!startTime) return 'ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ';
    
    try {
      const start = new Date(startTime);
      const now = new Date();
      const diffMs = now - start;
      const diffMins = Math.floor(diffMs / 60000);
      
      if (diffMins < 60) {
        return `${diffMins} ÿØŸÇŸäŸÇÿ©`;
      } else {
        const hours = Math.floor(diffMins / 60);
        const mins = diffMins % 60;
        return `${hours}:${mins.toString().padStart(2, '0')}`;
      }
    } catch {
      return 'ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ';
    }
  };

  const calculateRemainingTime = (rental) => {
    if (!rental.start_time || rental.is_open_time === 1) return 'ŸÖŸÅÿ™Ÿàÿ≠';
    
    try {
      const start = new Date(rental.start_time);
      const duration = rental.duration_minutes || 15;
      const end = new Date(start.getTime() + duration * 60000);
      const now = new Date();
      const remainingMs = end - now;
      
      if (remainingMs <= 0) return '00:00';
      
      const remainingMins = Math.floor(remainingMs / 60000);
      const remainingSecs = Math.floor((remainingMs % 60000) / 1000);
      
      return `${remainingMins}:${remainingSecs.toString().padStart(2, '0')}`;
    } catch {
      return '--:--';
    }
  };

  // ==================== PAGINATION ====================
  const totalPages = Math.ceil(filteredRentals.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const paginatedRentals = filteredRentals.slice(startIndex, endIndex);

  // ==================== EXPORT FUNCTION ====================
  const exportToExcel = () => {
    const dataToExport = filteredRentals.map(rental => ({
      'ÿ±ŸÇŸÖ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±': rental.rental_number,
      'ÿßŸÑÿπŸÖŸäŸÑ': rental.customer_name,
      'ÿßŸÑŸáÿßÿ™ŸÅ': rental.customer_phone,
      'ÿßŸÑŸÑÿπÿ®ÿ©': rental.game_name,
      'ÿßŸÑŸÖÿ®ŸÑÿ∫': rental.total_amount,
      'ÿßŸÑŸÖÿØŸÅŸàÿπ': rental.paid_amount,
      'ÿ≠ÿßŸÑÿ© ÿßŸÑÿØŸÅÿπ': rental.payment_status,
      'ÿßŸÑÿ≠ÿßŸÑÿ©': rental.status,
      'ŸÜŸàÿπ ÿßŸÑŸàŸÇÿ™': rental.is_open_time ? 'ŸÖŸÅÿ™Ÿàÿ≠' : 'ÿ´ÿßÿ®ÿ™',
      'ÿßŸÑŸÖÿØÿ© (ÿØŸÇŸäŸÇÿ©)': rental.duration_minutes || 0,
      'ŸàŸÇÿ™ ÿßŸÑÿ®ÿØÿ°': formatDateTime(rental.start_time),
      'ŸàŸÇÿ™ ÿßŸÑÿßŸÜÿ™Ÿáÿßÿ°': formatDateTime(rental.end_time),
      'ÿßŸÑŸÅÿ±ÿπ': rental.branch_name,
      'ÿßŸÑŸÖŸàÿ∏ŸÅ': rental.employee_name,
      'ŸÖŸÑÿßÿ≠ÿ∏ÿßÿ™': rental.notes
    }));
    
    // ÿ•ŸÜÿ¥ÿßÿ° CSV
    const headers = Object.keys(dataToExport[0] || {}).join(',');
    const rows = dataToExport.map(row => 
      Object.values(row).map(value => `"${value}"`).join(',')
    ).join('\n');
    
    const csvContent = `data:text/csv;charset=utf-8,\uFEFF${headers}\n${rows}`;
    
    const link = document.createElement('a');
    link.href = encodeURI(csvContent);
    link.download = `ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™_${new Date().toISOString().split('T')[0]}.csv`;
    link.click();
    
    showNotification('ÿ™ŸÖ ÿ™ÿµÿØŸäÿ± ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ®ŸÜÿ¨ÿßÿ≠', 'success');
  };

  // ==================== RENDER ====================
  if (loading && rentals.length === 0) {
    return (
      <div className="loading-screen">
        <div className="spinner"></div>
        <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™...</p>
        {currentShift && <p className="loading-sub">ÿßŸÑÿ¥ŸäŸÅÿ™ #{currentShift.id}</p>}
      </div>
    );
  }

  return (
    <div className="rentals-management">
      {/* ========== SHIFT INFO BANNER ========== */}
      {currentShift && (
        <div className="shift-banner">
          <div className="shift-info">
            <div className="shift-icon">
