  formatCurrency: (amount) => {
    const num = parseFloat(amount) || 0;
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0
    }).format(num);
  },

  formatDuration: (minutes) => {
    if (!minutes || minutes === 0) return 'ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠';
    if (minutes < 60) return `${minutes} ÿØŸÇŸäŸÇÿ©`;
    
    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;
    return `${hours} ÿ≥ÿßÿπÿ©${remainingMinutes > 0 ? ` ${remainingMinutes} ÿØŸÇŸäŸÇÿ©` : ''}`;
  },

  formatTime: (dateString) => {
    try {
      return new Date(dateString).toLocaleTimeString('ar-EG', {
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '--:--';
    }
  },

  formatDate: (dateString) => {
    try {
      return new Date(dateString).toLocaleDateString('ar-EG', {
        weekday: 'short',
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
    } catch {
      return '--/--/----';
    }
  },

  validateCustomerPhone: (phone) => {
    const phoneRegex = /^01[0-9]{9}$/;
    return phoneRegex.test(phone);
  },

  calculateTimeRemaining: (endTime) => {
    if (!endTime) return null;
    
    try {
      const end = new Date(endTime);
      const now = new Date();
      const timeLeftMs = end - now;
      
      if (timeLeftMs <= 0) return { expired: true, minutes: 0, formatted: 'ÿßŸÜÿ™ŸáŸâ' };
      
      const minutesLeft = Math.floor(timeLeftMs / 60000);
      const hours = Math.floor(minutesLeft / 60);
      const minutes = minutesLeft % 60;
      
      return {
        expired: false,
        minutes: minutesLeft,
        formatted: hours > 0 ? `${hours} ÿ≥ÿßÿπÿ© ${minutes} ÿØŸÇŸäŸÇÿ©` : `${minutes} ÿØŸÇŸäŸÇÿ©`
      };
    } catch (error) {
      return null;
    }
  },

  calculateRentalPrice: (game, durationMinutes, quantity = 1) => {
    if (!game) return 0;
    
    let price = 0;
    
    if (durationMinutes >= 60) {
      price = (game.price_per_hour || 0) * (durationMinutes / 60);
    } else {
      const quarters = Math.ceil(durationMinutes / 15);
      price = (game.price_per_15min || 0) * quarters;
    }
    
    return price * quantity;
  },

