// frontend/src/pages/Login.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [backendStatus, setBackendStatus] = useState('ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÇŸÇ...');
  const navigate = useNavigate();

  // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ≠ÿßŸÑÿ© Backend ÿπŸÜÿØ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿµŸÅÿ≠ÿ©
  useEffect(() => {
    checkBackendStatus();
  }, []);

  const checkBackendStatus = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/health');
      const data = await response.json();
      
      if (data.success && data.database === 'connected') {
        setBackendStatus('‚úÖ Backend ŸäÿπŸÖŸÑ ŸàŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÖÿ™ÿµŸÑÿ©');
      } else {
        setBackendStatus('‚ùå Backend ŸäÿπŸÖŸÑ ŸàŸÑŸÉŸÜ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ∫Ÿäÿ± ŸÖÿ™ÿµŸÑÿ©');
      }
    } catch (error) {
      setBackendStatus('‚ùå Backend ÿ∫Ÿäÿ± ŸÖÿ™ÿßÿ≠. ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ¥ÿ∫ŸäŸÑŸá ÿπŸÑŸâ http://localhost:5000');
    }
  };

// ŸÅŸä ÿØÿßŸÑÿ© handleLogin
const handleLogin = async (e) => {
  e.preventDefault();
  setLoading(true);
  setError('');

  try {
    console.log('üì§ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ∑ŸÑÿ® ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ...');
    
    const result = await authService.login(email, password);
    
    if (result.success) {
      console.log('‚úÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ ŸÜÿßÿ¨ÿ≠!');
      
      // ÿßŸÜÿ™ÿ∏ÿ± ŸÇŸÑŸäŸÑÿßŸã ÿ´ŸÖ ÿ™Ÿàÿ¨ŸäŸá
      setTimeout(() => {
        const user = authService.getCurrentUser();
        if (user?.role === 'admin') {
          window.location.href = '/admin/dashboard';
        } else {
          window.location.href = '/employee/dashboard';
        }
      }, 500);
      
    } else {
      setError(result.message || 'ŸÅÿ¥ŸÑ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ');
    }
    
  } catch (err) {
    console.error('üî• ÿÆÿ∑ÿ£ ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÇÿπ:', err);
    setError('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÇÿπ. ÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ Console ŸÑŸÑŸÖÿ≤ŸäÿØ ŸÖŸÜ ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ.');
  } finally {
    setLoading(false);
  }
};

  // ŸÅÿ™ÿ≠ Backend ŸÅŸä ÿ≠ÿßŸÑÿ© ÿπÿØŸÖ ÿ™ÿ¥ÿ∫ŸäŸÑŸá
  const openBackendInstructions = () => {
    alert(`
üöÄ ÿÆÿ∑Ÿàÿßÿ™ ÿ™ÿ¥ÿ∫ŸäŸÑ Backend:
1. ÿßŸÅÿ™ÿ≠ ŸÜÿßŸÅÿ∞ÿ© Terminal ÿ¨ÿØŸäÿØÿ©
2. ÿßŸÉÿ™ÿ®: cd backend
3. ÿßŸÉÿ™ÿ®: npm run dev
4. ÿßŸÜÿ™ÿ∏ÿ± ÿ≠ÿ™Ÿâ ÿ™ÿ±Ÿâ: "üöÄ Server running on port 5000"
5. ÿ´ŸÖ ÿ£ÿπÿØ ÿ™ÿ≠ŸÖŸäŸÑ Ÿáÿ∞Ÿá ÿßŸÑÿµŸÅÿ≠ÿ©
    `);
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <img 
            src="/images/logol3bty.jpg" 
            alt="L3BTY Logo" 
            className="login-logo"
            onError={(e) => {
              e.target.onerror = null;
              e.target.parentElement.style.display = 'none';
            }}
          />
          <h1>ŸÜÿ∏ÿßŸÖ ÿ™ÿ£ÿ¨Ÿäÿ± L3BTY</h1>
          <p className="login-subtitle">ÿ≥ÿ¨ŸÑ ÿØÿÆŸàŸÑŸÉ ŸÑŸÑŸàÿµŸàŸÑ ŸÑŸÑŸÜÿ∏ÿßŸÖ</p>
          
          {/* ÿ≠ÿßŸÑÿ© Backend */}
          <div className="backend-status">
            <p>{backendStatus}</p>
            {backendStatus.includes('‚ùå') && (
              <button 
                className="btn-help"
                onClick={openBackendInstructions}
              >
                ŸÉŸäŸÅ ÿ£ÿ¥ÿ∫ŸÑ Backendÿü
              </button>
            )}
          </div>
        </div>

        {error && (
          <div className="alert alert-danger">
            <strong>ÿÆÿ∑ÿ£:</strong> {error}
          </div>
        )}

        <form onSubmit={handleLogin} className="login-form">
          <div className="form-group">
            <label htmlFor="email">ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="ÿßÿØÿÆŸÑ ÿ®ÿ±ŸäÿØŸÉ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä"
              required
              className="form-control"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="ÿßÿØÿÆŸÑ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±"
              required
              className="form-control"
            />
          </div>

          <button 
            type="submit" 
            className="btn-login"
            disabled={loading}
          >
            {loading ? (
