// frontend/src/services/service.js
import api from './api-new';
import authService from './authService';

const service = {
  // üîê Authentication
  auth: authService,

  // üë• Users
  async getUsers() {
    try {
      const response = await api.get('/users');
      return response.data;
    } catch (error) {
      console.error('Get users error:', error);
      throw error;
    }
  },

  async createUser(userData) {
    try {
      const response = await api.post('/users', userData);
      return response.data;
    } catch (error) {
      console.error('Create user error:', error);
      throw error;
    }
  },

  // üéÆ Games
  async getGames() {
    try {
      const response = await api.get('/games');
      return response.data;
    } catch (error) {
      console.error('Get games error:', error);
      throw error;
    }
  },

  async createGame(gameData) {
    try {
      const response = await api.post('/games', gameData);
      return response.data;
    } catch (error) {
      console.error('Create game error:', error);
      throw error;
    }
  },

  // üè¨ Branches
  async getBranches() {
    try {
      const response = await api.get('/branches');
      return response.data;
    } catch (error) {
      console.error('Get branches error:', error);
      throw error;
    }
  },

  async createBranch(branchData) {
    try {
      const response = await api.post('/branches', branchData);
      return response.data;
    } catch (error) {
      console.error('Create branch error:', error);
      throw error;
    }
  },

  // ‚è±Ô∏è Rentals
  async getRentals(filters = {}) {
    try {
      const queryParams = new URLSearchParams(filters).toString();
      const url = `/rentals${queryParams ? `?${queryParams}` : ''}`;
      const response = await api.get(url);
      return response.data;
    } catch (error) {
      console.error('Get rentals error:', error);
      throw error;
    }
  },

  async createRental(rentalData) {
    try {
      const response = await api.post('/rentals', rentalData);
      return response.data;
    } catch (error) {
      console.error('Create rental error:', error);
      throw error;
    }
  },

  // üìä Dashboard
  async getDashboardStats() {
    try {
      const response = await api.get('/dashboard/stats');
      return response.data;
    } catch (error) {
      console.error('Get dashboard stats error:', error);
      throw error;
    }
  },

  async getRecentRentals() {
    try {
      const response = await api.get('/rentals?limit=5');
      return response.data;
    } catch (error) {
      console.error('Get recent rentals error:', error);
      throw error;
    }
  }
};

export default service;
