      const data = {
        shiftId,
        rentals,
        timestamp: Date.now(),
        expiry: Date.now() + 7 * 24 * 60 * 60 * 1000
      };
      localStorage.setItem(key, JSON.stringify(data));
    } catch (error) {
      console.error('❌ خطأ في حفظ التأجيرات المكتملة:', error);
    }
  },
  getCompletedRentals: shiftId => {
    if (!shiftId) return [];
    try {
      const key = `completed_rentals_shift_${shiftId}`;
      const data = localStorage.getItem(key);
      if (data) {
        const parsed = JSON.parse(data);
        if (parsed.expiry > Date.now()) {
          return parsed.rentals;
        } else {
          localStorage.removeItem(key);
        }
      }
    } catch (error) {
      console.error('❌ خطأ في جلب التأجيرات المكتملة:', error);
    }
    return [];
  },
  clearCompletedRentals: shiftId => {
    if (!shiftId) return;
    try {
      const key = `completed_rentals_shift_${shiftId}`;
      localStorage.removeItem(key);
    } catch (error) {
      console.error('❌ خطأ في حذف التأجيرات المكتملة:', error);
    }
  },
  cleanupOldData: () => {
    try {
      const now = Date.now();
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
