        todayRentals: prev.todayRentals + 1,
        todayRevenue: prev.todayRevenue + (Number(rental.total_amount) || 0),
        shiftRevenue: prev.shiftRevenue + (Number(rental.total_amount) || 0)
      }));
      
      console.log(`✅ تم إكمال التأجير ${rental.rental_number} محلياً`);
      
    } catch (error) {
      console.error(`🔥 خطأ في إكمال التأجير:`, error);
    }
  };
  
  const filteredGames = useMemo(() => {
    if (!games || !Array.isArray(games)) return [];
    
    const gamesWithAvailability = games.map(game => {
      return {
        ...game,
        rented_count: 0,
        available_count: 999,
        status: 'متاح'
      };
    });
    
    let filtered = gamesWithAvailability.filter(game => {
      if (!game) return false;
      
      const isActive = game.is_active === 1 || game.is_active === true;
      if (!isActive) return false;
      
      return true;
    });
    
    return filtered;
  }, [games]);
  
  const removeGame = (gameId) => {
    setSelectedGames(prev => prev.filter(game => game.id !== gameId));
    setSelectedGameQuantities(prev => {
      const newQuantities = { ...prev };
      delete newQuantities[gameId];
      return newQuantities;
    });
  };
  
  const updateGameQuantity = (gameId, newQuantity) => {
    if (newQuantity < 1) {
      removeGame(gameId);
      return;
    }
    
    setSelectedGameQuantities(prev => ({
      ...prev,
      [gameId]: newQuantity
    }));
  };
  
  const resetForm = () => {
    setCustomerName('');
    setCustomerPhone('');
    setCustomerNotes('');
    setSelectedGames([]);
    setSelectedGameQuantities({});
  };
  
  const handleQuickStartRental = () => {
    if (!currentShift) {
      showNotification('error', 'يجب بدء شيفت أولاً');
      return;
    }
    
    setShowEnhancedRentalModal(true);
    showNotification('info', 'افتحت نافذة التأجير المتعدد. يمكنك إدارة كل لعبة بشكل منفصل.');
  };
  
  const handleQuickAction = (action) => {
    switch(action) {
      case 'startShift':
        startShift();
        break;
      case 'quickRental':
        handleQuickStartRental();
        break;
      case 'refreshData':
        triggerRefresh();
        showNotification('info', 'جاري تحديث البيانات...');
        break;
      case 'refreshCompleted':
        loadCompletedRentals(true);
        showNotification('info', 'جاري تحديث التأجيرات المكتملة...');
        break;
      case 'viewStats':
        showNotification('info', `
📊 الإحصائيات الحالية:
• الألعاب المتاحة: ${statistics.availableGames}
• التأجيرات النشطة: ${statistics.activeRentals}
• التأجيرات المكتملة: ${statistics.completedCount || completedRentals.length}
• إيرادات اليوم: ${formatCurrency(statistics.todayRevenue)}
• تأجيرات اليوم: ${statistics.todayRentals}
${isManager ? `• إيرادات الشيفت: ${formatCurrency(statistics.shiftRevenue)}` : ''}
        `);
        break;
      case 'endShift':
        handleEndCurrentShift();
        break;
      default:
        console.log('إجراء غير معروف:', action);
    }
  };
  
  const getDisplayImage = (item) => {
    return item.enhanced_image_url || getGameImageUrl(item);
  };
  
  const NoShiftScreen = () => {
    return (
      <div className="no-shift-screen">
        <div className="no-shift-container">
          <div className="no-shift-icon">🎮</div>
          <h1 className="no-shift-title">لا يوجد شيفت نشط</h1>
          <p className="no-shift-description">
            يجب بدء شيفت جديد للتمكن من إدارة التأجيرات
          </p>
          
          <div className="no-shift-actions">
            <button 
              className="start-shift-btn-primary"
              onClick={startShift}
            >
              🚀 بدء شيفت جديد
            </button>
            
            <button 
              className="refresh-system-btn"
              onClick={() => window.location.reload()}
            >
              🔄 تحديث النظام
            </button>
          </div>
          
          <div className="no-shift-info">
            <p>👤 المستخدم: <strong>{user?.name}</strong></p>
            <p>🏪 الفرع: <strong>{user?.branch_name}</strong></p>
          </div>
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="rentals-page-loading-container">
        <div className="rentals-page-spinner"></div>
        <p>جاري تحميل البيانات...</p>
      </div>
    );
  }

  if (!currentShift) {
    return <NoShiftScreen />;
  }

  return (
    <div className="rentals-page-main">
      <div className="rentals-page-top-controls">
        <div className="rentals-page-header-section">
          <h1 className="rentals-page-title">
            <span className="rentals-page-title-icon">🎮</span>
            نظام إدارة التأجيرات
          </h1>
          <div className="rentals-page-user-info">
            <span className="rentals-page-user-name">{user?.name || 'مستخدم'}</span>
            <span className="rentals-page-user-role">
              {user?.role === 'employee' ? '👷 موظف' : '👑 مدير'}
            </span>
          </div>
        </div>
        
        <div className="rentals-page-controls-row">
          <QuickActionsMenu onActionClick={handleQuickAction} />
          
          <div className="rentals-page-shift-info-display">
            <div className="rentals-page-shift-timer">
              <span className="rentals-page-timer-icon">⏱️</span>
              <span className="rentals-page-timer-text">
                الشيفت #{currentShift.id} - بدأ: {formatEgyptDateTime(currentShift.start_time)}
              </span>
              <span className="rentals-page-current-time">
                الوقت الحالي: {currentEgyptTime}
              </span>
            </div>
            <div className="rentals-page-shift-actions">
              <button 
                className="rentals-page-btn-quick-rental"
                onClick={handleQuickStartRental}
                title="بدء تأجير جديد"
              >
                <span className="rentals-page-btn-icon">🚀</span>
                <span className="rentals-page-btn-text">بدء تأجير</span>
              </button>
              {isManager && (
                <button 
                  className="rentals-page-btn-end-shift"
                  onClick={handleEndCurrentShift}
                  title="إنهاء الشيفت الحالي"
                >
                  <span className="rentals-page-btn-icon">🏁</span>
                  <span className="rentals-page-btn-text">إنهاء الشيفت</span>
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
      
      {isManager && todaysShifts.length > 0 && (
        <div className="manager-todays-shifts">
          <div className="section-header">
            <h3>
              <span className="section-icon">📅</span>
              شيفتات اليوم
              <span className="badge-count">{todaysShifts.length}</span>
            </h3>
            <button 
              className="refresh-btn"
              onClick={loadTodaysShifts}
              title="تحديث شيفتات اليوم"
            >
              🔄
            </button>
          </div>
          
          <div className="shifts-scroll-container">
            {todaysShifts.map(shift => (
              <div key={shift.id} className="todays-shift-mini-card">
                <div className="shift-mini-header">
                  <span className="shift-id">#{shift.id}</span>
                  <span className={`shift-mini-status ${shift.status === 'منتهي' ? 'ended' : 'active'}`}>
                    {shift.status === 'منتهي' ? '✅' : '🔥'}
                  </span>
                </div>
                <div className="shift-mini-body">
                  <div className="shift-mini-info">
                    <span className="employee">{shift.employee_name}</span>
                    <span className="time">{formatEgyptDateTime(shift.start_time).split(' ')[1]}</span>
                  </div>
                  <div className="shift-mini-stats">
                    <span className="revenue">{formatCurrency(shift.total_revenue || 0)}</span>
                    <span className="rentals">{shift.total_rentals || 0} تأجير</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
      
      <div className="rentals-page-stats-container">
        <div className="rentals-page-stats-header">
          <div className="stats-title-section">
            <h3>
              <span className="stats-title-icon">📊</span>
              لوحة التحكم الحية
            </h3>
          </div>
          
          <div className="stats-controls">
            <button 
              className="stats-refresh-btn"
              onClick={() => {
                triggerRefresh();
                showNotification('info', 'جاري تحديث البيانات...');
              }}
            >
              🔄 تحديث البيانات
            </button>
          </div>
        </div>
        
        <div className="rentals-page-stats-grid">
          <div className="rentals-page-stat-card">
            <div className="stat-card-header">
              <div className="stat-icon">🔥</div>
            </div>
            <div className="stat-card-body">
              <div className="stat-main-value">{statistics.activeRentals}</div>
              <div className="stat-label">تأجيرات نشطة</div>
            </div>
          </div>
          
          <div className="rentals-page-stat-card">
            <div className="stat-card-header">
              <div className="stat-icon">🎮</div>
            </div>
            <div className="stat-card-body">
              <div className="stat-main-value">{statistics.availableGames}</div>
              <div className="stat-label">ألعاب متاحة</div>
              <div className="stat-sub-info">
                جميع الألعاب متاحة دائماً
              </div>
            </div>
          </div>
          
          <div className="rentals-page-stat-card revenue-highlight">
            <div className="stat-card-header">
              <div className="stat-icon">💰</div>
              <button 
                className="refresh-revenue-btn"
                onClick={checkShiftRevenue}
                title="تحديث إيراد الشيفت"
              >
                🔄
              </button>
            </div>
            <div className="stat-card-body">
              <div className="stat-main-value">{formatCurrency(statistics.shiftRevenue)}</div>
              <div className="stat-label">إيراد الشيفت الحالي</div>
              <div className="stat-sub-info">
                {currentShift ? `الشيفت #${currentShift.id}` : 'لا يوجد شيفت'}
              </div>
            </div>
          </div>
          
          <div className="rentals-page-stat-card system-card">
            <div className="stat-card-header">
              <div className="stat-icon">⚙️</div>
            </div>
            <div className="stat-card-body">
              <div className="stat-main-value">
                #{currentShift.id}
              </div>
              <div className="stat-label">الشيفت الحالي</div>
              <div className="stat-sub-info">
                {formatEgyptDateTime(currentShift.start_time)}
              </div>
            </div>
          </div>
        </div>
      </div>
      
      <div className="rentals-page-games-section">
        <div className="rentals-page-section-header">
          <h2>
            <span className="rentals-page-header-icon">🎮</span>
            الألعاب المتاحة
            <span className="rentals-page-games-count">
              ({filteredGames.length} لعبة)
            </span>
          </h2>
          <div className="rentals-page-section-controls">
            <button 
              className="rentals-page-refresh-btn"
              onClick={loadGames}
              title="تحديث قائمة الألعاب"
            >
              🔄 تحديث الألعاب
            </button>
          </div>
        </div>
        
        <div className="rentals-page-games-grid">
          {filteredGames.length > 0 ? (
            filteredGames.map(game => (
              <div 
                key={game.id}
                className="rentals-page-game-card available"
                onClick={() => handleGameClick(game)}
                title={`إضافة ${game.name} للتأجير`}
              >
                <div className="rentals-page-game-image-container">
                  <img 
                    src={getDisplayImage(game)}
                    alt={game.name}
                    className="rentals-page-game-image clear-image"
                    style={{ 
                      objectFit: 'cover',
                      objectPosition: 'center'
                    }}
                    onError={handleImageError}
                  />
                </div>
                
                <div className="rentals-page-game-info">
                  <div className="rentals-page-game-price-badge">
                    {formatCurrency(game.price_per_15min)}
                  </div>
                  
                  <h3 className="rentals-page-game-title">
                    <span>{game.name}</span>
                    {selectedGames.some(g => g.id === game.id) && (
                      <span className="rentals-page-selected-indicator">
                        ✨ {selectedGameQuantities[game.id] || 1}x
                      </span>
                    )}
                  </h3>
                  
                  <div className="rentals-page-game-actions">
                    <button 
                      className={`rentals-page-select-game-btn ${
                        selectedGames.some(g => g.id === game.id) ? 'selected-btn' : 'available-btn'
                      }`}
                      onClick={(e) => {
                        e.stopPropagation();
                        handleGameClick(game);
                      }}
                    >
                      {selectedGames.some(g => g.id === game.id) 
                        ? `✅ مختارة (${selectedGameQuantities[game.id] || 1})` 
                        : '➕ إضافة للتأجير'}
                    </button>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="rentals-page-no-games">
              <div className="rentals-page-empty-icon">🎮</div>
              <h3>لا توجد ألعاب متاحة حالياً</h3>
              <button 
                className="rentals-page-refresh-games-btn"
                onClick={loadGames}
              >
                🔄 تحديث الألعاب
              </button>
            </div>
          )}
        </div>
      </div>
      
      <div className="rentals-page-active-rentals-section">
        <div className="rentals-page-section-header">
          <h2>
            <span className="rentals-page-header-icon">🔥</span>
            التأجيرات النشطة
            <span className="rentals-page-active-count">
              ({activeRentals.length} تأجير)
            </span>
          </h2>
          
          <div className="rentals-page-section-controls">
            <button 
              className="rentals-page-refresh-btn"
              onClick={loadActiveRentals}
              title="تحديد يدوي"
            >
              🔄 تحديث التأجيرات
            </button>
          </div>
        </div>
        
        {activeRentals.length === 0 ? (
          <div className="rentals-page-empty-state">
            <div className="rentals-page-empty-icon">📭</div>
            <h3>لا توجد تأجيرات نشطة حالياً</h3>
            <div className="rentals-page-empty-actions">
              <button onClick={handleQuickStartRental} className="rentals-page-btn-quick-rental">
                🚀 بدء تأجير جديد
              </button>
            </div>
          </div>
        ) : (
          <div className="rentals-page-active-rentals-table-container">
            <table className="rentals-page-active-rentals-table">
              <thead>
                <tr>
                  <th>رقم التأجير</th>
                  <th>العميل</th>
                  <th>اللعبة</th>
                  <th>الكمية</th>
                  <th>وقت البدء</th>
                  <th>المدة</th>
                  <th>الوقت المتبقي</th>
                  <th>حالة الدفع</th>
                  <th>الإجراءات</th>
                </tr>
              </thead>
              <tbody>
                {activeRentals.map(rental => (
                  <tr key={rental.id} className={rental.is_expired ? 'expired' : ''}>
                    <td>
                      <span className="rentals-page-rental-number">
                        #{rental.rental_number}
                      </span>
                    </td>
                    <td>
                      <div className="rentals-page-customer-info">
                        <span className="rentals-page-customer-name">{rental.customer_name}</span>
                        <span className="rentals-page-customer-phone">{rental.customer_phone}</span>
                      </div>
                    </td>
                    <td>
                      <div className="rentals-page-rental-game-info">
                        <img 
                          src={getDisplayImage(rental)}
                          alt={rental.game_name}
                          className="rentals-page-rental-game-image"
                          onError={handleImageError}
                        />
                        <span className="rentals-page-game-name-only">{rental.game_name}</span>
                      </div>
                    </td>
                    <td>
                      <span className="rentals-page-quantity">
                        {rental.quantity || 1} قطعة
                      </span>
                    </td>
                    <td>
                      <span className="rentals-page-start-time">
                        {formatEgyptDateTime(rental.start_time)}
                      </span>
                    </td>
                    <td>
                      <span className="rentals-page-duration">
                        {formatDuration(rental.duration_minutes)}
                      </span>
                    </td>
                    <td>
                      <div className="rentals-page-time-left-container">
                        {rental.is_open_time ? (
                          <span className="rentals-page-open-time-badge">⏳ وقت مفتوح</span>
                        ) : rental.duration_minutes > 0 ? (
                          <>
                            <span className={`rentals-page-time-left ${rental.is_expired ? 'expired' : ''}`}>
                              {rental.time_left || 'جاري الحساب...'}
                              {rental.time_left === 'انتهى' && (
                                <span className="rentals-page-expired-indicator">⏰</span>
                              )}
                            </span>
                            {rental.is_expired && (
                              <button 
                                className="rentals-page-auto-complete-btn"
                                onClick={() => handleAutoCompleteRental(rental)}
                                title="إكمال التأجير تلقائياً"
                              >
                                ✅ إكمال
                              </button>
                            )}
                          </>
                        ) : (
                          <span className="rentals-page-open-time-badge">⏳ وقت مفتوح</span>
                        )}
                      </div>
                    </td>
                    <td>
                      <div className="rentals-page-payment-status">
                        {rental.payment_status === 'مدفوع' ? (
                          <span className="rentals-page-paid-badge">
                            ✅ مدفوع
                            {rental.duration_minutes > 0 && (
                              <span className="rentals-page-fixed-badge">⏰ ثابت</span>
                            )}
                          </span>
                        ) : rental.payment_status === 'عند الإنهاء' ? (
                          <span className="rentals-page-pending-badge">
                            ⏳ عند الإنهاء
                            {rental.duration_minutes === 0 && (
                              <span className="rentals-page-open-badge">∞ مفتوح</span>
                            )}
                          </span>
                        ) : (
                          <span className="rentals-page-unpaid-badge">❌ غير مدفوع</span>
                        )}
                      </div>
                    </td>
                    <td>
                      <div className="rentals-page-actions-container">
                        <button 
                          className="rentals-page-details-btn"
                          onClick={() => setSelectedRentalDetails(rental)}
                          title="عرض التفاصيل"
                        >
                          <span className="rentals-page-btn-icon">👁️</span>
                          <span className="rentals-page-btn-text">عرض</span>
                        </button>
                        
                        <RentalActionsDropdown
                          rental={rental}
                          onActionClick={(action, rentalData) => {
                            switch(action) {
                              case 'changeGame':
                                handleChangeGameEnhanced(rentalData);
                                break;
                              case 'cancel':
                                handleCancelRentalEnhanced(rentalData);
                                break;
                              case 'endTime':
                                handleEndOpenTimeEnhanced(rentalData);
                                break;
                              default:
                                console.log('إجراء غير معروف:', action);
                            }
                          }}
                        />
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
      
      {!isEmployee && (
        <div className="rentals-page-completed-section">
          <div className="rentals-page-section-header">
            <h2>
              <span className="rentals-page-header-icon">✅</span>
              التأجيرات المكتملة
              <span className="rentals-page-completed-count">
                ({completedRentals.length} تأجير)
              </span>
            </h2>
            <div className="rentals-page-section-controls">
              <button 
                className="rentals-page-refresh-btn"
                onClick={() => loadCompletedRentals(true)}
                title="تحديث التأجيرات المكتملة"
              >
                🔄 تحديث المكتملة
              </button>
            </div>
          </div>
          
          {completedRentals.length === 0 ? (
            <div className="rentals-page-empty-state">
              <div className="rentals-page-empty-icon">📊</div>
              <h3>لا توجد تأجيرات مكتملة</h3>
            </div>
          ) : (
            <div className="rentals-page-completed-table-container">
              <table className="rentals-page-completed-rentals-table">
                <thead>
                  <tr>
                    <th>رقم التأجير</th>
                    <th>العميل</th>
                    <th>اللعبة</th>
                    <th>وقت البدء</th>
                    <th>وقت الانتهاء</th>
                    <th>المدة</th>
                    <th>المبلغ</th>
                    <th>طريقة الدفع</th>
                    <th>الحالة</th>
                  </tr>
                </thead>
                <tbody>
                  {completedRentals.map(rental => (
                    <tr key={rental.id} className={
                      rental.status === 'مسترد' ? 'refunded-row' : 
                      rental.status === 'ملغي' ? 'cancelled-row' : 
                      'completed-row'
                    }>
                      <td>#{rental.rental_number}</td>
                      <td>
                        <div className="rentals-page-customer-info">
                          <span className="rentals-page-customer-name">{rental.customer_name}</span>
                          <span className="rentals-page-customer-phone">{rental.customer_phone}</span>
                        </div>
                      </td>
                      <td>
                        <div className="rentals-page-completed-game-info">
                          <img 
                            src={getDisplayImage(rental)}
                            alt={rental.game_name}
                            className="rentals-page-completed-game-image"
                            onError={handleImageError}
                          />
                          {rental.game_name}
                        </div>
                      </td>
                      <td>{formatEgyptDateTime(rental.start_time)}</td>
                      <td>{formatEgyptDateTime(rental.actual_end_time || rental.end_time)}</td>
                      <td>{formatDuration(rental.actual_duration_minutes || rental.duration_minutes)}</td>
                      <td className="rentals-page-amount-cell">
                        {rental.status === 'مسترد' ? (
                          <>
                            <span className="rentals-page-amount-zero">0 ج.م</span>
                            <span className="rentals-page-refund-badge">
                              💸 مسترد: {formatCurrency(rental.refund_amount || rental.total_amount || 0)}
                            </span>
                          </>
                        ) : rental.status === 'ملغي' ? (
                          <span className="rentals-page-amount-cancelled">❌ ملغي</span>
                        ) : (
                          <span className="rentals-page-amount-completed">
                            {formatCurrency(rental.total_amount || 0)}
                          </span>
                        )}
                      </td>
                      <td>{rental.payment_method || 'كاش'}</td>
                      <td>
                        <span className={`rentals-page-status-badge ${
                          rental.status === 'مسترد' ? 'refunded' : 
                          rental.status === 'ملغي' ? 'cancelled' : 
                          'completed'
                        }`}>
                          {rental.status === 'مسترد' ? '💸 مسترد' : 
                           rental.status === 'ملغي' ? '❌ ملغي' : 
                           '✅ مكتمل'}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      )}
      
      {showEnhancedRentalModal && (
        <EnhancedRentalListModal
          show={showEnhancedRentalModal}
          onClose={() => setShowEnhancedRentalModal(false)}
          games={games}
          selectedGames={selectedGames}
          selectedGameQuantities={selectedGameQuantities}
          onStartRental={handleStartEnhancedRentalMain}
          currentShift={currentShift}
        />
      )}
      
      {selectedRentalDetails && (
        <RentalDetailsModal
          rental={selectedRentalDetails}
          onClose={() => setSelectedRentalDetails(null)}
        />
      )}
      
      {showChangeGameModal && selectedRentalForChange && (
        <ChangeGameModal
          rental={selectedRentalForChange}
          availableGames={availableGamesForChange}
          onClose={() => {
            setShowChangeGameModal(false);
            setSelectedRentalForChange(null);
          }}
          onGameSelected={handleChangeGameEnhanced}
        />
      )}
    </div>
  );
};

export default Rentals;
