import React, { useState, useEffect } from 'react';
import service from '../../services/service';
import './AdminPages.css';

const UsersManagement = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterRole, setFilterRole] = useState('all');
  
  const itemsPerPage = 10;
  
  const [newUser, setNewUser] = useState({
    username: '',
    email: '',
    password: '123456',
    name: '',
    role: 'employee',
    branch_id: 1,
    phone: '',
    is_active: true
  });

  const [branches, setBranches] = useState([]);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…ÙŠÙ†
      const usersResponse = await service.getUsers();
      if (usersResponse.success) {
        setUsers(usersResponse.data);
      }
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„ÙØ±ÙˆØ¹
      const branchesResponse = await service.getBranches();
      if (branchesResponse.success) {
        setBranches(branchesResponse.data);
      }
      
    } catch (error) {
      setError('ØªØ¹Ø°Ø± ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª');
      console.error('Load data error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddUser = async () => {
    if (!newUser.username || !newUser.email || !newUser.name) {
      alert('ÙŠØ±Ø¬Ù‰ Ù…Ù„Ø¡ Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø­Ù‚ÙˆÙ„ Ø§Ù„Ù…Ø·Ù„ÙˆØ¨Ø©');
      return;
    }

    try {
      const response = await service.createUser(newUser);
      
      if (response.success) {
        alert('âœ… ØªÙ… Ø¥Ù†Ø´Ø§Ø¡ Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù… Ø¨Ù†Ø¬Ø§Ø­');
        setShowAddModal(false);
        setNewUser({
          username: '',
          email: '',
          password: '123456',
          name: '',
          role: 'employee',
          branch_id: 1,
          phone: '',
          is_active: true
        });
        loadData();
      } else {
        alert(response.message || 'ÙØ´Ù„ ÙÙŠ Ø¥Ù†Ø´Ø§Ø¡ Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…');
