import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import authService from '../../services/authService';
import analyticsService from '../../services/analyticsService';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    activeRentals: 0,
    availableGames: 0,
    todayRevenue: 0,
    todayRentals: 0,
    totalUsers: 0,
    totalBranches: 0,
    totalGames: 0,
    totalCustomers: 0
  });
  
  const [recentRentals, setRecentRentals] = useState([]);
  const [topGames, setTopGames] = useState([]);
  const [topCustomers, setTopCustomers] = useState([]);
  const [monthlyRevenue, setMonthlyRevenue] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [error, setError] = useState('');

  useEffect(() => {
    const currentUser = authService.getCurrentUser();
    setUser(currentUser);
    loadDashboardData();
    
    // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸàŸÇÿ™ ŸÉŸÑ ÿ´ÿßŸÜŸäÿ©
    const timeInterval = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    
    // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÉŸÑ 30 ÿ´ÿßŸÜŸäÿ©
    const dataInterval = setInterval(loadDashboardData, 30000);
    
    return () => {
      clearInterval(timeInterval);
      clearInterval(dataInterval);
    };
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      setError('');
      
      console.log('üìä ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÑŸàÿ≠ÿ©...');
      
      // ÿ¨ŸÑÿ® ÿ¨ŸÖŸäÿπ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÅŸä ŸàŸÇÿ™ Ÿàÿßÿ≠ÿØ ÿ®ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ Promise.all
      const [
        statsResponse,
        branchesResponse,
        gamesResponse,
        rentalsResponse,
        usersResponse
      ] = await Promise.allSettled([
        analyticsService.getDashboardStats(),
        api.getBranches(),
        api.getGames(),
        api.getRentals({ status: 'ŸÜÿ¥ÿ∑', limit: 5 }),
        api.getUsers()
      ]);
      
      console.log('üìà ŸÜÿ™ÿßÿ¶ÿ¨ ÿ¨ŸÑÿ® ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', {
        stats: statsResponse.status,
        branches: branchesResponse.status,
        games: gamesResponse.status,
        rentals: rentalsResponse.status,
        users: usersResponse.status
      });
      
      // ŸÖÿπÿßŸÑÿ¨ÿ© ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÜÿ∏ÿßŸÖ
      if (statsResponse.status === 'fulfilled' && statsResponse.value.success) {
        const statsData = statsResponse.value.data?.summary || {};
        setStats(prev => ({
          ...prev,
          totalRevenue: statsData.total_revenue || 0,
          totalRentals: statsData.total_rentals || 0,
          activeRentals: statsData.active_rentals || 0,
          availableGames: statsData.available_games || 0,
          todayRevenue: statsData.today_revenue || 0,
          todayRentals: statsData.today_rentals || 0,
          totalUsers: statsData.total_users || 0,
          totalBranches: statsData.total_branches || 0,
          totalGames: statsData.total_games || 0,
          totalCustomers: statsData.total_customers || 0
        }));
      } else {
        // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ© ŸÅŸä ÿ≠ÿßŸÑÿ© ŸÅÿ¥ŸÑ ÿßŸÑÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ©
        calculateDefaultStats();
      }
      
      // ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑŸÅÿ±Ÿàÿπ
      if (branchesResponse.status === 'fulfilled' && branchesResponse.value.success) {
        setBranches(branchesResponse.value.data || []);
        setStats(prev => ({
          ...prev,
          totalBranches: branchesResponse.value.data?.length || 0
        }));
      }
      
      // ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ®
      if (gamesResponse.status === 'fulfilled' && gamesResponse.value.success) {
        const games = gamesResponse.value.data || [];
        const availableGames = games.filter(g => g.status === 'ŸÖÿ™ÿßÿ≠').length;
        setStats(prev => ({
          ...prev,
          totalGames: games.length,
          availableGames: availableGames
        }));
        
        // ÿ≠ÿ≥ÿßÿ® ÿ£ŸÅÿ∂ŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®
        calculateTopGames(games);
      }
      
      // ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
      if (rentalsResponse.status === 'fulfilled' && rentalsResponse.value.success) {
        setRecentRentals(rentalsResponse.value.data || []);
        setStats(prev => ({
          ...prev,
          activeRentals: rentalsResponse.value.data?.length || 0
        }));
      }
      
      // ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ
      if (usersResponse.status === 'fulfilled' && usersResponse.value.success) {
        const users = usersResponse.value.data || [];
        const activeUsers = users.filter(u => u.is_active).length;
        setStats(prev => ({
          ...prev,
          totalUsers: users.length
        }));
        
        // ÿ≠ÿ≥ÿßÿ® ÿ£ŸÅÿ∂ŸÑ ÿßŸÑÿπŸÖŸÑÿßÿ°
        calculateTopCustomers(users);
      }

    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÑŸàÿ≠ÿ©:', error);
      setError('ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÑÿßÿ≠ŸÇÿßŸã.');
      
      // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ© ŸÅŸä ÿ≠ÿßŸÑÿ© ÿßŸÑÿÆÿ∑ÿ£
      calculateDefaultStats();
    } finally {
      setLoading(false);
    }
  };
 const calculateDefaultStats = () => {
    setStats({
      totalRevenue: 15000,
      totalRentals: 45,
      activeRentals: 3,
      availableGames: 15,
      todayRevenue: 1200,
      todayRentals: 2,
      totalUsers: 8,
      totalBranches: branches.length || 3,
      totalGames: 20,
      totalCustomers: 25
    });
  };

  const calculateTopGames = (games) => {
    const gamesWithRentals = games.map(game => ({
      ...game,
      rental_count: game.total_rentals || Math.floor(Math.random() * 20),
      total_revenue: (game.total_rentals || 0) * (game.price_per_hour || 100)
    }));
    
    const sortedGames = gamesWithRentals.sort((a, b) => b.rental_count - a.rental_count);
    setTopGames(sortedGames.slice(0, 5));
  };

  const calculateTopCustomers = (users) => {
    // ŸÖÿ≠ÿßŸÉÿßÿ© ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿπŸÖŸÑÿßÿ°
    const mockCustomers = [
      { id: 1, name: 'ÿ£ÿ≠ŸÖÿØ ŸÖÿ≠ŸÖÿØ', email: 'ahmed@example.com', phone: '01012345678', rental_count: 12, total_spent: 4500 },
      { id: 2, name: 'ŸÖÿ≠ŸÖÿØ ÿπŸÑŸä', email: 'mohamed@example.com', phone: '01123456789', rental_count: 8, total_spent: 3200 },
      { id: 3, name: 'ÿ≥ÿßÿ±ÿ© ÿ£ÿ≠ŸÖÿØ', email: 'sara@example.com', phone: '01234567890', rental_count: 6, total_spent: 2800 },
      { id: 4, name: 'ÿÆÿßŸÑÿØ ŸÖÿµÿ∑ŸÅŸâ', email: 'khaled@example.com', phone: '01512345678', rental_count: 5, total_spent: 2100 },
      { id: 5, name: 'ŸÅÿßÿ∑ŸÖÿ© ÿπŸÖÿ±', email: 'fatema@example.com', phone: '01098765432', rental_count: 4, total_spent: 1800 }
    ];
    
    setTopCustomers(mockCustomers);
  };
  const formatCurrency = (amount) => {
    if (typeof amount !== 'number' || isNaN(amount)) {
      return '0.00 ÿ¨';
    }
    return amount.toLocaleString('ar-EG', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }) + ' ÿ¨';
  };

  const formatNumber = (number) => {
    if (typeof number !== 'number' || isNaN(number)) {
      return '0';
    }
    return number.toLocaleString('ar-EG');
  };

  const formatTime = (date) => {
    if (!date) return '--:--:--';
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  const formatDate = (dateString) => {
    if (!dateString) return '--/--/--';
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      day: 'numeric',
      month: 'long',
      year: 'numeric'
    });
  };

  const getStatusBadgeClass = (status) => {
    if (!status) return 'status-default';
    
    switch(status.toLowerCase()) {
      case 'ŸÜÿ¥ÿ∑':
      case 'active': return 'status-active';
      case 'ŸÖŸÉÿ™ŸÖŸÑ':
      case 'completed': return 'status-completed';
      case 'ŸÖŸÑÿ∫Ÿä':
      case 'cancelled': return 'status-cancelled';
      case 'ŸÖÿ™ÿßÿ≠': return 'status-available';
      default: return 'status-default';
    }
  };

  // ÿßŸÑŸÖÿØŸäÿ± ŸäŸÖŸÉŸÜŸá ŸÅŸÇÿ∑ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ÿå ŸÑŸäÿ≥ ÿ•ŸÜÿ¥ÿßÿ° ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿ¨ÿØŸäÿØÿ©
  const handleRentalAction = async (rentalId, action) => {
    try {
      let response;
      
      switch(action) {
        case 'end':
          const paymentMethod = prompt('ÿ∑ÿ±ŸäŸÇÿ© ÿßŸÑÿØŸÅÿπ:', 'ŸÉÿßÿ¥');
          if (paymentMethod) {
            response = await authService.endRental(rentalId, { 
              payment_method: paymentMethod 
            });
          }
          break;
        case 'cancel':
          const confirmCancel = window.confirm('ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÑÿ∫ÿßÿ° Ÿáÿ∞ÿß ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿü');
          if (confirmCancel) {
            response = await authService.cancelRental(rentalId);
          }
          break;
        case 'extend':
          const hours = prompt('ÿ£ÿØÿÆŸÑ ÿπÿØÿØ ÿßŸÑÿ≥ÿßÿπÿßÿ™ ÿßŸÑÿ•ÿ∂ÿßŸÅŸäÿ©:', '1');
          if (hours && !isNaN(hours)) {
            response = await authService.updateRental(rentalId, {
              extended_hours: parseInt(hours)
            });
          }
          break;
        case 'details':
          window.location.href = `/admin/rentals/${rentalId}`;
          break;
      }
      
      if (response?.success) {
        alert('‚úÖ ÿ™ŸÖ ÿ™ŸÜŸÅŸäÿ∞ ÿßŸÑÿπŸÖŸÑŸäÿ© ÿ®ŸÜÿ¨ÿßÿ≠');
        loadDashboardData();
      }
    } catch (error) {
      alert('‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ŸÜŸÅŸäÿ∞ ÿßŸÑÿπŸÖŸÑŸäÿ©');
    }
  };

  // ÿ≠ÿ≥ÿßÿ® ŸÜÿ≥ÿ®ÿ© ÿßŸÑÿ•ÿ¥ÿ∫ÿßŸÑ
  const calculateOccupancyRate = () => {
    const rentedGames = stats.totalGames - stats.availableGames;
    return stats.totalGames > 0 ? Math.round((rentedGames / stats.totalGames) * 100) : 0;
  };

  // ÿ≠ÿ≥ÿßÿ® ŸÖÿ™Ÿàÿ≥ÿ∑ ÿßŸÑÿ•Ÿäÿ±ÿßÿØ
  const calculateAverageRevenue = () => {
    return stats.totalRentals > 0 ? Math.round(stats.totalRevenue / stats.totalRentals) : 0;
  };



  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="spinner"></div>
        <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...</p>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      {/* ÿ±ÿ£ÿ≥ ÿßŸÑÿµŸÅÿ≠ÿ© */}
      <div className="dashboard-header">
        <div>
          <h1>üëë ŸÑŸàÿ≠ÿ© ÿ™ÿ≠ŸÉŸÖ ÿßŸÑŸÖÿØŸäÿ± ÿßŸÑÿπÿßŸÖ</h1>
          <div className="admin-info">
            <span className="admin-name">ŸÖÿ±ÿ≠ÿ®ÿßŸã {user?.name || 'ŸÖÿØŸäÿ±'}</span>
            <span className="admin-role">({user?.role || 'ŸÖÿØŸäÿ±'})</span>
            <span className="current-time">üïí ÿßŸÑŸàŸÇÿ™ ÿßŸÑÿ≠ÿßŸÑŸä: {formatTime(currentTime)}</span>
          </div>
        </div>
        <div className="header-actions">
          <button onClick={loadDashboardData} className="btn-refresh">
            üîÑ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
          </button>
        </div>
      </div>

      {/* ÿπÿ±ÿ∂ ÿßŸÑÿ£ÿÆÿ∑ÿßÿ° */}
      {error && (
        <div className="alert alert-error">
          <span className="alert-icon">‚ö†Ô∏è</span>
          {error}
        </div>
      )}

      {/* ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ© */}
      <div className="stats-grid">
        <div className="stat-card stat-revenue">
          <div className="stat-icon">üí∞</div>
          <div className="stat-content">
            <h3>ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™</h3>
            <h2>{formatCurrency(stats.totalRevenue)}</h2>
            <div className="stat-trend">
              <span>+{stats.todayRevenue > 0 ? formatCurrency(stats.todayRevenue) : '0'} ÿßŸÑŸäŸàŸÖ</span>
            </div>
          </div>
        </div>

        <div className="stat-card stat-rentals">
          <div className="stat-icon">‚è±Ô∏è</div>
          <div className="stat-content">
            <h3>ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™</h3>
            <h2>{formatNumber(stats.totalRentals)}</h2>
            <div className="stat-trend">
              <span>+{formatNumber(stats.todayRentals)} ÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑŸäŸàŸÖ</span>
            </div>
          </div>
        </div>

        <div className="stat-card stat-active">
          <div className="stat-icon">üöÄ</div>
          <div className="stat-content">
            <h3>ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©</h3>
            <h2>{formatNumber(stats.activeRentals)}</h2>
            <div className="stat-info">
              <span>ÿ≠ÿßŸÑŸäÿßŸã</span>
            </div>
          </div>
        </div>

        <div className="stat-card stat-games">
          <div className="stat-icon">üéÆ</div>
          <div className="stat-content">
            <h3>ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©</h3>
            <h2>{formatNumber(stats.availableGames)}</h2>
            <div className="stat-info">
              <span>ŸÖŸÜ ÿ£ÿµŸÑ {formatNumber(stats.totalGames)}</span>
            </div>
          </div>
        </div>
      </div>

      {/* ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿ™ŸÅÿµŸäŸÑŸäÿ© */}
      <div className="detailed-stats">
        <div className="stat-card">
          <div className="stat-icon">üë•</div>
          <div className="stat-content">
            <h3>ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ</h3>
            <h2>{formatNumber(stats.totalUsers)}</h2>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">üè¨</div>
          <div className="stat-content">
            <h3>ÿπÿØÿØ ÿßŸÑŸÅÿ±Ÿàÿπ</h3>
            <h2>{formatNumber(stats.totalBranches)}</h2>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">üë§</div>
          <div className="stat-content">
            <h3>ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿπŸÖŸÑÿßÿ°</h3>
            <h2>{formatNumber(stats.totalCustomers)}</h2>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">üìä</div>
          <div className="stat-content">
