import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import authService from '../../services/authService';
import service from '../../services/service';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    activeRentals: 0,
    availableGames: 0,
    todayRevenue: 0,
    todayRentals: 0,
    totalUsers: 0,
    totalBranches: 0,
    totalGames: 0
  });
  
  const [recentRentals, setRecentRentals] = useState([]);
  const [topGames, setTopGames] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    const currentUser = authService.getCurrentUser();
    setUser(currentUser);
    loadDashboardData();
    
    // تحديث الوقت كل ثانية
    const timeInterval = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    
    // تحديث البيانات كل دقيقة
    const dataInterval = setInterval(loadDashboardData, 60000);
    
    return () => {
      clearInterval(timeInterval);
      clearInterval(dataInterval);
    };
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      
      // تحميل إحصائيات النظام
      const statsResponse = await authService.getDashboardStats();
      if (statsResponse.success) {
        setStats(statsResponse.data);
      }

      // تحميل آخر التأجيرات النشطة
      const rentalsResponse = await authService.getRecentRentals(5);
      if (rentalsResponse.success) {
        setRecentRentals(rentalsResponse.data);
      }

      // تحميل الألعاب الأكثر تأجيراً
      const gamesResponse = await authService.getTopGames(5);
      if (gamesResponse.success) {
        setTopGames(gamesResponse.data);
      }

    } catch (error) {
      console.error('Error loading dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    if (typeof amount !== 'number') return '0.00 ج';
    return amount.toLocaleString('ar-EG', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }) + ' ج';
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  const getStatusBadgeClass = (status) => {
    switch(status?.toLowerCase()) {
      case 'نشط':
      case 'active': return 'status-active';
      case 'مكتمل':
      case 'completed': return 'status-completed';
      case 'ملغي':
      case 'cancelled': return 'status-cancelled';
      case 'متاح': return 'status-available';
      default: return 'status-default';
    }
  };

  // المدير يمكنه فقط إدارة التأجيرات، ليس إنشاء تأجيرات جديدة
  const handleRentalAction = async (rentalId, action) => {
    try {
      let response;
      
      switch(action) {
        case 'end':
          const paymentMethod = prompt('طريقة الدفع:', 'كاش');
          if (paymentMethod) {
            response = await authService.endRental(rentalId, { 
              payment_method: paymentMethod 
            });
          }
          break;
        case 'cancel':
          const confirmCancel = window.confirm('هل تريد إلغاء هذا التأجير؟');
          if (confirmCancel) {
            response = await authService.cancelRental(rentalId);
          }
          break;
        case 'extend':
          const hours = prompt('أدخل عدد الساعات الإضافية:', '1');
          if (hours && !isNaN(hours)) {
            response = await authService.updateRental(rentalId, {
              extended_hours: parseInt(hours)
            });
          }
          break;
        case 'details':
          window.location.href = `/admin/rentals/${rentalId}`;
          break;
      }
      
      if (response?.success) {
        alert('✅ تم تنفيذ العملية بنجاح');
        loadDashboardData();
      }
    } catch (error) {
      alert('❌ حدث خطأ أثناء تنفيذ العملية');
    }
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="spinner"></div>
        <p>جاري تحميل البيانات...</p>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      {/* رأس الصفحة */}
      <div className="dashboard-header">
        <div>
          <h1>👑 لوحة تحكم المدير العام</h1>
          <div className="admin-info">
            <span className="admin-name">مرحباً {user?.name}</span>
            <span className="admin-role">({user?.role})</span>
            <span className="current-time">🕒 الوقت الحالي: {formatTime(currentTime)}</span>
          </div>
        </div>
        <div className="header-actions">
          <button onClick={loadDashboardData} className="btn-refresh">
            🔄 تحديث البيانات
          </button>
          {/* تم إزالة زر بدء تأجير جديد من لوحة المدير */}
        </div>
      </div>

      {/* الإحصائيات الرئيسية */}
      <div className="stats-grid">
        <div className="stat-card stat-revenue">
          <div className="stat-icon">💰</div>
          <div className="stat-content">
            <h3>إجمالي الإيرادات</h3>
            <h2>{formatCurrency(stats.totalRevenue)}</h2>
            <div className="stat-trend">
              <span>+{stats.todayRevenue > 0 ? formatCurrency(stats.todayRevenue) : '0'} اليوم</span>
            </div>
          </div>
        </div>

        <div className="stat-card stat-rentals">
          <div className="stat-icon">⏱️</div>
          <div className="stat-content">
            <h3>إجمالي التأجيرات</h3>
            <h2>{stats.totalRentals.toLocaleString('ar-EG')}</h2>
            <div className="stat-trend">
              <span>+{stats.todayRentals} تأجير اليوم</span>
            </div>
          </div>
        </div>

        <div className="stat-card stat-active">
          <div className="stat-icon">🚀</div>
          <div className="stat-content">
            <h3>التأجيرات النشطة</h3>
            <h2>{stats.activeRentals}</h2>
            <div className="stat-info">
              <span>حالياً</span>
            </div>
          </div>
        </div>

        <div className="stat-card stat-games">
          <div className="stat-icon">🎮</div>
          <div className="stat-content">
            <h3>الألعاب المتاحة</h3>
            <h2>{stats.availableGames}</h2>
            <div className="stat-info">
              <span>من أصل {stats.totalGames || 0}</span>
            </div>
          </div>
        </div>
      </div>

      {/* إحصائيات تفصيلية */}
      <div className="detailed-stats">
        <div className="stat-card">
          <div className="stat-icon">👥</div>
          <div className="stat-content">
            <h3>إجمالي المستخدمين</h3>
            <h2>{stats.totalUsers || 0}</h2>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">🏬</div>
          <div className="stat-content">
            <h3>عدد الفروع</h3>
            <h2>{stats.totalBranches || 0}</h2>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">📊</div>
          <div className="stat-content">
            <h3>متوسط القيمة</h3>
            <h2>{stats.totalRentals > 0 ? formatCurrency(stats.totalRevenue / stats.totalRentals) : formatCurrency(0)}</h2>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">🎯</div>
          <div className="stat-content">
            <h3>نسبة الإشغال</h3>
            <h2>{stats.totalGames > 0 ? Math.round((stats.totalGames - stats.availableGames) / stats.totalGames * 100) : 0}%</h2>
          </div>
        </div>
      </div>

      {/* الصف الثاني: إجراءات سريعة وجدول التأجيرات */}
      <div className="dashboard-row">
        
        {/* إجراءات سريعة للمدير */}
        <div className="quick-actions-section">
          <div className="section-header">
            <h2>⚡ إجراءات سريعة للمدير</h2>
          </div>
          <div className="actions-grid">
            <Link to="/admin/users" className="action-card admin-action">
              <div className="action-icon">👑</div>
              <div className="action-content">
                <h3>إدارة المستخدمين</h3>
                <p>إضافة، تعديل، حذف المستخدمين والصلاحيات</p>
              </div>
              <span className="action-badge">مدير فقط</span>
            </Link>
            
            <Link to="/admin/games" className="action-card">
              <div className="action-icon">🎮</div>
              <div className="action-content">
                <h3>إدارة الألعاب</h3>
                <p>إضافة وتعديل ألعاب المتجر</p>
              </div>
            </Link>
            
            <Link to="/admin/branches" className="action-card">
              <div className="action-icon">🏬</div>
              <div className="action-content">
                <h3>إدارة الفروع</h3>
                <p>إضافة وتعديل الفروع والألعاب</p>
              </div>
            </Link>
            
            <Link to="/admin/reports" className="action-card admin-action">
              <div className="action-icon">📈</div>
              <div className="action-content">
                <h3>التقارير الشاملة</h3>
                <p>تقارير الإيرادات والأداء</p>
              </div>
              <span className="action-badge">مدير فقط</span>
            </Link>
            
            <Link to="/admin/activities" className="action-card admin-action">
              <div className="action-icon">📊</div>
              <div className="action-content">
                <h3>سجل الأنشطة</h3>
                <p>تتبع جميع أنشطة النظام</p>
              </div>
              <span className="action-badge">مدير فقط</span>
            </Link>
            
            <Link to="/admin/settings" className="action-card admin-action">
              <div className="action-icon">⚙️</div>
              <div className="action-content">
                <h3>إعدادات النظام</h3>
                <p>تعديل إعدادات النظام العام</p>
              </div>
              <span className="action-badge">مدير فقط</span>
            </Link>
          </div>
        </div>

        {/* آخر التأجيرات مع إجراءات */}
        <div className="recent-rentals-section">
          <div className="section-header">
            <h2>🕒 آخر التأجيرات النشطة</h2>
            <Link to="/admin/rentals" className="btn-view-all">
              عرض الكل →
            </Link>
          </div>
          
          {recentRentals.length === 0 ? (
            <div className="empty-state">
              <p>لا توجد تأجيرات نشطة حالياً</p>
              <p className="hint">التأجيرات تتم فقط من خلال الفروع</p>
            </div>
          ) : (
            <div className="rentals-table">
              <table>
                <thead>
                  <tr>
                    <th>رقم التأجير</th>
                    <th>العميل</th>
                    <th>اللعبة</th>
                    <th>الفرع</th>
                    <th>المبلغ</th>
                    <th>الحالة</th>
                    <th>الإجراءات</th>
                  </tr>
                </thead>
                <tbody>
                  {recentRentals.map(rental => (
                    <tr key={rental.id}>
                      <td>
                        <strong>#{rental.rental_number || rental.id}</strong>
                        <div className="rental-time">
                          <small>بدأ: {new Date(rental.start_time).toLocaleTimeString('ar-EG', {hour: '2-digit', minute:'2-digit'})}</small>
                        </div>
                      </td>
                      <td>
                        <div className="customer-info">
                          <div className="customer-name">{rental.customer_name}</div>
                          <small className="customer-phone">{rental.customer_phone}</small>
                        </div>
                      </td>
                      <td>{rental.game_name}</td>
                      <td>{rental.branch_name}</td>
                      <td className="amount">{formatCurrency(rental.total_amount)}</td>
                      <td>
                        <span className={`status-badge ${getStatusBadgeClass(rental.status)}`}>
                          {rental.status}
                        </span>
                      </td>
                      <td>
                        <div className="action-buttons">
                          {rental.status === 'نشط' && (
                            <>
                              <button 
                                className="btn-action btn-success"
                                onClick={() => handleRentalAction(rental.id, 'end')}
                                title="إنهاء التأجير"
                              >
                                ✅
                              </button>
                              <button 
                                className="btn-action btn-warning"
                                onClick={() => handleRentalAction(rental.id, 'extend')}
                                title="تمديد التأجير"
                              >
                                ⏱️
                              </button>
                              <button 
                                className="btn-action btn-danger"
                                onClick={() => handleRentalAction(rental.id, 'cancel')}
                                title="إلغاء التأجير"
                              >
                                ❌
                              </button>
                            </>
                          )}
                          <button 
                            className="btn-action btn-info"
                            onClick={() => handleRentalAction(rental.id, 'details')}
                            title="تفاصيل التأجير"
                          >
                            👁️
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* الصف الثالث: الألعاب الأكثر تأجيراً */}
      <div className="top-games-section">
        <div className="section-header">
          <h2>🏆 الألعاب الأكثر تأجيراً</h2>
          <Link to="/admin/games" className="btn-view-all">
            إدارة الألعاب →
          </Link>
        </div>
        
        {topGames.length === 0 ? (
          <div className="empty-state">
            <p>لا توجد بيانات عن الألعاب</p>
          </div>
        ) : (
          <div className="games-grid">
            {topGames.map((game, index) => (
              <div key={game.id || index} className="game-card">
                <div className="game-image-container">
                  {game.image_url ? (
                    <img 
                      src={`/images/${game.image_url}`}
                      alt={game.name}
                      className="game-image"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = "/images/default-game.jpg";
                      }}
                    />
                  ) : (
                    <div className="game-image-placeholder">
                      <span className="placeholder-icon">🎮</span>
                    </div>
                  )}
                  <div className="game-overlay">
                    <div className="game-rank">🏆 #{index + 1}</div>
                    <div className="game-price">
                      <span className="price-amount">{game.price_per_hour || 0}</span>
                      <span className="price-unit">ج/ساعة</span>
                    </div>
                  </div>
                </div>
                
                <div className="game-card-body">
                  <h3 className="game-title">{game.name}</h3>
                  <div className="game-stats">
                    <div className="stat-item">
                      <span className="stat-label">عدد التأجيرات:</span>
                      <span className="stat-value">{game.rental_count || 0}</span>
                    </div>
                    <div className="stat-item">
                      <span className="stat-label">الإيرادات:</span>
                      <span className="stat-value">{formatCurrency(game.total_revenue || 0)}</span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
