    }
    
    if (formData.duration_type === 'fixed' && !formData.duration_minutes) {
      errors.push('ÙŠØ¬Ø¨ ØªØ­Ø¯ÙŠØ¯ Ù…Ø¯Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ±');
