// frontend/src/pages/employee/Dashboard.jsx (ูุญุฏุซ)
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../../services/api';
import authService from '../../services/authService';
import './EmployeeDashboard.css';

const EmployeeDashboard = () => {
  const [stats, setStats] = useState({
    availableGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });
  
  const [availableGames, setAvailableGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [user, setUser] = useState(null);
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const currentUser = authService.getCurrentUser();
    setUser(currentUser);
    loadDashboardData();
    loadBranchInfo();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      
      // ุชุญููู ุงูุฅุญุตุงุฆูุงุช
      const statsResponse = await api.get('/dashboard/stats');
      if (statsResponse.data.success) {
        setStats(statsResponse.data.data);
      }

      // ุชุญููู ุงูุฃูุนุงุจ ุงููุชุงุญุฉ
      const gamesResponse = await api.get('/games');
      if (gamesResponse.data.success) {
        setAvailableGames(gamesResponse.data.data.filter(game => game.status === 'ูุชุงุญ'));
      }

      // ุชุญููู ุงูุชุฃุฌูุฑุงุช ุงููุดุทุฉ
      const rentalsResponse = await api.get('/rentals?status=ูุดุท');
      if (rentalsResponse.data.success) {
        setActiveRentals(rentalsResponse.data.data);
      }

    } catch (error) {
      console.error('Error loading dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadBranchInfo = async () => {
    try {
      const response = await api.get('/branches');
      if (response.data.success && user?.branch_id) {
        const userBranch = response.data.data.find(b => b.id === user.branch_id);
        setBranch(userBranch);
      }
    } catch (error) {
      console.error('Error loading branch info:', error);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 2
    }).format(amount);
  };

  const formatTime = (dateString) => {
    return new Date(dateString).toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const calculateTimeLeft = (startTime, expectedDuration) => {
    const start = new Date(startTime);
    const expectedEnd = new Date(start.getTime() + expectedDuration * 60000);
    const now = new Date();
    const diff = expectedEnd - now;
    
    if (diff <= 0) return 'ููุชูู';
    
    const minutes = Math.floor(diff / 60000);
    if (minutes < 60) return `${minutes} ุฏูููุฉ`;
    return `${Math.floor(minutes / 60)} ุณุงุนุฉ ${minutes % 60} ุฏูููุฉ`;
  };

  const handleCompleteRental = async (rentalId) => {
    if (window.confirm('ูู ุชุฑูุฏ ุฅููุงุก ูุฐุง ุงูุชุฃุฌูุฑุ')) {
      try {
        await api.put(`/rentals/${rentalId}/complete`);
        loadDashboardData();
      } catch (error) {
        alert('ุญุฏุซ ุฎุทุฃ ูู ุฅููุงุก ุงูุชุฃุฌูุฑ');
      }
    }
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="spinner"></div>
        <p>ุฌุงุฑู ุชุญููู ุงูุจูุงูุงุช...</p>
      </div>
    );
  }

  return (
    <div className="employee-dashboard">
      {/* ุฑุฃุณ ุงูุตูุญุฉ */}
      <div className="dashboard-header">
        <div>
          <h1>ููุญุฉ ุงูููุธู</h1>
          <p>
            {branch?.name ? `ูุฑุน ${branch.name} - ${branch.location}` : 'ูุธุงู ุชุฃุฌูุฑ ุงูุฃูุนุงุจ'}
          </p>
        </div>
        <div className="header-info">
          <span className="welcome">ูุฑุญุจุงู {user?.name}</span>
          <span className="role">
            {user?.role === 'branch_manager' ? 'ูุฏูุฑ ูุฑุน' : 'ููุธู'}
          </span>
        </div>
      </div>

      {/* ุฅุญุตุงุฆูุงุช ุณุฑูุนุฉ */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">๐ฎ</div>
          <div className="stat-content">
            <h3>ุงูุฃูุนุงุจ ุงููุชุงุญุฉ</h3>
            <h2>{stats.availableGames}</h2>
            <p>ูุนุจุฉ ุฌุงูุฒุฉ</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">โฑ๏ธ</div>
          <div className="stat-content">
            <h3>ุงูุชุฃุฌูุฑุงุช ุงููุดุทุฉ</h3>
            <h2>{stats.activeRentals}</h2>
            <p>ุชุฃุฌูุฑ ููุฏ ุงูุชุดุบูู</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">๐ฐ</div>
          <div className="stat-content">
            <h3>ุฅูุฑุงุฏุงุช ุงูููู</h3>
            <h2>{formatCurrency(stats.todayRevenue)}</h2>
            <p>ุฌููู ูุตุฑู</p>
