import React, { useState, useEffect, useCallback } from 'react';
import { 
  Search, RefreshCw, Eye, XCircle, User, Calendar,
  DollarSign, ChevronLeft, ChevronRight, X,
  Clock, Smartphone, Download, BarChart3, History,
  Activity, FilterX, AlertCircle, CheckCircle
} from 'lucide-react';
import api from '../../services/api';
import './RentalsManagement.css';

const RentalsManagement = () => {
  // ==================== Ø§Ù„Ø­Ø§Ù„Ø§Øª Ø§Ù„Ø£Ø³Ø§Ø³ÙŠØ© ====================
  const [rentals, setRentals] = useState([]);
  const [filteredRentals, setFilteredRentals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [apiError, setApiError] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState('checking'); // checking, connected, error
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…
  const [currentUser, setCurrentUser] = useState(null);
  const [userBranch, setUserBranch] = useState(null);
  const [branches, setBranches] = useState([]);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØªØ§Øª
  const [shifts, setShifts] = useState([]);
  const [selectedShift, setSelectedShift] = useState(null);
  const [currentShift, setCurrentShift] = useState(null);
  
  // Ø­Ø§Ù„Ø© Ø§Ù„Ù†ÙˆØ§ÙØ°
  const [showRentalDetailsModal, setShowRentalDetailsModal] = useState(false);
  const [selectedRental, setSelectedRental] = useState(null);
  const [showShiftStatsModal, setShowShiftStatsModal] = useState(false);
  
  // Ø§Ù„ÙÙ„Ø§ØªØ±
  const [filters, setFilters] = useState({
    status: 'all',
    payment_status: 'all',
    dateFrom: '',
    dateTo: ''
  });
  
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(20);
  
  // Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
  const [stats, setStats] = useState({
    totalRentals: 0,
    activeRentals: 0,
    completedRentals: 0,
    cancelledRentals: 0,
    totalRevenue: 0,
    todayRevenue: 0
  });

  // ==================== Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù† Ø§ØªØµØ§Ù„ API ====================
  const checkApiConnection = useCallback(async () => {
    try {
      console.log('ðŸ” ÙØ­Øµ Ø§ØªØµØ§Ù„ API...');
      const healthCheck = await api.healthCheck();
      
      if (healthCheck && healthCheck.success !== false) {
        console.log('âœ… API Ù…ØªØµÙ„ Ø¨Ù†Ø¬Ø§Ø­');
        setConnectionStatus('connected');
        return true;
      } else {
        console.warn('âš ï¸ API Ø§Ø³ØªØ¬Ø§Ø¨ ÙˆÙ„ÙƒÙ† Ù…Ø¹ Ø®Ø·Ø£');
        setConnectionStatus('error');
        return false;
      }
    } catch (error) {
      console.error('âŒ ÙØ´Ù„ Ø§Ù„Ø§ØªØµØ§Ù„ Ø¨Ù€ API:', error);
      setConnectionStatus('error');
      setApiError('ØªØ¹Ø°Ø± Ø§Ù„Ø§ØªØµØ§Ù„ Ø¨Ø§Ù„Ø®Ø§Ø¯Ù…. ØªØ£ÙƒØ¯ Ù…Ù† ØªØ´ØºÙŠÙ„ Ø§Ù„Ø®Ø§Ø¯Ù… Ø¹Ù„Ù‰ Ø§Ù„Ù…Ù†ÙØ° 5000');
      return false;
    }
  }, []);

  // ==================== ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ù…Ù† API ====================
  const loadData = useCallback(async () => {
    setLoading(true);
    setApiError(null);
    
    try {
      // 1. Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù† Ø§ØªØµØ§Ù„ API
      const isConnected = await checkApiConnection();
      if (!isConnected) {
        // Ø¥Ø°Ø§ ÙØ´Ù„ Ø§Ù„Ø§ØªØµØ§Ù„ØŒ Ø§Ø³ØªØ®Ø¯Ù… Ø¨ÙŠØ§Ù†Ø§Øª ØªØ¬Ø±ÙŠØ¨ÙŠØ©
        console.log('ðŸ“± Ø§Ø³ØªØ®Ø¯Ø§Ù… Ø¨ÙŠØ§Ù†Ø§Øª ØªØ¬Ø±ÙŠØ¨ÙŠØ©');
        useMockData();
        setLoading(false);
        return;
      }
      
      // 2. ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…
      const user = api.getCurrentUser();
      setCurrentUser(user);
      console.log('ðŸ‘¤ Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…:', user?.name || 'ØºÙŠØ± Ù…Ø¹Ø±ÙˆÙ');
      
      // 3. ØªØ­Ù…ÙŠÙ„ Ø§Ù„ÙØ±ÙˆØ¹
      try {
        const branchesRes = await api.getBranches();
        if (branchesRes?.success && branchesRes.data) {
          setBranches(branchesRes.data);
        }
      } catch (error) {
        console.warn('âš ï¸ ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ÙØ±ÙˆØ¹:', error);
      }
      
      // 4. ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ÙØ±Ø¹
      if (user?.branch_id) {
        try {
          const branchRes = await api.getBranch(user.branch_id);
          if (branchRes?.success) {
            setUserBranch(branchRes.data);
          }
        } catch (error) {
          console.warn('âš ï¸ ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ÙØ±Ø¹:', error);
