// frontend/src/AppRoutes.jsx
import React from 'react';
import { Routes, Route, Navigate, useLocation } from 'react-router-dom';
import Layout from './components/Layout';

// Import all pages
import Login from './pages/Login';
import AdminDashboard from './pages/admin/Dashboard';
import EmployeeDashboard from './pages/employee/Dashboard';
import AnalyticsPage from './pages/AnalyticsPage';
import ReportsPage from './pages/ReportsPage';
import RentalPage from './pages/RentalPage';

// Create simplified components for missing pages
const UsersManagement = () => <div style={{ padding: '20px' }}>إدارة المستخدمين (قيد التطوير)</div>;
const BranchesManagement = () => <div style={{ padding: '20px' }}>إدارة الفروع (قيد التطوير)</div>;
const GamesManagement = () => <div style={{ padding: '20px' }}>إدارة الألعاب (قيد التطوير)</div>;
const ActivitiesLog = () => <div style={{ padding: '20px' }}>سجل الأنشطة (قيد التطوير)</div>;
const Settings = () => <div style={{ padding: '20px' }}>الإعدادات (قيد التطوير)</div>;
const EmployeeRentals = () => <div style={{ padding: '20px' }}>تأجيرات الموظف (قيد التطوير)</div>;
const EmployeeGames = () => <div style={{ padding: '20px' }}>ألعاب الموظف (قيد التطوير)</div>;

// Protected Route Component
const ProtectedRoute = ({ children, allowedRoles = [] }) => {
  const token = localStorage.getItem('token');
  const userStr = localStorage.getItem('user');
  
  if (!token || !userStr) {
    return <Navigate to="/login" replace />;
  }
  
  try {
    const user = JSON.parse(userStr);
    
    if (allowedRoles.length > 0 && !allowedRoles.includes(user.role)) {
      // Redirect based on role
      if (user.role === 'employee') {
        return <Navigate to="/employee/dashboard" replace />;
      } else {
        return <Navigate to="/admin/dashboard" replace />;
      }
    }
    
    return children;
  } catch (error) {
    return <Navigate to="/login" replace />;
  }
};

const AppRoutes = () => {
  const location = useLocation();
  
  // Check if current route is login page
  const isLoginPage = location.pathname === '/login';
  
  return (
    <Routes>
      {/* Public Routes */}
      <Route path="/login" element={<Login />} />
      
      {/* Admin Routes */}
      <Route path="/admin" element={
        <ProtectedRoute allowedRoles={['admin', 'branch_manager']}>
          <Layout />
        </ProtectedRoute>
      }>
        <Route index element={<Navigate to="dashboard" replace />} />
        <Route path="dashboard" element={<AdminDashboard />} />
        <Route path="users" element={<UsersManagement />} />
        <Route path="branches" element={<BranchesManagement />} />
        <Route path="games" element={<GamesManagement />} />
        <Route path="activities" element={<ActivitiesLog />} />
        <Route path="settings" element={<Settings />} />
      </Route>
      
      {/* Employee Routes */}
      <Route path="/employee" element={
        <ProtectedRoute allowedRoles={['employee']}>
          <Layout />
        </ProtectedRoute>
      }>
        <Route index element={<Navigate to="dashboard" replace />} />
        <Route path="dashboard" element={<EmployeeDashboard />} />
        <Route path="rentals" element={<EmployeeRentals />} />
        <Route path="games" element={<EmployeeGames />} />
      </Route>
      
      {/* Other Routes */}
      <Route path="/analytics" element={
        <ProtectedRoute allowedRoles={['admin', 'branch_manager']}>
          <Layout><AnalyticsPage /></Layout>
        </ProtectedRoute>
      } />
      
      <Route path="/reports" element={
        <ProtectedRoute allowedRoles={['admin', 'branch_manager']}>
          <Layout><ReportsPage /></Layout>
        </ProtectedRoute>
      } />
      
      <Route path="/rental" element={
        <ProtectedRoute allowedRoles={['employee']}>
          <Layout><RentalPage /></Layout>
        </ProtectedRoute>
      } />
      
      {/* Default Redirect */}
      <Route path="/" element={
        localStorage.getItem('token') ? (
          JSON.parse(localStorage.getItem('user'))?.role === 'employee' 
            ? <Navigate to="/employee/dashboard" replace /> 
            : <Navigate to="/admin/dashboard" replace />
        ) : (
          <Navigate to="/login" replace />
        )
      } />
      
      {/* 404 Route */}
      <Route path="*" element={<Navigate to="/" replace />} />
    </Routes>
  );
};

export default AppRoutes;
