    try {
      if (!user?.branch_id) return;
      
      console.log('üéÆ ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ®...');
      
      const response = await api.get('/games', {
        params: { branch_id: user.branch_id }
      });
      
      if (response.success && response.data) {
        // ÿ™ÿµŸÅŸäÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿ≠ÿ≥ÿ® ÿßŸÑÿ≠ÿßŸÑÿ©
        const available = response.data.filter(game => game.status === 'ŸÖÿ™ÿßÿ≠');
        const rented = response.data.filter(game => game.status === 'ŸÖÿ§ÿ¨ÿ±ÿ©');
        const maintenance = response.data.filter(game => game.status === 'ÿµŸäÿßŸÜÿ©');
        
        setAvailableGames(available);
        setRentedGames(rented);
        setMaintenanceGames(maintenance);
        
        // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
        setStats(prev => ({
          ...prev,
          available: available.length,
          rented: rented.length,
          maintenance: maintenance.length
        }));
        
        console.log(`‚úÖ ÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ${response.data.length} ŸÑÿπÿ®ÿ©`);
      } else {
        console.warn('‚ö†Ô∏è ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿ£ŸÑÿπÿßÿ®');
      }
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®:', error);
      showNotification('error', 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ®');
    }
  };

  // ‚úÖ 2. ÿØÿßŸÑÿ© ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
