        params: { 
          branch_id: user.branch_id,
          date: today 
        }
      });
      
      if (response.success) {
        setCompletedRentals(response.data || []);
        console.log(`‚úÖ ÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ${response.data?.length || 0} ÿ™ÿ£ÿ¨Ÿäÿ± ŸÖŸÉÿ™ŸÖŸÑ ÿßŸÑŸäŸàŸÖ`);
