import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { 
  Gamepad2, Clock, User, Phone, MapPin, Mail, 
  ShoppingCart, DollarSign, CheckCircle, XCircle, Eye, 
  Plus, Minus, Trash2, Calendar, Printer, Download,
  RefreshCw, AlertCircle, Check, X, Building, Zap,
  Lock, Unlock, Loader2, Filter, Search, ChevronRight,
  CreditCard, Shield, Star, TrendingUp, Users, Package
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

// ==================== ÿØŸàÿßŸÑ ŸÖÿ≥ÿßÿπÿØÿ© ====================
const formatCurrency = (amount) => {
  if (!amount && amount !== 0) return '0 ÿ¨.ŸÖ';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => {
  if (!phone) return false;
  const cleaned = phone.replace(/\D/g, '');
  return cleaned.length === 11 && cleaned.startsWith('01');
};

const calculateDuration = (startTime, endTime) => {
  if (!startTime || !endTime) return 0;
  const start = new Date(startTime);
  const end = new Date(endTime);
  return Math.round((end - start) / (1000 * 60));
};

// ==================== ŸÖŸÉŸàŸÜ ÿ®ÿ∑ÿßŸÇÿ© ÿßŸÑŸÑÿπÿ®ÿ© ====================
const GameCard = ({ game, onSelect, isSelected }) => {
  const getGameImage = useCallback((game) => {
    if (!game) return '/images/default-game.jpg';
    
    const imageMap = {
      'ÿØÿ±ŸäŸÅÿ™ ŸÉÿßÿ±': '/images/Driftcar.jpg',
      'ŸáÿßŸÅÿ± ÿ®Ÿàÿ±ÿØ': '/images/Hoverboard.jpg',
      'ÿ≥ŸÉŸàÿ™ÿ±': '/images/Scooter.jpg',
      'ÿπÿ±ÿ®ÿ© ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿäÿ©': '/images/Car.jpg',
      'ŸÖŸàÿ™Ÿàÿ≥ŸäŸÉŸÑ ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä': '/images/Motor.jpg',
      'Ÿáÿßÿ±ŸÑŸä': '/images/harley.jpg',
      'ÿ≥Ÿäÿ¨ŸàÿßŸä': '/images/Segway.jpg',
      'ŸÉÿ±ÿßÿ≤Ÿä ŸÉÿßÿ±': '/images/Ninebot.jpg',
      'ÿ≥ŸÉŸäÿ™ ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä': '/images/Skate.jpg',
      'ÿ™ÿ±ÿßŸÖÿ®ŸàŸÑŸäŸÜ': '/images/Trampoline.jpg',
      'ŸÖÿ≠ÿßŸÉŸä': '/images/Simulator.jpg'
    };
    
    if (game.image_url && game.image_url !== 'default-game.jpg') {
      return game.image_url.startsWith('http') ? game.image_url : `/images/${game.image_url}`;
    }
    
    return imageMap[game.name] || '/images/default-game.jpg';
  }, []);

  return (
    <div 
      className={`game-card ${isSelected ? 'selected' : ''}`}
      onClick={() => onSelect(game)}
    >
      <div className="game-image-container">
        <img 
          src={getGameImage(game)} 
          alt={game.name}
          className="game-image"
          onError={(e) => e.target.src = '/images/default-game.jpg'}
        />
        <div className="game-status available">
          <CheckCircle size={14} />
          <span>ŸÖÿ™ÿßÿ≠</span>
        </div>
        {isSelected && (
          <div className="game-selected-indicator">
            <Check size={20} />
          </div>
        )}
      </div>
      
      <div className="game-info">
        <h4>{game.name}</h4>
        <div className="game-category">{game.category}</div>
        <div className="game-price">
          {formatCurrency(game.price_per_15min)} / 15 ÿØŸÇŸäŸÇÿ©
        </div>
        <button className="btn-select-game">
          <Plus size={16} />
          {isSelected ? 'ŸÖÿ≠ÿØÿØÿ©' : 'ÿ•ÿ∂ÿßŸÅÿ©'}
        </button>
      </div>
    </div>
  );
};

// ==================== ŸÖŸÉŸàŸÜ ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ® ====================
const GamesList = ({ games, branchId, selectedGames, onSelectGame }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [availableGames, setAvailableGames] = useState([]);

  useEffect(() => {
    if (!branchId || !games) return;
    
    const branchGames = games.filter(game => {
      if (!game) return false;
      const gameBranchId = parseInt(game.branch_id);
      const currentBranchId = parseInt(branchId);
      return gameBranchId === currentBranchId && game.status === 'ŸÖÿ™ÿßÿ≠';
    });
    
    setAvailableGames(branchGames);
  }, [games, branchId]);

  const filteredGames = useMemo(() => {
    if (!availableGames.length) return [];
    
    return availableGames.filter(game => {
      const matchesSearch = game.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          game.category?.toLowerCase().includes(searchTerm.toLowerCase());
      
      const matchesCategory = selectedCategory === 'all' || game.category === selectedCategory;
      
      return matchesSearch && matchesCategory;
    });
  }, [availableGames, searchTerm, selectedCategory]);

  const categories = useMemo(() => {
    if (!availableGames.length) return ['all'];
    
    const cats = new Set(['all']);
    availableGames.forEach(game => {
      if (game.category) cats.add(game.category);
    });
    return Array.from(cats);
  }, [availableGames]);

  if (!branchId) {
    return (
      <div className="games-section">
        <div className="empty-state">
          <Building size={48} />
          <h3>ÿßÿÆÿ™ÿ± ÿßŸÑŸÅÿ±ÿπ ÿ£ŸàŸÑÿßŸã</h3>
          <p>Ÿäÿ¨ÿ® ÿ™ÿ≠ÿØŸäÿØ ŸÅÿ±ÿπ ŸÑÿπÿ±ÿ∂ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©</p>
        </div>
      </div>
    );
  }

  return (
    <div className="games-section">
      <div className="section-header">
        <div className="section-title">
          <Gamepad2 size={24} />
          <div>
            <h3>ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©</h3>
            <p className="section-subtitle">
              {availableGames.length} ŸÑÿπÿ®ÿ© ŸÖÿ™ÿßÿ≠ÿ© ŸÑŸÑÿ•Ÿäÿ¨ÿßÿ±
            </p>
          </div>
        </div>
        
        <div className="section-controls">
          <div className="search-box">
            <Search size={18} />
            <input
              type="text"
              placeholder="ÿßÿ®ÿ≠ÿ´ ÿπŸÜ ŸÑÿπÿ®ÿ©..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
            {searchTerm && (
              <button onClick={() => setSearchTerm('')} className="clear-search">
                <X size={16} />
              </button>
            )}
          </div>
          
          {categories.length > 1 && (
            <div className="filter-controls">
              <Filter size={18} />
              <select 
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="filter-select"
              >
                {categories.map(cat => (
                  <option key={cat} value={cat}>
                    {cat === 'all' ? 'ÿ¨ŸÖŸäÿπ ÿßŸÑŸÅÿ¶ÿßÿ™' : cat}
                  </option>
                ))}
              </select>
            </div>
          )}
        </div>
      </div>
      
      {availableGames.length === 0 ? (
        <div className="empty-state">
          <Package size={48} />
          <h3>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸÑÿπÿßÿ® ŸÖÿ™ÿßÿ≠ÿ©</h3>
          <p>ÿ¨ŸÖŸäÿπ ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸÖÿ§ÿ¨ÿ±ÿ© ÿ£Ÿà ŸÇŸäÿØ ÿßŸÑÿµŸäÿßŸÜÿ©</p>
        </div>
      ) : filteredGames.length === 0 ? (
        <div className="empty-state">
          <Search size={48} />
          <h3>ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿ£ŸÑÿπÿßÿ®</h3>
          <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸÑÿπÿßÿ® ÿ™ÿ∑ÿßÿ®ŸÇ "{searchTerm}"</p>
        </div>
      ) : (
        <div className="games-grid">
          {filteredGames.map(game => (
            <GameCard
              key={game.id}
              game={game}
              isSelected={selectedGames.some(g => g.id === game.id)}
              onSelect={onSelectGame}
            />
          ))}
        </div>
      )}
    </div>
  );
};

// ==================== ŸÖŸÉŸàŸÜ ÿ™ÿ£ÿ¨Ÿäÿ± ÿ≥ÿ±Ÿäÿπ ====================
const QuickRentalModal = ({ 
  show, 
  onClose, 
  selectedGames, 
  currentShift,
  onConfirmRental 
}) => {
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    phone: '',
    email: ''
  });
  
  const [rentalType, setRentalType] = useState('fixed');
  const [duration, setDuration] = useState(15);
  const [paymentMethod, setPaymentMethod] = useState('ŸÉÿßÿ¥');
  const [notes, setNotes] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const calculateTotal = useCallback(() => {
    return selectedGames.reduce((total, game) => {
      const units = Math.ceil(duration / 15);
      const pricePer15Min = game.price_per_15min || 100;
      return total + (pricePer15Min * units);
    }, 0);
  }, [selectedGames, duration]);

  const handleSubmit = async () => {
    if (!customerInfo.name || !customerInfo.phone) {
      alert('Ÿäÿ±ÿ¨Ÿâ ÿ•ÿØÿÆÿßŸÑ ÿßÿ≥ŸÖ ÿßŸÑÿπŸÖŸäŸÑ Ÿàÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ');
      return;
    }
    
    if (!validatePhone(customerInfo.phone)) {
      alert('ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠. Ÿäÿ¨ÿ® ÿ£ŸÜ ŸäŸÉŸàŸÜ 11 ÿ±ŸÇŸÖÿßŸã ŸàŸäÿ®ÿØÿ£ ÿ®ŸÄ 01');
      return;
    }

    setIsSubmitting(true);

    try {
      const rentalData = {
        customerInfo,
        games: selectedGames,
        rentalType,
        duration: rentalType === 'fixed' ? duration : null,
        paymentMethod,
        notes,
        totalAmount: calculateTotal(),
        shiftId: currentShift?.id,
        isOpenTime: rentalType === 'open'
      };

      await onConfirmRental(rentalData);
      onClose();
      
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', error);
      alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
    } finally {
      setIsSubmitting(false);
    }
  };

  if (!show) return null;

  return (
    <div className="modal-overlay">
      <div className="modal quick-rental-modal">
        <div className="modal-header">
          <h2>
            <ShoppingCart size={24} />
