// ==================== ÿßŸÑŸÖŸÉŸàŸÜ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä ====================
const Rentals = () => {
  const { user } = useAuth();
  const isManager = user?.role === 'admin' || user?.role === 'branch_manager';

  const [loading, setLoading] = useState({
    games: true,
    rentals: true,
    shift: true,
    stats: true
  });
  
  const [games, setGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [currentShift, setCurrentShift] = useState(null);
  const [selectedGames, setSelectedGames] = useState([]);
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

  // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
  const loadAllData = useCallback(async () => {
    setLoading({ games: true, rentals: true, shift: true, stats: true });
    
    try {
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®
      const gamesResponse = await api.getGames({ branch_id: user?.branch_id });
      if (gamesResponse.success) {
        setGames(gamesResponse.data);
        setStats(prev => ({
          ...prev,
          availableGames: gamesResponse.data.filter(g => g.status === 'ŸÖÿ™ÿßÿ≠').length,
          totalGames: gamesResponse.data.length
        }));
      }
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
      const rentalsResponse = await api.getActiveRentals();
      if (rentalsResponse.success) {
        setActiveRentals(rentalsResponse.data);
        setStats(prev => ({
          ...prev,
          activeRentals: rentalsResponse.data.length
        }));
      }
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÜÿ¥ÿ∑
      const shiftResponse = await api.getActiveShift();
      if (shiftResponse.success) {
        setCurrentShift(shiftResponse.data);
      }
      
      setLoading({ games: false, rentals: false, shift: false, stats: false });
      
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', error);
      setLoading({ games: false, rentals: false, shift: false, stats: false });
    }
  }, [user?.branch_id]);

  useEffect(() => {
    if (user?.branch_id) {
      loadAllData();
    }
  }, [user?.branch_id, loadAllData]);

  // ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿÆÿ™ÿßÿ±ÿ©
  const handleSelectGame = (game) => {
    if (!currentShift) {
      alert('Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ£ŸàŸÑÿßŸã');
      return;
    }
    
    if (selectedGames.some(g => g.id === game.id)) {
      // ÿ•ÿ≤ÿßŸÑÿ© ÿßŸÑŸÑÿπÿ®ÿ© ÿ•ÿ∞ÿß ŸÉÿßŸÜÿ™ ŸÖÿÆÿ™ÿßÿ±ÿ© ÿ®ÿßŸÑŸÅÿπŸÑ
      setSelectedGames(selectedGames.filter(g => g.id !== game.id));
    } else {
      // ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÑÿπÿ®ÿ©
      setSelectedGames([...selectedGames, game]);
    }
  };

  // ÿ•ŸÜÿ¥ÿßÿ° ÿ™ÿ£ÿ¨Ÿäÿ±
  const handleCreateRental = async (rentalData) => {
    try {
      const results = [];
      
      for (const game of rentalData.games) {
        const response = await api.createRental({
          game_id: game.id,
          customer_name: rentalData.customerInfo.name,
          customer_phone: rentalData.customerInfo.phone,
          duration_minutes: rentalData.duration,
          rental_type: rentalData.rentalType,
          payment_method: rentalData.paymentMethod,
          notes: rentalData.notes,
          total_amount: rentalData.totalAmount / rentalData.games.length
        });
        
        if (response.success) {
          results.push({
            game: game.name,
            success: true,
            rental_number: response.data?.rental_number
          });
        }
      }
      
      alert(`‚úÖ ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ${results.length} ÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠`);
      
      setShowRentalModal(false);
      setSelectedGames([]);
      loadAllData();
      
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', error);
      alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
    }
  };

  // ŸÖÿπÿßŸÑÿ¨ÿ© ÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
  const handleRentalAction = async (action, rental) => {
    try {
      switch(action) {
        case 'details': {
          alert(`üìã ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:
ÿ±ŸÇŸÖ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±: ${rental.rental_number || 'ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±'}
ÿßŸÑÿπŸÖŸäŸÑ: ${rental.customer_name}
ÿßŸÑŸáÿßÿ™ŸÅ: ${rental.customer_phone}
ÿßŸÑŸÑÿπÿ®ÿ©: ${rental.game_name}
ŸàŸÇÿ™ ÿßŸÑÿ®ÿØÿ°: ${formatDateTime(rental.start_time)}
ÿßŸÑÿ≠ÿßŸÑÿ©: ${rental.status}
ÿßŸÑŸÖÿ®ŸÑÿ∫: ${formatCurrency(rental.total_amount || 0)}
ÿ≠ÿßŸÑÿ© ÿßŸÑÿØŸÅÿπ: ${rental.payment_status}`);
          break;
        }
          
        case 'end': {
          if (window.confirm('ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÜŸáÿßÿ° Ÿáÿ∞ÿß ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿü')) {
            const response = await api.completeRental(rental.id, { payment_method: 'ŸÉÿßÿ¥' });
            if (response.success) {
              alert('‚úÖ ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
              loadAllData();
            }
          }
          break;
        }
          
        case 'cancel': {
          if (window.confirm('ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÑÿ∫ÿßÿ° Ÿáÿ∞ÿß ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿü')) {
            const response = await api.cancelRental(rental.id, 0, 'ÿ™ŸÖ ÿßŸÑÿ•ŸÑÿ∫ÿßÿ° ŸÖŸÜ ŸÇÿ®ŸÑ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ');
            if (response.success) {
              alert('‚úÖ ÿ™ŸÖ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
              loadAllData();
            }
          }
          break;
        }
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°:', error);
      alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ŸÜŸÅŸäÿ∞ ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°');
    }
  };

  // ÿ®ÿØÿ° Ÿàÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™
  const startShift = async () => {
    try {
      const response = await api.startShift();
      if (response.success) {
        setCurrentShift(response.data);
        alert('‚úÖ ÿ™ŸÖ ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™');
        loadAllData();
      }
    } catch (error) {
      alert('‚ùå ŸÅÿ¥ŸÑ ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™');
    }
  };

  const endShift = async () => {
    if (!currentShift) return;
    
    if (window.confirm('ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸäÿü')) {
      try {
        const response = await api.endShift(currentShift.id);
        if (response.success) {
          setCurrentShift(null);
          alert('‚úÖ ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™');
          loadAllData();
        }
      } catch (error) {
        alert('‚ùå ŸÅÿ¥ŸÑ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™');
      }
    }
  };

  if (loading.games) {
    return (
      <div className="loading-container">
        <Loader2 className="spinner" size={48} />
        <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÜÿ∏ÿßŸÖ...</p>
      </div>
    );
  }

  return (
    <div className="rentals-page">
      {/* ÿßŸÑÿ±ÿ£ÿ≥ */}
      <header className="page-header">
        <div className="header-content">
          <div className="header-title">
            <Gamepad2 size={32} />
            <div>
              <h1>ŸÜÿ∏ÿßŸÖ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™</h1>
              <p className="header-subtitle">
                {user?.branch_name} - {isManager ? 'ŸÖÿØŸäÿ±' : 'ŸÖŸàÿ∏ŸÅ'}
              </p>
            </div>
          </div>
          
          <div className="header-actions">
            <div className="selected-games-indicator">
              {selectedGames.length > 0 && (
                <button 
                  onClick={() => setShowRentalModal(true)}
                  className="btn btn-primary"
                >
                  <ShoppingCart size={20} />
                  ÿ®ÿØÿ° ÿ™ÿ£ÿ¨Ÿäÿ± ({selectedGames.length})
                </button>
              )}
            </div>
            
            <button 
              onClick={loadAllData}
              className="btn btn-secondary"
            >
              <RefreshCw size={20} />
              ÿ™ÿ≠ÿØŸäÿ´
            </button>
          </div>
        </div>
        
        <StatsCards 
          stats={stats}
          currentShift={currentShift}
          onStartShift={startShift}
          onEndShift={endShift}
        />
      </header>
      
      {/* ÿ±ÿ≥ÿßŸÑÿ© ÿπŸÜÿØ ÿπÿØŸÖ Ÿàÿ¨ŸàÿØ ÿ¥ŸäŸÅÿ™ */}
      {!currentShift && (
        <div className="no-shift-alert">
          <AlertCircle size={24} />
          <div>
            <h3>ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑</h3>
            <p>Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØ ŸÑŸÑÿ™ŸÖŸÉŸÜ ŸÖŸÜ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™</p>
          </div>
          <button onClick={startShift} className="btn btn-success">
            <Zap size={16} />
            ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØ
          </button>
        </div>
      )}
      
      {/* ÿßŸÑŸÖÿ≠ÿ™ŸàŸâ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä */}
      <main className="main-content">
        {currentShift ? (
          <>
            <GamesList 
              games={games}
              branchId={user?.branch_id}
              selectedGames={selectedGames}
              onSelectGame={handleSelectGame}
            />
            
            <ActiveRentals 
              rentals={activeRentals}
              onAction={handleRentalAction}
              loading={loading.rentals}
            />
          </>
        ) : (
          <div className="waiting-for-shift">
            <Shield size={64} />
            <h2>ŸÅŸä ÿßŸÜÿ™ÿ∏ÿßÿ± ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™</h2>
            <p>ÿßÿ®ÿØÿ£ ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØ ŸÑŸÑŸàÿµŸàŸÑ ÿ•ŸÑŸâ ÿ¨ŸÖŸäÿπ ÿßŸÑŸÖŸÖŸäÿ≤ÿßÿ™</p>
            <button onClick={startShift} className="btn btn-primary btn-lg">
              <Zap size={20} />
              ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØ
            </button>
          </div>
        )}
      </main>
      
      {/* ŸÜÿßŸÅÿ∞ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑÿ≥ÿ±Ÿäÿπ */}
      <QuickRentalModal 
        show={showRentalModal}
        onClose={() => {
          setShowRentalModal(false);
          setSelectedGames([]);
        }}
        selectedGames={selectedGames}
        currentShift={currentShift}
        onConfirmRental={handleCreateRental}
      />
    </div>
  );
};

export default Rentals;
