import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import authService from '../../services/authService';
import './GamesManagement.css';

const GamesManagement = () => {
  const { user } = useAuth();
  const [games, setGames] = useState([]);
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedBranch, setSelectedBranch] = useState('all');
  const [branchStats, setBranchStats] = useState({});

  useEffect(() => {
    loadGamesData();
  }, []);

  const loadGamesData = async () => {
    try {
      setLoading(true);
      setError('');

      const [gamesResponse, branchesResponse] = await Promise.allSettled([
        authService.getGames(),
        authService.getBranches()
      ]);

      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
      if (gamesResponse.status === 'fulfilled' && gamesResponse.value.success) {
        const gamesData = gamesResponse.value.data || [];
        setGames(gamesData);
        calculateBranchStats(gamesData);
      }

      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„ÙØ±ÙˆØ¹
      if (branchesResponse.status === 'fulfilled' && branchesResponse.value.success) {
        setBranches(branchesResponse.value.data || []);
      }

    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', error);
      setError('ØªØ¹Ø°Ø± ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨. ÙŠØ±Ø¬Ù‰ Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø© Ù…Ø±Ø© Ø£Ø®Ø±Ù‰.');
      setGames([]);
      setBranches([]);
      setBranchStats({});
    } finally {
      setLoading(false);
    }
  };

  const calculateBranchStats = (gamesList) => {
    const stats = {};
    
    gamesList.forEach(game => {
      const branchId = game.branch_id;
      const branchName = game.branch_name || `ÙØ±Ø¹ ${branchId || 'ØºÙŠØ± Ù…Ø­Ø¯Ø¯'}`;
      const gameName = game.name;
      
      if (!stats[branchId]) {
        stats[branchId] = {
          name: branchName,
          totalGames: 0,
          gameCounts: {}, // Ø¹Ø¯Ø¯ ÙƒÙ„ Ù‚Ø·Ø¹Ø© Ù…Ù† Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
          gamesByStatus: {
            Ù…ØªØ§Ø­: 0,
            Ù…Ø¤Ø¬Ø±Ø©: 0,
            ØµÙŠØ§Ù†Ø©: 0,
            'ØºÙŠØ± Ù…ØªØ§Ø­': 0
          }
        };
      }
      
      // Ø²ÙŠØ§Ø¯Ø© Ø§Ù„Ø¹Ø¯Ø¯ Ø§Ù„Ø¥Ø¬Ù…Ø§Ù„ÙŠ Ù„Ù„Ø£Ù„Ø¹Ø§Ø¨ ÙÙŠ Ù‡Ø°Ø§ Ø§Ù„ÙØ±Ø¹
      stats[branchId].totalGames++;
      
      // Ø²ÙŠØ§Ø¯Ø© Ø¹Ø¯Ø¯ ÙƒÙ„ Ù‚Ø·Ø¹Ø© Ù…Ù† Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
      stats[branchId].gameCounts[gameName] = (stats[branchId].gameCounts[gameName] || 0) + 1;
      
      // Ø²ÙŠØ§Ø¯Ø© Ø¹Ø¯Ø¯ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø­Ø³Ø¨ Ø§Ù„Ø­Ø§Ù„Ø©
      const status = game.status || 'Ù…ØªØ§Ø­';
      stats[branchId].gamesByStatus[status] = (stats[branchId].gamesByStatus[status] || 0) + 1;
    });
    
    setBranchStats(stats);
  };

  const getGamesByBranch = (branchId) => {
    if (branchId === 'all') {
      return games;
    }
    return games.filter(game => game.branch_id == branchId);
  };

  const calculateTotalStats = () => {
    const totals = {
      totalGames: games.length,
      totalBranches: Object.keys(branchStats).length,
      availableGames: 0,
      rentedGames: 0,
      maintenanceGames: 0
    };

    games.forEach(game => {
      switch (game.status) {
        case 'Ù…ØªØ§Ø­':
          totals.availableGames++;
          break;
        case 'Ù…Ø¤Ø¬Ø±Ø©':
          totals.rentedGames++;
          break;
        case 'ØµÙŠØ§Ù†Ø©':
          totals.maintenanceGames++;
          break;
      }
    });

    return totals;
  };

  if (loading) {
    return (
      <div className="games-management-page">
        <div className="loading-container">
          <div className="spinner"></div>
          <p>Ø¬Ø§Ø±ÙŠ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨...</p>
        </div>
      </div>
    );
  }

  const totals = calculateTotalStats();
  const filteredGames = getGamesByBranch(selectedBranch);
  const selectedBranchData = selectedBranch !== 'all' ? branchStats[selectedBranch] : null;

  return (
    <div className="games-management-page">
      {/* Ø±Ø£Ø³ Ø§Ù„ØµÙØ­Ø© */}
      <div className="page-header">
        <div className="header-left">
          <h1>ğŸ® Ù…Ø®Ø²ÙˆÙ† Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø­Ø³Ø¨ Ø§Ù„ÙØ±ÙˆØ¹</h1>
          <p className="page-description">Ø¹Ø±Ø¶ ØªÙˆØ²ÙŠØ¹ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ÙˆØ¹Ø¯Ø¯ Ø§Ù„Ù‚Ø·Ø¹ ÙÙŠ ÙƒÙ„ ÙØ±Ø¹</p>
        </div>
        <div className="header-right">
          <button 
            className="btn btn-secondary"
            onClick={loadGamesData}
          >
            ğŸ”„ ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
          </button>
        </div>
      </div>

      {error && (
        <div className="alert alert-error">
          <span>âš ï¸ {error}</span>
          <button onClick={loadGamesData}>Ø¥Ø¹Ø§Ø¯Ø© Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø©</button>
        </div>
      )}

      {/* Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø¥Ø¬Ù…Ø§Ù„ÙŠØ© */}
      <div className="total-stats">
        <h2>ğŸ“Š Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„Ø¥Ø¬Ù…Ø§Ù„ÙŠØ©</h2>
        <div className="stats-grid">
          <div className="stat-card stat-primary">
            <div className="stat-icon">ğŸ¬</div>
            <div className="stat-content">
              <h3>Ø¹Ø¯Ø¯ Ø§Ù„ÙØ±ÙˆØ¹</h3>
              <p className="stat-value">{totals.totalBranches}</p>
            </div>
          </div>

          <div className="stat-card stat-success">
            <div className="stat-icon">ğŸ®</div>
            <div className="stat-content">
              <h3>Ø¥Ø¬Ù…Ø§Ù„ÙŠ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨</h3>
              <p className="stat-value">{totals.totalGames}</p>
            </div>
          </div>

          <div className="stat-card stat-info">
            <div className="stat-icon">âœ…</div>
            <div className="stat-content">
              <h3>Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„Ù…ØªØ§Ø­Ø©</h3>
              <p className="stat-value">{totals.availableGames}</p>
            </div>
          </div>

          <div className="stat-card stat-warning">
            <div className="stat-icon">â±ï¸</div>
            <div className="stat-content">
              <h3>Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„Ù…Ø¤Ø¬Ø±Ø©</h3>
              <p className="stat-value">{totals.rentedGames}</p>
            </div>
          </div>
        </div>
      </div>

      {/* ÙÙ„ØªØ±Ø© Ø­Ø³Ø¨ Ø§Ù„ÙØ±Ø¹ */}
      <div className="branch-filter">
        <h2>ğŸ¬ Ø§Ø®ØªØ± Ø§Ù„ÙØ±Ø¹ Ù„Ø¹Ø±Ø¶ ØªÙØ§ØµÙŠÙ„Ù‡</h2>
        <div className="filter-controls">
          <select 
            value={selectedBranch} 
            onChange={(e) => setSelectedBranch(e.target.value)}
            className="branch-select"
          >
            <option value="all">ğŸ“‹ Ø¬Ù…ÙŠØ¹ Ø§Ù„ÙØ±ÙˆØ¹</option>
            {branches.map(branch => (
              <option key={branch.id} value={branch.id}>
                {branch.name} - {branchStats[branch.id]?.totalGames || 0} Ù„Ø¹Ø¨Ø©
              </option>
            ))}
          </select>
        </div>
      </div>

      {/* Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„ÙØ±Ø¹ Ø§Ù„Ù…Ø­Ø¯Ø¯ */}
      {selectedBranch !== 'all' && selectedBranchData && (
        <div className="branch-details">
          <div className="branch-header-card">
            <h2>ğŸ“‹ ØªÙØ§ØµÙŠÙ„ Ø§Ù„ÙØ±Ø¹: {selectedBranchData.name}</h2>
            <div className="branch-summary">
              <div className="summary-item">
                <span className="summary-label">Ø¥Ø¬Ù…Ø§Ù„ÙŠ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨</span>
                <span className="summary-value">{selectedBranchData.totalGames}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Ù…ØªØ§Ø­Ø©</span>
                <span className="summary-value success">{selectedBranchData.gamesByStatus.Ù…ØªØ§Ø­ || 0}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Ù…Ø¤Ø¬Ø±Ø©</span>
                <span className="summary-value warning">{selectedBranchData.gamesByStatus.Ù…Ø¤Ø¬Ø±Ø© || 0}</span>
