} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';
const loadShiftRevenue = useCallback(async () => {
  if (!currentShift || !currentShift.id) return;
  
  try {
    // Ø·Ø±ÙŠÙ‚Ø© 1: Ø§Ø­Ø³Ø¨ Ù…Ù† Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø©
    const revenue = await calculateRealRevenue();
    
    setStats(prev => ({
      ...prev,
      todayRevenue: revenue
    }));
    
    console.log('ğŸ’° Ø¥ÙŠØ±Ø§Ø¯ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ù…Ø­Ø¯Ø«:', formatCurrency(revenue));
    
  } catch (error) {
    console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¥ÙŠØ±Ø§Ø¯ Ø§Ù„Ø´ÙŠÙØª:', error);
  }
}, [currentShift?.id, calculateRealRevenue]);
const formatCurrency = (amount) => {
  if (!amount && amount !== 0) return '0 Ø¬.Ù…';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => {
  if (!phone || phone.trim() === '') return true;
  const cleaned = phone.replace(/\D/g, '');
  return cleaned.length === 11 && cleaned.startsWith('01');
};

const calculateDuration = (startTime, endTime) => {
  if (!startTime || !endTime) return 0;
  const start = new Date(startTime);
  const end = new Date(endTime);
  return Math.round((end - start) / (1000 * 60));
};



const Rentals = () => {
  const { user } = useAuth();
  const isManager = user?.role === 'admin' || user?.role === 'branch_manager';
  
  const [loading, setLoading] = useState({
    games: true,
    rentals: true,
    shift: true,
    stats: true
  });
  
  const [games, setGames] = useState([]);
  const [allGames, setAllGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [currentShift, setCurrentShift] = useState(null);
  const [selectedGames, setSelectedGames] = useState([]);
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [selectedRentalForChange, setSelectedRentalForChange] = useState(null);
  const [showRerentalModal, setShowRerentalModal] = useState(false);
  const [selectedCompletedRental, setSelectedCompletedRental] = useState(null);

  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

  const refreshAllData = useCallback(async (force = false) => {
    if (loading.games && loading.rentals && loading.shift && !force) {
      console.log('â³ Ø§Ù„ØªØ­Ø¯ÙŠØ« Ù‚ÙŠØ¯ Ø§Ù„ØªÙ†ÙÙŠØ° Ø¨Ø§Ù„ÙØ¹Ù„...');
      return;
    }
    
    console.log('ğŸ”„ ØªØ­Ø¯ÙŠØ« Ø´Ø§Ù…Ù„ Ù„Ù„Ø¨ÙŠØ§Ù†Ø§Øª...');
    
    setLoading({ games: true, rentals: true, shift: true, stats: true });
    
    try {
      await loadGames();
      await loadCurrentShift();
      
      if (currentShift && currentShift.id) {
        await Promise.all([
          loadActiveRentalsForCurrentShift(),
          isManager ? loadCompletedRentals() : Promise.resolve()
        ]);
      }
    } catch (error) {
      console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª:', error);
    } finally {
      setTimeout(() => {
        setLoading({ games: false, rentals: false, shift: false, stats: false });
      }, 300);
    }
  }, [currentShift?.id, isManager]);

  const loadGames = useCallback(async () => {
    try {
      console.log('ğŸ® ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ù„Ù„ÙØ±Ø¹', user?.branch_id);
      
      if (!user?.branch_id) {
        console.log('âš ï¸ Ù„Ø§ ÙŠÙˆØ¬Ø¯ ÙØ±Ø¹ Ù…Ø­Ø¯Ø¯');
        setGames([]);
        return;
      }
      
      const response = await api.loadGamesSmart(user.branch_id);
        
      if (response.success) {
        console.log(`âœ… ØªÙ… ØªØ­Ù…ÙŠÙ„ ${response.data.length} Ù„Ø¹Ø¨Ø©`);
        setGames(response.data);
        setAllGames(response.data);
        setStats(prev => ({
          ...prev,
          availableGames: response.data.length,
          totalGames: response.data.length
        }));
      } else {
        console.warn('âš ï¸ ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', response.message);
        setGames([]);
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', error);
      setGames([]);
    } finally {
      setLoading(prev => ({ ...prev, games: false }));
    }
  }, [user?.branch_id]);

  const loadCurrentShift = useCallback(async (forceRefresh = false) => {
    try {
      const response = await api.getCurrentShift();
      
      if (response.success && response.data) {
        const shift = response.data;
        
        if (shift.employee_id === user?.id || shift.employee_name === user?.name) {
          setCurrentShift(shift);
          localStorage.setItem('current_shift', JSON.stringify(shift));
          return shift;
        } else {
          console.warn('âš ï¸ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ù†Ø´Ø· Ù„Ø§ ÙŠÙ†ØªÙ…ÙŠ Ù„Ù„Ù…Ø³ØªØ®Ø¯Ù… Ø§Ù„Ø­Ø§Ù„ÙŠ');
          setCurrentShift(null);
          localStorage.removeItem('current_shift');
          return null;
        }
      } else {
        setCurrentShift(null);
        localStorage.removeItem('current_shift');
        return null;
      }
    } catch (error) {
      console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØª:', error);
      setCurrentShift(null);
      localStorage.removeItem('current_shift');
      return null;
    } finally {
      setLoading(prev => ({ ...prev, shift: false }));
    }
  }, [user?.id, user?.name]);

const loadActiveRentalsForCurrentShift = useCallback(async () => {
  if (!user?.branch_id || !currentShift?.id) {
    console.log('âš ï¸ Ù„Ø§ ÙŠÙˆØ¬Ø¯ ÙØ±Ø¹ Ù…Ø­Ø¯Ø¯ Ø£Ùˆ Ø´ÙŠÙØª Ù†Ø´Ø·');
    setActiveRentals([]);
    return;
  }
  
  try {
    setLoading(prev => ({ ...prev, rentals: true }));
    
    console.log(`ğŸ”„ Ø¬Ù„Ø¨ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø© Ù„Ù„Ø´ÙŠÙØª #${currentShift.id}`);
    
    // Ø§Ø³ØªØ®Ø¯Ù… Ø§Ù„Ù†Ù‚Ø·Ø© Ø§Ù„Ø¹Ø§Ù…Ø© Ù…Ø¹ Ø§Ù„ÙÙ„ØªØ±Ø©
    const response = await api.get('/rentals', {
      params: {
        shift_id: currentShift.id,
        branch_id: user.branch_id,
        status: 'Ù†Ø´Ø·'
      }
    });
    
    if (response.success && response.data) {
      console.log(`âœ… ØªÙ… ØªØ­Ù…ÙŠÙ„ ${response.data.length} ØªØ£Ø¬ÙŠØ± Ù†Ø´Ø· Ù„Ù„Ø´ÙŠÙØª Ø§Ù„Ø­Ø§Ù„ÙŠ`);
      setActiveRentals(response.data);
    } else {
      console.warn('âš ï¸ ÙØ´Ù„ Ø¬Ù„Ø¨ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª:', response.message);
      setActiveRentals([]);
    }
    
  } catch (error) {
    console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø©:', error);
    setActiveRentals([]);
  } finally {
    setLoading(prev => ({ ...prev, rentals: false }));
  }
}, [currentShift?.id, user?.branch_id]);

const loadCompletedRentals = useCallback(async () => {
  if (!isManager || !currentShift?.id) return;
  
  try {
    // Ø§Ø³ØªØ®Ø¯Ù… Ø§Ù„Ù†Ù‚Ø·Ø© Ø§Ù„Ø¹Ø§Ù…Ø© Ù…Ø¹ Ø§Ù„ÙÙ„ØªØ±Ø©
    const response = await api.get('/rentals', {
      params: {
        shift_id: currentShift.id,
        branch_id: user?.branch_id,
        status: 'Ù…ÙƒØªÙ…Ù„'
      }
    });
    
    if (response.success && response.data) {
      const completedRentalsData = response.data;
      
      setCompletedRentals(completedRentalsData);
      
      // âœ… Ø­Ø³Ø§Ø¨ Ø§Ù„Ø¥ÙŠØ±Ø§Ø¯ Ø¨Ø´ÙƒÙ„ ØµØ­ÙŠØ­
      let completedRevenue = 0;
      completedRentalsData.forEach(rental => {
        if (rental.status === 'Ù…ÙƒØªÙ…Ù„') {
          if (rental.rental_type === 'open' || rental.is_open_time === 1) {
            // Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ù…ÙØªÙˆØ­: Ø£Ø¶Ù final_amount ÙÙ‚Ø· (ØªÙ… Ø¯ÙØ¹Ù‡ Ø¹Ù†Ø¯ Ø§Ù„Ø¥Ù†Ù‡Ø§Ø¡)
            completedRevenue += parseFloat(rental.final_amount || 0);
          } else {
            // Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ø«Ø§Ø¨Øª: Ø£Ø¶Ù paid_amount (ØªÙ… Ø¯ÙØ¹Ù‡ Ø¹Ù†Ø¯ Ø§Ù„Ø¥Ù†Ø´Ø§Ø¡)
            completedRevenue += parseFloat(rental.paid_amount || rental.total_amount || 0);
          }
        }
      });
      
      setStats(prev => ({
        ...prev,
        todayRevenue: completedRevenue,
        todayRentals: completedRentalsData.length
      }));
    }
  } catch (error) {
    console.error('âŒ Ø®Ø·Ø£ ÙÙŠ Ø¬Ù„Ø¨ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø©:', error);
    setCompletedRentals([]);
  }
}, [currentShift?.id, isManager, user?.branch_id]);

  const processCompletedRentalsManual = useCallback(async () => {
    try {
      console.log('ğŸ”„ Ù…Ø¹Ø§Ù„Ø¬Ø© ÙŠØ¯ÙˆÙŠØ© Ù„Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø©...');
      
      if (!activeRentals || activeRentals.length === 0) {
        console.log('âœ… Ù„Ø§ ØªÙˆØ¬Ø¯ ØªØ£Ø¬ÙŠØ±Ø§Øª Ù†Ø´Ø·Ø© ØªØ­ØªØ§Ø¬ Ù…Ø¹Ø§Ù„Ø¬Ø©');
        return { success: true, data: { processed_count: 0 } };
      }
      
      const now = new Date();
      let hasExpired = false;
      
      for (const rental of activeRentals) {
        if (!rental.start_time) continue;
        
        const rentalType = getRentalType(rental);
        const startTime = new Date(rental.start_time);
        
        if (rentalType === 'fixed') {
          const duration = rental.duration_minutes || 15;
          const endTime = new Date(startTime.getTime() + duration * 60000);
          
          if (now > endTime) {
            hasExpired = true;
            console.log(`â° ØªØ£Ø¬ÙŠØ± ${rental.id} Ø§Ù†ØªÙ‡Ù‰ ÙˆÙ‚ØªÙ‡ (Ø«Ø§Ø¨Øª)`);
          }
        }
      }
      
      if (!hasExpired) {
        console.log('âœ… Ù„Ø§ ØªÙˆØ¬Ø¯ ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù†ØªÙ‡Ù‰ ÙˆÙ‚ØªÙ‡Ø§ Ø­Ø§Ù„ÙŠØ§Ù‹');
        return { success: true, data: { processed_count: 0 } };
      }
      
      try {
        const response = await api.post('/rentals/process-completed-simple');
        console.log('âœ… Ù†ØªÙŠØ¬Ø© Ø§Ù„Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ø¨Ø³ÙŠØ·Ø©:', response);
        return response;
      } catch (error) {
        console.warn('âš ï¸ ÙØ´Ù„Øª Ø§Ù„Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ø¨Ø³ÙŠØ·Ø©:', error.message);
        
        const expiredRentals = activeRentals.filter(rental => {
          if (!rental.start_time) return false;
          const startTime = new Date(rental.start_time);
          const rentalType = getRentalType(rental);
          
          if (rentalType === 'fixed') {
            const duration = rental.duration_minutes || 15;
            const endTime = new Date(startTime.getTime() + duration * 60000);
            return now > endTime;
          }
          return false;
        });
        
        if (expiredRentals.length === 0) {
          return { success: true, data: { processed_count: 0 } };
        }
        
        console.log(`ğŸ”§ Ù…Ø¹Ø§Ù„Ø¬Ø© ${expiredRentals.length} ØªØ£Ø¬ÙŠØ± ÙŠØ¯ÙˆÙŠØ§Ù‹`);
        
        for (const rental of expiredRentals) {
