                payment_method: 'ÙƒØ§Ø´'
              });
            } else {
              await api.completeRental(rental.id, {
                payment_method: 'ÙƒØ§Ø´',
                final_amount: rental.paid_amount || rental.total_amount
              });
            }
            
            console.log(`âœ… ØªÙ… Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ± ${rental.id}`);
          } catch (rentalError) {
            console.warn(`âš ï¸ ÙØ´Ù„ Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ± ${rental.id}:`, rentalError.message);
          }
          
          await new Promise(resolve => setTimeout(resolve, 300));
        }
        
        setTimeout(() => {
          refreshAllData();
        }, 2000);
        
        return { 
          success: true, 
          data: { 
            processed_count: expiredRentals.length,
            manual_processing: true 
          } 
        };
      }
      
    } catch (error) {
      console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ Ø§Ù„Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„ÙŠØ¯ÙˆÙŠØ©:', error);
      return {
        success: false,
        message: 'Ø®Ø·Ø£ ÙÙŠ Ø§Ù„Ù…Ø¹Ø§Ù„Ø¬Ø©: ' + error.message
      };
    }
  }, [activeRentals, refreshAllData]);

  const getRentalType = useCallback((rental) => {
    if (!rental) return 'fixed';
    
    // Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù† Ù†ÙˆØ¹ Ø§Ù„ØªØ£Ø¬ÙŠØ± Ù…Ø±Ø© ÙˆØ§Ø­Ø¯Ø© ÙÙ‚Ø·
    if (rental.rental_type === 'open') {
      return 'open';
    }
    
    if (rental.rental_type === 'fixed') {
      return 'fixed';
    }
    
    if (rental.is_open_time === 1) {
      return 'open';
    }
    
    if (rental.is_open_time === 0) {
      return 'fixed';
