import React, { createContext, useState, useContext, useEffect } from 'react';
import authService from '../services/authService';

// ÿ•ŸÜÿ¥ÿßÿ° Context ŸÖÿπ ŸÇŸäŸÖÿ© ÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©
const AuthContext = createContext({
  user: null,
  loading: true,
  login: async () => ({ success: false, message: 'Not implemented' }),
  logout: () => {},
  isAuthenticated: false,
  isAdmin: false,
  isBranchManager: false,
  isEmployee: false
});

// Custom Hook ŸÑÿßÿ≥ÿ™ÿÆÿØÿßŸÖ AuthContext
export const useAuth = () => {
  const context = useContext(AuthContext);
  
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadUser = async () => {
      try {
        console.log('üîç ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ...');
        const currentUser = authService.getCurrentUser();
        
        if (currentUser) {
          console.log('‚úÖ ÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ŸÖÿ≥ÿ™ÿÆÿØŸÖ:', currentUser);
          setUser(currentUser);
        } else {
          console.log('‚ÑπÔ∏è ŸÑÿß ŸäŸàÿ¨ÿØ ŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿ≥ÿ¨ŸÑ');
          setUser(null);
        }
      } catch (error) {
        console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:', error);
        setUser(null);
      } finally {
        setLoading(false);
      }
    };

    loadUser();
  }, []);

  const login = async (email, password) => {
    try {
      console.log('üîê ŸÖÿ≠ÿßŸàŸÑÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ:', email);
      const response = await authService.login(email, password);
      
      console.log('üìã ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÅŸä Context:', response);
      
      if (response && response.success) {
        // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖŸÜ ÿßŸÑÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ©
        let userData = null;
        
        if (response.user) {
          userData = response.user;
        } else if (response.data && response.data.user) {
          userData = response.data.user;
        } else {
          // ÿ•ÿ∞ÿß ŸÑŸÖ ÿ™ŸÉŸÜ ŸáŸÜÿßŸÉ ÿ®ŸäÿßŸÜÿßÿ™ ŸÖÿ≥ÿ™ÿÆÿØŸÖÿå ÿßÿ≠ÿµŸÑ ÿπŸÑŸäŸáÿß ŸÖŸÜ localStorage
          userData = authService.getCurrentUser();
        }
        
        if (userData) {
          setUser(userData);
          return { success: true, user: userData };
        } else {
          return { 
            success: false, 
            message: 'ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸàŸÑŸÉŸÜ ŸÑŸÖ Ÿäÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ'
          };
        }
      } else {
        return { 
          success: false, 
          message: response?.message || 'ŸÅÿ¥ŸÑ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ' 
        };
      }
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ:', error);
      return { 
        success: false, 
        message: 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ' 
      };
    }
  };

  const logout = () => {
    console.log('üö™ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨...');
    authService.logout();
    setUser(null);
  };

  const value = {
    user,
    loading,
    login,
    logout,
    isAuthenticated: !!user,
    isAdmin: user?.role === 'admin',
    isBranchManager: user?.role === 'branch_manager',
    isEmployee: user?.role === 'employee'
  };

  console.log('üîÑ ŸÇŸäŸÖÿ© AuthContext:', value);

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

// ÿ™ÿµÿØŸäÿ± ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿä ŸÑŸÄ useContext (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)
export default useAuth;
