import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import authService from '../../services/authService';
import { toast } from 'react-hot-toast';
import Logo from '../../components/Logo';

function GamesManagement() {
  const { user } = useAuth();
  const [games, setGames] = useState([]);
  const [branches, setBranches] = useState([]);
  const [gameTypes, setGameTypes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [showCustomGame, setShowCustomGame] = useState(false);
  const [formData, setFormData] = useState({
    code: '',
    name: '',
    game_type: '',
    custom_name: '',
    description: '',
    base_price: '',
    price_per_15min: '',
    quantity: '1',
    branch_id: '',
    status: 'ACTIVE'
  });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [gamesRes, branchesRes, typesRes] = await Promise.all([
        authService.getGames(),
        authService.getBranches(),
        authService.getGameTypes()
      ]);
      
      setGames(gamesRes.games);
      setBranches(branchesRes.branches);
      setGameTypes(typesRes.gameTypes);
    } catch (error) {
      toast.error(error.message);
    } finally {
      setLoading(false);
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.code.trim()) newErrors.code = 'كود اللعبة مطلوب';
    else if (!/^[A-Z0-9]{2,10}$/.test(formData.code.toUpperCase())) {
      newErrors.code = 'الكود يجب أن يحتوي على أحرف وأرقام فقط (2-10 حرف)';
    }
    
    if (showCustomGame && !formData.custom_name.trim()) {
      newErrors.custom_name = 'اسم اللعبة الجديدة مطلوب';
    } else if (!showCustomGame && !formData.game_type) {
      newErrors.game_type = 'نوع اللعبة مطلوب';
    }
    
    if (!formData.price_per_15min || parseFloat(formData.price_per_15min) <= 0) {
      newErrors.price_per_15min = 'سعر ربع الساعة يجب أن يكون أكبر من صفر';
    }
    
    if (!formData.quantity || parseInt(formData.quantity) < 1) {
      newErrors.quantity = 'الكمية يجب أن تكون 1 على الأقل';
    }
    
    if (!formData.branch_id) newErrors.branch_id = 'الفرع مطلوب';
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleGameTypeChange = (e) => {
    const value = e.target.value;
    setFormData({...formData, game_type: value});
    
    if (value === 'custom') {
      setShowCustomGame(true);
    } else {
      setShowCustomGame(false);
      setFormData({...formData, game_type: value, name: value});
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      toast.error('يوجد أخطاء في النموذج');
      return;
    }

    try {
      const gameData = {
        code: formData.code.toUpperCase(),
        name: showCustomGame ? formData.custom_name : formData.game_type,
        description: formData.description,
        price_per_15min: parseFloat(formData.price_per_15min),
        quantity: parseInt(formData.quantity),
        branch_id: formData.branch_id,
        status: formData.status
      };

      const response = await authService.createGame(gameData);
      
      if (response.success) {
        toast.success('تم إنشاء اللعبة بنجاح');
        setShowModal(false);
        resetForm();
        loadData();
      }
    } catch (error) {
      toast.error(error.message);
    }
  };

  const resetForm = () => {
    setFormData({
      code: '',
      name: '',
      game_type: '',
      custom_name: '',
      description: '',
      base_price: '',
      price_per_15min: '',
      quantity: '1',
      branch_id: '',
      status: 'ACTIVE'
    });
    setShowCustomGame(false);
  };

  const handleDelete = async (gameId, gameName) => {
    if (!window.confirm(`هل أنت متأكد من حذف اللعبة "${gameName}"؟`)) return;

    try {
      const response = await authService.deleteGame(gameId);
      if (response.success) {
        toast.success('تم حذف اللعبة بنجاح');
        loadData();
      }
    } catch (error) {
      toast.error(error.message);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="spinner">جاري التحميل...</div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-800">إدارة الألعاب</h1>
          <p className="text-gray-600">إدارة وإضافة ألعاب النظام</p>
        </div>
        <div className="flex items-center gap-4">
          <Logo size="sm" />
          <button
            onClick={() => setShowModal(true)}
            className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg flex items-center gap-2"
          >
            <span>+</span>
            <span>إضافة لعبة جديدة</span>
          </button>
        </div>
      </div>

      {/* إحصائيات الألعاب */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6">
        <div className="bg-white p-6 rounded-lg shadow">
          <div className="flex items-center">
            <div className="p-3 bg-blue-100 rounded-lg mr-4">
              <span className="text-2xl">🎮</span>
            </div>
            <div>
              <p className="text-gray-600">إجمالي الألعاب</p>
              <h3 className="text-2xl font-bold">{games.length}</h3>
            </div>
          </div>
        </div>
        
        <div className="bg-white p-6 rounded-lg shadow">
          <div className="flex items-center">
            <div className="p-3 bg-green-100 rounded-lg mr-4">
              <span className="text-2xl">✅</span>
            </div>
            <div>
              <p className="text-gray-600">متاح للإيجار</p>
              <h3 className="text-2xl font-bold">
                {games.filter(g => g.is_active && g.is_available && g.quantity > 0).length}
              </h3>
            </div>
          </div>
        </div>
        
        <div className="bg-white p-6 rounded-lg shadow">
          <div className="flex items-center">
            <div className="p-3 bg-yellow-100 rounded-lg mr-4">
              <span className="text-2xl">⏸️</span>
            </div>
            <div>
              <p className="text-gray-600">غير متاح</p>
              <h3 className="text-2xl font-bold">
                {games.filter(g => !g.is_available || g.quantity <= 0).length}
              </h3>
            </div>
          </div>
        </div>
        
        <div className="bg-white p-6 rounded-lg shadow">
          <div className="flex items-center">
            <div className="p-3 bg-red-100 rounded-lg mr-4">
              <span className="text-2xl">❌</span>
            </div>
            <div>
              <p className="text-gray-600">غير نشط</p>
              <h3 className="text-2xl font-bold">
                {games.filter(g => !g.is_active).length}
              </h3>
            </div>
          </div>
        </div>
      </div>

      {/* جدول الألعاب */}
      <div className="bg-white rounded-lg shadow overflow-hidden">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase">الكود</th>
              <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase">اسم اللعبة</th>
              <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase">النوع</th>
              <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase">الفرع</th>
              <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase">السعر/¼س</th>
              <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase">الكمية</th>
              <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase">الحالة</th>
              <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase">الإجراءات</th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {games.map((game) => (
              <tr key={game.id}>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm font-mono font-bold text-blue-600">{game.code}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm font-medium text-gray-900">{game.name}</div>
                  {game.description && (
                    <div className="text-xs text-gray-500 mt-1">{game.description}</div>
                  )}
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-gray-900">{game.type || '---'}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-gray-900">{game.branch_name}</div>
                  <div className="text-xs text-gray-500">{game.branch_code}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm font-bold text-green-600">
                    {parseFloat(game.price_per_15min || game.base_price || 0).toFixed(2)} ج.م
                  </div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-center">
                    <span className={`inline-flex items-center justify-center w-8 h-8 rounded-full ${
                      game.quantity > 0 ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                    }`}>
                      {game.quantity || 0}
                    </span>
                  </div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="flex flex-col gap-1">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                      game.is_active ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                    }`}>
                      {game.is_active ? 'نشط' : 'غير نشط'}
                    </span>
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                      game.is_available ? 'bg-blue-100 text-blue-800' : 'bg-yellow-100 text-yellow-800'
                    }`}>
                      {game.is_available ? 'متاح' : 'غير متاح'}
                    </span>
                  </div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                  <button
                    onClick={() => handleDelete(game.id, game.name)}
                    className="text-red-600 hover:text-red-900"
                  >
                    حذف
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal إضافة لعبة */}
      {showModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-full max-w-lg shadow-lg rounded-md bg-white">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-medium text-gray-900">إضافة لعبة جديدة</h3>
              <button
                onClick={() => {
                  setShowModal(false);
                  resetForm();
                }}
                className="text-gray-400 hover:text-gray-500"
              >
                ✕
              </button>
            </div>

            <form onSubmit={handleSubmit}>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    كود اللعبة *
                    <span className="text-xs text-gray-500 mr-2">(مثل: DRIFT01, SCOOTER01)</span>
                  </label>
                  <input
                    type="text"
                    value={formData.code}
                    onChange={(e) => setFormData({...formData, code: e.target.value.toUpperCase()})}
                    placeholder="أدخل كود اللعبة"
                    className={`mt-1 block w-full rounded-md border ${
                      errors.code ? 'border-red-500' : 'border-gray-300'
                    } px-3 py-2`}
                  />
                  {errors.code && <p className="mt-1 text-sm text-red-600">{errors.code}</p>}
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">نوع اللعبة *</label>
                  <select
                    value={formData.game_type}
                    onChange={handleGameTypeChange}
                    className={`mt-1 block w-full rounded-md border ${
                      errors.game_type ? 'border-red-500' : 'border-gray-300'
                    } px-3 py-2`}
                  >
                    <option value="">اختر نوع اللعبة</option>
                    {gameTypes.map((type, index) => (
                      <option key={index} value={type}>{type}</option>
                    ))}
                    <option value="custom">+ إضافة لعبة غير موجودة</option>
                  </select>
                  {errors.game_type && !showCustomGame && (
                    <p className="mt-1 text-sm text-red-600">{errors.game_type}</p>
                  )}
                </div>

                {showCustomGame && (
                  <div>
                    <label className="block text-sm font-medium text-gray-700">اسم اللعبة الجديدة *</label>
                    <input
                      type="text"
                      value={formData.custom_name}
                      onChange={(e) => setFormData({...formData, custom_name: e.target.value})}
                      placeholder="أدخل اسم اللعبة الجديدة"
                      className={`mt-1 block w-full rounded-md border ${
                        errors.custom_name ? 'border-red-500' : 'border-gray-300'
                      } px-3 py-2`}
                    />
                    {errors.custom_name && (
                      <p className="mt-1 text-sm text-red-600">{errors.custom_name}</p>
                    )}
                  </div>
                )}

                <div>
                  <label className="block text-sm font-medium text-gray-700">الوصف (اختياري)</label>
                  <textarea
                    value={formData.description}
                    onChange={(e) => setFormData({...formData, description: e.target.value})}
                    placeholder="وصف مختصر للعبة"
                    rows="2"
                    className="mt-1 block w-full rounded-md border border-gray-300 px-3 py-2"
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700">السعر لكل ¼ ساعة *</label>
                    <input
                      type="number"
                      min="1"
                      step="0.5"
                      value={formData.price_per_15min}
                      onChange={(e) => setFormData({...formData, price_per_15min: e.target.value})}
                      placeholder="مثل: 20"
                      className={`mt-1 block w-full rounded-md border ${
                        errors.price_per_15min ? 'border-red-500' : 'border-gray-300'
                      } px-3 py-2`}
                    />
                    {errors.price_per_15min && (
                      <p className="mt-1 text-sm text-red-600">{errors.price_per_15min}</p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700">الكمية *</label>
                    <input
                      type="number"
                      min="1"
                      value={formData.quantity}
                      onChange={(e) => setFormData({...formData, quantity: e.target.value})}
                      className={`mt-1 block w-full rounded-md border ${
                        errors.quantity ? 'border-red-500' : 'border-gray-300'
                      } px-3 py-2`}
                    />
                    {errors.quantity && <p className="mt-1 text-sm text-red-600">{errors.quantity}</p>}
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">الفرع *</label>
                  <select
                    value={formData.branch_id}
                    onChange={(e) => setFormData({...formData, branch_id: e.target.value})}
                    className={`mt-1 block w-full rounded-md border ${
                      errors.branch_id ? 'border-red-500' : 'border-gray-300'
                    } px-3 py-2`}
                    disabled={user?.role === 'BRANCH_MANAGER'}
                  >
                    <option value="">اختر الفرع</option>
                    {branches.map((branch) => (
                      <option key={branch.id} value={branch.id}>
                        {branch.name} ({branch.code})
                      </option>
                    ))}
                  </select>
                  {errors.branch_id && <p className="mt-1 text-sm text-red-600">{errors.branch_id}</p>}
                  {user?.role === 'BRANCH_MANAGER' && (
                    <p className="mt-1 text-xs text-gray-500">سيتم إضافة اللعبة لفرعك تلقائياً</p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">الحالة</label>
                  <select
                    value={formData.status}
                    onChange={(e) => setFormData({...formData, status: e.target.value})}
                    className="mt-1 block w-full rounded-md border border-gray-300 px-3 py-2"
                  >
                    <option value="ACTIVE">نشط</option>
                    <option value="INACTIVE">غير نشط</option>
                  </select>
                </div>
              </div>

              <div className="mt-6 flex justify-end gap-3">
                <button
                  type="button"
                  onClick={() => {
                    setShowModal(false);
                    resetForm();
                  }}
                  className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
                >
                  إلغاء
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-purple-600 text-white rounded-md hover:bg-purple-700"
                >
                  إضافة اللعبة
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default GamesManagement;
