// frontend/src/components/Layout.jsx
import React from 'react';
import { Outlet, useNavigate, Link } from 'react-router-dom';
import authService from '../services/authService';
import './Layout.css';

const Layout = () => {
  const navigate = useNavigate();
  const user = authService.getUser();

  const handleLogout = () => {
    authService.logout();
    navigate('/login');
  };

  const getUserRoleText = (role) => {
    switch(role) {
      case 'admin': return 'المدير العام';
      case 'branch_manager': return 'مدير فرع';
      case 'employee': return 'موظف';
      default: return 'مستخدم';
    }
  };

  const getNavItems = () => {
    if (user?.role === 'employee') {
      return [
        { path: '/employee/dashboard', label: 'الرئيسية' },
        { path: '/employee/rentals', label: 'التأجيرات' },
        { path: '/employee/games', label: 'الألعاب' },
        { path: '/rental', label: 'تأجير جديد' }
      ];
    } else {
      return [
        { path: '/admin/dashboard', label: 'الرئيسية' },
        { path: '/admin/users', label: 'المستخدمين' },
        { path: '/admin/branches', label: 'الفروع' },
        { path: '/admin/games', label: 'الألعاب' },
        { path: '/analytics', label: 'التحليلات' },
        { path: '/reports', label: 'التقارير' },
        { path: '/admin/activities', label: 'سجل الأنشطة' },
        { path: '/admin/settings', label: 'الإعدادات' }
      ];
    }
  };

  return (
    <div className="layout-container">
      {/* Header */}
      <header className="layout-header">
        <div className="header-left">
          <img 
            src="/images/logol3bty.jpg" 
            alt="L3BTY Logo" 
            className="header-logo"
          />
          <div className="brand-info">
            <h2>نظام تأجير L3BTY</h2>
            <p className="user-role">
              {getUserRoleText(user?.role)} - {user?.branch_name || 'الفرع الرئيسي'}
            </p>
          </div>
        </div>
        
        <div className="header-right">
          <div className="user-info">
            <span className="user-name">{user?.name || 'مستخدم'}</span>
            <span className="user-email">{user?.email}</span>
          </div>
          <button onClick={handleLogout} className="logout-btn">
            تسجيل الخروج
          </button>
        </div>
      </header>

      <div className="layout-body">
        {/* Sidebar */}
        <aside className="layout-sidebar">
          <nav className="sidebar-nav">
            <ul>
              {getNavItems().map((item, index) => (
                <li key={index}>
                  <Link 
                    to={item.path} 
                    className={window.location.pathname === item.path ? 'active' : ''}
                  >
                    {item.label}
                  </Link>
                </li>
              ))}
            </ul>
          </nav>
          
          <div className="sidebar-footer">
            <p className="system-info">
              نظام تأجير الألعاب الكهربائية
              <br />
              الإصدار 1.0.0
            </p>
          </div>
        </aside>

        {/* Main Content */}
        <main className="layout-main">
          <div className="main-container">
            <Outlet />
          </div>
        </main>
      </div>
    </div>
  );
};

export default Layout;
