      const now = new Date();
      const timeDiffMinutes = (now - startTime) / 60000;
      
      if (timeDiffMinutes > 3) {
        showNotification('error', 'ŸÑÿß ŸäŸÖŸÉŸÜ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ÿπÿØ ŸÖÿ±Ÿàÿ± 3 ÿØŸÇÿßÿ¶ŸÇ ŸÖŸÜ ÿßŸÑÿ®ÿØÿ°');
