    const pricePer15Min = game?.price_per_15min || 100;
    
    const units = Math.ceil(durationMinutes / 15);
    const calculatedAmount = pricePer15Min * units * (selectedOpenTimeRental.quantity || 1);
    
    setEndRental(selectedOpenTimeRental);
    setEndAmount(calculatedAmount.toString());
    setRemainingAmount(calculatedAmount - (selectedOpenTimeRental.paid_amount || 0) - (selectedOpenTimeRental.deposit || 0));
    
    setEndRental(prev => ({
      ...prev,
      end_details: {
