    const gameQuantity = quantities[game.id] || 1;
    const gameDuration = gameDurations[game.id] || selectedDuration;
    const units = Math.ceil(gameDuration / 15);
    const pricePer15Min = game.price_per_15min || 100;
    
    return pricePer15Min * units * gameQuantity;
  };

