                  placeholder="ÿ£ÿØÿÆŸÑ ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÖÿ±ÿ™ÿ¨ÿπ"
                  required
                  className="form-input"
                  min="0"
                  max={cancelRental.paid_amount || 10000}
                  step="10"
                />
              </div>
              
              <div className="modal-actions">
                <button 
                  onClick={handleCancelRental}
                  className="btn-confirm-cancel"
                >
                  ‚úÖ ÿ™ÿ£ŸÉŸäÿØ ÿßŸÑÿ•ŸÑÿ∫ÿßÿ°
                </button>
                <button 
                  onClick={() => {
                    setShowCancelModal(false);
                    setCancelRental(null);
                    setCancelAmount('');
                  }} 
                  className="btn-cancel"
                >
                  ÿ•ŸÑÿ∫ÿßÿ°
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* ‚úÖ ŸÜÿßŸÅÿ∞ÿ© ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± */}
      {showEndModal && endRental && (
        <div className="modal-overlay">
          <div className="modal end-modal">
            <div className="modal-header">
              <h2>‚è≥ ÿ•ŸÜŸáÿßÿ° ÿ™ÿ£ÿ¨Ÿäÿ± #{endRental.rental_number}</h2>
              <button 
                onClick={() => {
                  setShowEndModal(false);
                  setEndRental(null);
                  setEndAmount('');
                  setRemainingAmount(0);
                }} 
                className="close-btn"
              >
                ‚úï
              </button>
            </div>
            
            <div className="modal-body">
              <div className="end-info">
                <div className="info-grid">
                  <div className="info-item">
                    <span className="info-label">ÿßŸÑÿπŸÖŸäŸÑ:</span>
                    <span className="info-value">{endRental.customer_name}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">ÿßŸÑŸÑÿπÿ®ÿ©:</span>
                    <span className="info-value">{endRental.game_name}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">ŸàŸÇÿ™ ÿßŸÑÿ®ÿØÿ°:</span>
                    <span className="info-value">{formatTime(endRental.start_time)}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÖÿØŸÅŸàÿπ:</span>
                    <span className="info-value">{formatCurrency(endRental.paid_amount || 0)}</span>
                  </div>
                </div>
                
                {endRental.end_details && (
                  <div className="time-calculations">
                    <h4>üßÆ ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑÿ≠ÿ≥ÿßÿ®</h4>
                    <div className="calculation-details">
                      <div className="calc-row">
                        <span>ÿßŸÑŸÖÿØÿ© ÿßŸÑŸÅÿπŸÑŸäÿ©:</span>
                        <span>{endRental.end_details.duration_minutes} ÿØŸÇŸäŸÇÿ©</span>
                      </div>
                      <div className="calc-row">
                        <span>ÿπÿØÿØ Ÿàÿ≠ÿØÿßÿ™ ÿßŸÑŸÄ 15 ÿØŸÇŸäŸÇÿ©:</span>
                        <span>{endRental.end_details.units} Ÿàÿ≠ÿØÿ©</span>
                      </div>
                      <div className="calc-row total">
                        <span>ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸä:</span>
                        <span className="total-amount">
                          {formatCurrency(endRental.end_details.calculated_amount)}
                        </span>
                      </div>
                    </div>
                  </div>
                )}
              </div>
              
              <div className="form-group">
                <label>ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÜŸáÿßÿ¶Ÿä *</label>
                <div className="amount-input-group">
                  <input
                    type="number"
                    value={endAmount}
                    onChange={(e) => {
                      const value = e.target.value;
                      setEndAmount(value);
                      const total = parseFloat(value) || 0;
                      const paid = parseFloat(endRental.paid_amount) || 0;
                      const deposit = parseFloat(endRental.deposit) || 0;
                      setRemainingAmount(Math.max(0, total - paid - deposit));
                    }}
                    placeholder="ÿ£ÿØÿÆŸÑ ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÜŸáÿßÿ¶Ÿä"
                    required
                    className="form-input"
                    min="0"
                    step="10"
                  />
                </div>
                <div className="input-hint">
                  ‚ìò ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÖÿ≠ÿ≥Ÿàÿ® ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã: {formatCurrency(endAmount || 0)}
                </div>
              </div>
              
              <div className="modal-actions">
                <button 
                  onClick={handleCompleteRental}
                  className="btn-confirm-end"
                >
                  üí≥ ÿ•ŸÜŸáÿßÿ° Ÿàÿ™ÿ≠ÿµŸäŸÑ {formatCurrency(endAmount || 0)}
                </button>
                <button 
                  onClick={() => {
                    setShowEndModal(false);
                    setEndRental(null);
                    setEndAmount('');
                    setRemainingAmount(0);
                  }} 
                  className="btn-cancel"
                >
                  ÿ•ŸÑÿ∫ÿßÿ°
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Rentals;
