import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { 
  Search, Filter, RefreshCw, Eye, Edit, Trash2,
  CheckCircle, XCircle, Building, User, Calendar,
  DollarSign, Save, ChevronLeft, ChevronRight,
  AlertCircle, X, Clock, Smartphone, Plus,
  Download, BarChart3, MoreVertical, Shield,
  Play, Pause, StopCircle, Timer, Award,
  TrendingUp, Users, Gamepad2, Phone, Mail,
  MapPin, Printer, CreditCard, Wallet,
  History, Target, Activity, Zap, Coffee,
  ChevronDown, ChevronUp, Layers, PieChart,
  FileText, FilterX, ListFilter, Settings
} from 'lucide-react';
import api from '../../services/api';
import authService from '../../services/authService';
import './RentalsManagement.css';

const RentalsManagement = () => {
  // ==================== ÿßŸÑÿ≠ÿßŸÑÿßÿ™ ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ© ====================
  const [rentals, setRentals] = useState([]);
  const [filteredRentals, setFilteredRentals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸàÿßŸÑÿ¥ŸäŸÅÿ™ÿßÿ™
  const [currentUser, setCurrentUser] = useState(null);
  const [currentShift, setCurrentShift] = useState(null);
  const [allShifts, setAllShifts] = useState([]);
  const [selectedShift, setSelectedShift] = useState(null);
  const [userBranch, setUserBranch] = useState(null);
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÜÿ∏ÿßŸÖ
  const [branches, setBranches] = useState([]);
  const [games, setGames] = useState([]);
  const [employees, setEmployees] = useState([]);
  
  // ÿ≠ÿßŸÑÿßÿ™ ÿßŸÑŸÜŸàÿßŸÅÿ∞ ÿßŸÑŸÖŸÜÿ®ÿ´ŸÇÿ©
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
const [showStatsModal, setShowStatsModal] = useState(false);
  const [showRentalDetailsModal, setShowRentalDetailsModal] = useState(false);
  const [showShiftSelectorModal, setShowShiftSelectorModal] = useState(false);
  const [showShiftStatsModal, setShowShiftStatsModal] = useState(false);
  
  // ÿßŸÑÿπŸÜÿßÿµÿ± ÿßŸÑŸÖÿ≠ÿØÿØÿ©
  const [selectedRental, setSelectedRental] = useState(null);
  const [editedRental, setEditedRental] = useState(null);
  const [rentalToDelete, setRentalToDelete] = useState(null);
  
  // ÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑŸÅŸÑÿßÿ™ÿ±
  const [filters, setFilters] = useState({
    status: 'all',
    branch_id: 'all',
    payment_status: 'all',
    dateFrom: '',
    dateTo: '',
    shift_id: null,
    employee_id: null,
    game_id: null,
    rental_type: 'all' // 'all', 'fixed', 'open'
  });
  
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(20);
  
  // ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿ¥ÿßŸÖŸÑÿ©
  const [globalStats, setGlobalStats] = useState({
    totalRentals: 0,
    activeRentals: 0,
    completedRentals: 0,
    cancelledRentals: 0,
    totalRevenue: 0,
    averageRental: 0,
    todayRevenue: 0,
    todayRentals: 0,
    thisWeekRevenue: 0,
    thisMonthRevenue: 0,
    openTimeRentals: 0,
    fixedTimeRentals: 0,
    shifts: [] // ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ŸÑŸÉŸÑ ÿ¥ŸäŸÅÿ™
  });
  
  // ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÖÿ≠ÿØÿØ
  const [shiftStats, setShiftStats] = useState({
    shiftId: null,
    shiftNumber: null,
    employeeName: null,
    branchName: null,
    startTime: null,
    endTime: null,
    duration: '0:00',
    totalRentals: 0,
    activeRentals: 0,
    completedRentals: 0,
    cancelledRentals: 0,
    totalRevenue: 0,
    fixedRevenue: 0,
    openRevenue: 0,
    rentals: []
  });

  // ==================== ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ© ====================
  const loadInitialData = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      
      console.log('üöÄ ÿ®ÿØÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...');
      
      // 1. ÿ™ÿ≠ŸÖÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
      const user = api.getCurrentUser();
      setCurrentUser(user);
      console.log('üë§ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:', user?.name);
      
      // 2. ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ
      try {
        const branchesRes = await api.getBranches();
        if (branchesRes?.success) {
          setBranches(branchesRes.data || []);
          console.log('üè¨ ÿßŸÑŸÅÿ±Ÿàÿπ:', branchesRes.data?.length || 0);
        }
      } catch (branchError) {
        console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ:', branchError.message);
      }
      
      // 3. ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ŸÅÿ±ÿπ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
      if (user?.branch_id) {
        const branchRes = await api.getBranch(user.branch_id);
        if (branchRes?.success) {
          setUserBranch(branchRes.data);
          console.log('üìç ÿßŸÑŸÅÿ±ÿπ:', branchRes.data.name);
        }
      }
      
      // 4. ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™ÿßÿ™
      await loadAllShifts();
      
      // 5. ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
      await loadAllRentals();
      
      console.log('‚úÖ ÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ®ŸÜÿ¨ÿßÿ≠');
      
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', error);
      setError(error.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™');
    } finally {
      setLoading(false);
    }
  }, []);

 // ÿ™ÿ≠ŸÖŸäŸÑ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ¥ŸäŸÅÿ™ÿßÿ™ (ŸÖÿπ ÿßÿ≥ÿ™ÿ´ŸÜÿßÿ° ÿ¥ŸäŸÅÿ™ ÿßŸÑŸÖÿØŸäÿ±)
const loadAllShifts = useCallback(async () => {
  try {
    console.log('üîÑ ÿ¨ŸÑÿ® ÿ¨ŸÖŸäÿπ ÿßŸÑÿ¥ŸäŸÅÿ™ÿßÿ™...');
    
    // ‚úÖ ŸÑÿß ŸÜÿ≠ÿßŸàŸÑ ÿ¨ŸÑÿ® ÿ¥ŸäŸÅÿ™ ÿßŸÑŸÖÿØŸäÿ± ŸÑÿ£ŸÜŸá ŸÑÿß ŸäŸÖŸÑŸÉ ÿ¥ŸäŸÅÿ™
    if (currentUser?.role === 'admin' || currentUser?.role === 'branch_manager') {
      // ŸÑÿß ŸÜÿ¨ŸÑÿ® ÿ¥ŸäŸÅÿ™ ÿ≠ÿßŸÑŸä ŸÑŸÑŸÖÿØŸäÿ±
      setCurrentShift(null);
      setSelectedShift(null);
      
      // ŸÜÿ¨ŸÑÿ® ŸÅŸÇÿ∑ ÿßŸÑÿ¥ŸäŸÅÿ™ÿßÿ™ ÿßŸÑÿÆÿßÿµÿ© ÿ®ÿßŸÑŸÖŸàÿ∏ŸÅŸäŸÜ
      try {
        const allShiftsRes = await api.getShifts({ 
          branch_id: currentUser?.branch_id,
          limit: 100,
          exclude_admin: true // ÿ®ÿßÿ±ÿßŸÖÿ™ÿ± ÿ¨ÿØŸäÿØ ŸÑÿßÿ≥ÿ™ÿ´ŸÜÿßÿ° ÿ¥ŸäŸÅÿ™ÿßÿ™ ÿßŸÑŸÖÿØŸäÿ±
        });
        
        if (allShiftsRes?.success && allShiftsRes.data) {
          // ‚úÖ ÿ™ÿµŸÅŸäÿ© ÿ•ÿ∂ÿßŸÅŸäÿ©: ÿßÿ≥ÿ™ÿ®ÿπÿßÿØ ÿ£Ÿä ÿ¥ŸäŸÅÿ™ ŸÑŸÑŸÖÿØŸäÿ±
          const employeeShifts = allShiftsRes.data.filter(shift => 
            shift.employee_role !== 'admin' && 
            shift.employee_role !== 'branch_manager'
          );
          
          setAllShifts(employeeShifts);
          console.log('üìã ÿ¥ŸäŸÅÿ™ÿßÿ™ ÿßŸÑŸÖŸàÿ∏ŸÅŸäŸÜ:', employeeShifts.length);
        }
      } catch (error) {
        console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑ ÿ™ÿ≠ŸÖŸäŸÑ ÿ¥ŸäŸÅÿ™ÿßÿ™ ÿßŸÑŸÖŸàÿ∏ŸÅŸäŸÜ:', error.message);
        // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿ®ŸäÿßŸÜÿßÿ™ ÿ™ÿ¨ÿ±Ÿäÿ®Ÿäÿ© ŸÑŸÑÿ¥ŸäŸÅÿ™ÿßÿ™
        setAllShifts(generateMockShifts());
      }
    } else {
      // ŸÑŸÑŸÖŸàÿ∏ŸÅ ÿßŸÑÿπÿßÿØŸä: ŸÜÿ¨ŸÑÿ® ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä
      const shiftRes = await api.getCurrentShift();
      if (shiftRes?.success && shiftRes.data) {
        setCurrentShift(shiftRes.data);
        setSelectedShift(shiftRes.data);
        console.log('‚úÖ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä ŸÑŸÑŸÖŸàÿ∏ŸÅ:', shiftRes.data.id);
      }
    }
  } catch (error) {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™ÿßÿ™:', error);
  }
}, [currentUser]);

// ÿ™ÿ≠ŸÖŸäŸÑ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ (ŸÖÿπ ÿßÿ≥ÿ™ÿ´ŸÜÿßÿ° ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖÿØŸäÿ±)
const loadAllRentals = useCallback(async () => {
  try {
    console.log('üìã ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™...');
    
    const params = {
      limit: 1000,
      include_game: true,
      include_branch: true,
      include_employee: true,
      include_shift: true
    };
    
    // ÿ•ÿ∂ÿßŸÅÿ© ŸÅŸÑÿ™ÿ± ÿßŸÑŸÅÿ±ÿπ
    if (currentUser?.branch_id) {
      params.branch_id = currentUser.branch_id;
    }
    
    // ‚úÖ ÿ•ÿ∂ÿßŸÅÿ© ŸÅŸÑÿ™ÿ± ŸÑÿßÿ≥ÿ™ÿ´ŸÜÿßÿ° ÿ¥ŸäŸÅÿ™ ÿßŸÑŸÖÿØŸäÿ±
    if (currentUser?.role === 'admin' || currentUser?.role === 'branch_manager') {
      params.exclude_admin_shifts = true; // ÿßÿ≥ÿ™ÿ´ŸÜÿßÿ° ÿ¥ŸäŸÅÿ™ÿßÿ™ ÿßŸÑŸÖÿØŸäÿ±
    }
    
    const response = await api.getRentals(params);
    
    if (response?.success && response.data) {
      // ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      let processedRentals = response.data.map(rental => ({
        ...rental,
        rental_type: rental.rental_type || (rental.is_open_time === 1 ? 'open' : 'fixed'),
        time_type_display: (rental.rental_type === 'open' || rental.is_open_time === 1) ? 'üîÑ ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠' : '‚è∞ ŸàŸÇÿ™ ÿ´ÿßÿ®ÿ™',
        game_name: rental.game_name || rental.game?.name || 'ŸÑÿπÿ®ÿ© ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅÿ©',
        customer_phone: rental.customer_phone || '00000000000',
        price_per_15min: rental.price_per_15min || rental.game?.price_per_15min || 100,
        branch_name: rental.branch_name || rental.branch?.name || `ŸÅÿ±ÿπ ${rental.branch_id}`,
        employee_name: rental.employee_name || rental.employee?.name || 'ŸÖŸàÿ∏ŸÅ',
        shift_number: rental.shift_number || `ÿ¥ŸäŸÅÿ™ #${rental.shift_id || '?'}`
      }));
      
      // ‚úÖ ÿ™ÿµŸÅŸäÿ© ÿ•ÿ∂ÿßŸÅŸäÿ©: ÿßÿ≥ÿ™ÿ®ÿπÿßÿØ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖÿØŸäÿ± (ÿ•ÿ∞ÿß ŸÉÿßŸÜ ŸÑŸÑŸÖÿØŸäÿ± shift_id)
      if (currentUser?.role === 'admin' || currentUser?.role === 'branch_manager') {
        // ÿßÿ≥ÿ™ÿ®ÿπÿßÿØ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑÿ™Ÿä ŸÑŸäÿ≥ ŸÑŸáÿß shift_id (ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖÿØŸäÿ±)
        processedRentals = processedRentals.filter(rental => rental.shift_id);
        
        // ÿßÿ≥ÿ™ÿ®ÿπÿßÿØ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑÿ™Ÿä ŸÑŸäÿ≥ ŸÑŸáÿß employee_name (ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖÿØŸäÿ±)
        processedRentals = processedRentals.filter(rental => 
          rental.employee_name && rental.employee_name !== 'ŸÖÿØŸäÿ±' && rental.employee_name !== 'admin'
        );
      }
      
      setRentals(processedRentals);
      console.log(`‚úÖ ÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ${processedRentals.length} ÿ™ÿ£ÿ¨Ÿäÿ± ŸÑŸÑŸÖŸàÿ∏ŸÅŸäŸÜ ŸÅŸÇÿ∑`);
      
      // ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ¥ÿßŸÖŸÑÿ© (ÿ®ÿØŸàŸÜ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖÿØŸäÿ±)
      calculateGlobalStats(processedRentals);
    } else {
      console.warn('‚ö†Ô∏è ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™');
      setRentals([]);
    }
  } catch (error) {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™:', error);
    setRentals([]);
  }
}, [currentUser]);

  // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÑÿ¥ŸäŸÅÿ™ ŸÖÿ≠ÿØÿØ
  const loadRentalsForShift = useCallback(async (shiftId) => {
    try {
      setLoading(true);
      console.log(`üìã ÿ¨ŸÑÿ® ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™ ${shiftId}...`);
      
      const params = {
        shift_id: shiftId,
        limit: 500,
        include_game: true,
        include_employee: true
      };
      
      if (currentUser?.branch_id) {
        params.branch_id = currentUser.branch_id;
      }
      
      const response = await api.getRentals(params);
      
      if (response?.success && response.data) {
        const processedRentals = response.data.map(rental => ({
          ...rental,
          rental_type: rental.rental_type || (rental.is_open_time === 1 ? 'open' : 'fixed'),
          time_type_display: (rental.rental_type === 'open' || rental.is_open_time === 1) ? 'üîÑ ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠' : '‚è∞ ŸàŸÇÿ™ ÿ´ÿßÿ®ÿ™',
          game_name: rental.game_name || rental.game?.name || 'ŸÑÿπÿ®ÿ© ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅÿ©',
          customer_phone: rental.customer_phone || '00000000000'
        }));
        
        // ÿ≠ÿ≥ÿßÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™
        calculateShiftStats(processedRentals, shiftId);
      }
      
    } catch (error) {
      console.error(`‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™ ${shiftId}:`, error);
    } finally {
      setLoading(false);
    }
  }, [currentUser]);

  // ==================== ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ====================
  const calculateGlobalStats = (rentalsData) => {
    const active = rentalsData.filter(r => r.status === 'ŸÜÿ¥ÿ∑').length;
    const completed = rentalsData.filter(r => r.status === 'ŸÖŸÉÿ™ŸÖŸÑ').length;
    const cancelled = rentalsData.filter(r => r.status === 'ŸÖŸÑÿ∫Ÿä').length;
    
    const totalRevenue = rentalsData
      .filter(r => r.payment_status === 'ŸÖÿØŸÅŸàÿπ')
      .reduce((sum, r) => sum + (parseFloat(r.final_amount) || parseFloat(r.total_amount) || 0), 0);
    
    const today = new Date().toISOString().split('T')[0];
    const todayRentals = rentalsData.filter(r => 
      r.start_time && r.start_time.split('T')[0] === today
    );
    
    const todayRevenue = todayRentals
      .filter(r => r.payment_status === 'ŸÖÿØŸÅŸàÿπ')
      .reduce((sum, r) => sum + (parseFloat(r.final_amount) || parseFloat(r.total_amount) || 0), 0);
    
    // ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ Ÿáÿ∞ÿß ÿßŸÑÿ£ÿ≥ÿ®Ÿàÿπ
    const oneWeekAgo = new Date();
    oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);
    const weekRentals = rentalsData.filter(r => 
      r.start_time && new Date(r.start_time) >= oneWeekAgo
    );
    
    const weekRevenue = weekRentals
      .filter(r => r.payment_status === 'ŸÖÿØŸÅŸàÿπ')
      .reduce((sum, r) => sum + (parseFloat(r.final_amount) || parseFloat(r.total_amount) || 0), 0);
    
    // ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ Ÿáÿ∞ÿß ÿßŸÑÿ¥Ÿáÿ±
    const oneMonthAgo = new Date();
    oneMonthAgo.setDate(oneMonthAgo.getDate() - 30);
    const monthRentals = rentalsData.filter(r => 
      r.start_time && new Date(r.start_time) >= oneMonthAgo
    );
    
    const monthRevenue = monthRentals
      .filter(r => r.payment_status === 'ŸÖÿØŸÅŸàÿπ')
      .reduce((sum, r) => sum + (parseFloat(r.final_amount) || parseFloat(r.total_amount) || 0), 0);
    
    // ÿ£ŸÜŸàÿßÿπ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
    const openTimeRentals = rentalsData.filter(r => r.rental_type === 'open' || r.is_open_time === 1).length;
    const fixedTimeRentals = rentalsData.filter(r => r.rental_type === 'fixed' || r.is_open_time === 0).length;
    
    // ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ŸÑŸÉŸÑ ÿ¥ŸäŸÅÿ™
    const shiftStatsMap = {};
    rentalsData.forEach(rental => {
      if (rental.shift_id) {
        if (!shiftStatsMap[rental.shift_id]) {
          shiftStatsMap[rental.shift_id] = {
            shiftId: rental.shift_id,
            shiftNumber: rental.shift_number || `ÿ¥ŸäŸÅÿ™ #${rental.shift_id}`,
            employeeName: rental.employee_name || 'ŸÖŸàÿ∏ŸÅ',
            branchName: rental.branch_name || `ŸÅÿ±ÿπ ${rental.branch_id}`,
            startTime: rental.shift_start_time,
            totalRentals: 0,
            completedRentals: 0,
            revenue: 0
          };
        }
        
        shiftStatsMap[rental.shift_id].totalRentals++;
        
        if (rental.status === 'ŸÖŸÉÿ™ŸÖŸÑ') {
          shiftStatsMap[rental.shift_id].completedRentals++;
          const amount = parseFloat(rental.final_amount) || parseFloat(rental.total_amount) || 0;
          shiftStatsMap[rental.shift_id].revenue += amount;
        }
      }
    });
    
    const shiftsArray = Object.values(shiftStatsMap).sort((a, b) => 
      new Date(b.startTime || 0) - new Date(a.startTime || 0)
    );
    
    setGlobalStats({
      totalRentals: rentalsData.length,
      activeRentals: active,
      completedRentals: completed,
      cancelledRentals: cancelled,
      totalRevenue,
      averageRental: completed > 0 ? totalRevenue / completed : 0,
      todayRevenue,
      todayRentals: todayRentals.length,
      thisWeekRevenue: weekRevenue,
      thisMonthRevenue: monthRevenue,
      openTimeRentals,
      fixedTimeRentals,
      shifts: shiftsArray
    });
  };

  const calculateShiftStats = (rentalsData, shiftId) => {
    const shiftRentals = rentalsData.filter(r => r.shift_id == shiftId);
    
    const active = shiftRentals.filter(r => r.status === 'ŸÜÿ¥ÿ∑').length;
    const completed = shiftRentals.filter(r => r.status === 'ŸÖŸÉÿ™ŸÖŸÑ').length;
    const cancelled = shiftRentals.filter(r => r.status === 'ŸÖŸÑÿ∫Ÿä').length;
    
    const totalRevenue = shiftRentals
      .filter(r => r.payment_status === 'ŸÖÿØŸÅŸàÿπ')
      .reduce((sum, r) => sum + (parseFloat(r.final_amount) || parseFloat(r.total_amount) || 0), 0);
    
    const fixedRevenue = shiftRentals
      .filter(r => (r.rental_type === 'fixed' || r.is_open_time === 0) && r.payment_status === 'ŸÖÿØŸÅŸàÿπ')
      .reduce((sum, r) => sum + (parseFloat(r.final_amount) || parseFloat(r.total_amount) || 0), 0);
    
    const openRevenue = shiftRentals
      .filter(r => (r.rental_type === 'open' || r.is_open_time === 1) && r.payment_status === 'ŸÖÿØŸÅŸàÿπ')
      .reduce((sum, r) => sum + (parseFloat(r.final_amount) || parseFloat(r.total_amount) || 0), 0);
    
    // ÿ≠ÿ≥ÿßÿ® ŸÖÿØÿ© ÿßŸÑÿ¥ŸäŸÅÿ™
    let duration = '0:00';
    if (shiftRentals.length > 0) {
      const firstRental = shiftRentals.reduce((earliest, r) => 
        new Date(r.start_time) < new Date(earliest.start_time) ? r : earliest
      , shiftRentals[0]);
      
      const lastRental = shiftRentals.reduce((latest, r) => 
        new Date(r.end_time || r.start_time) > new Date(latest.end_time || latest.start_time) ? r : latest
      , shiftRentals[0]);
      
      if (firstRental?.start_time && lastRental?.end_time) {
        const start = new Date(firstRental.start_time);
        const end = new Date(lastRental.end_time);
        const diffMs = end - start;
        const hours = Math.floor(diffMs / 3600000);
        const minutes = Math.floor((diffMs % 3600000) / 60000);
        duration = `${hours}:${minutes.toString().padStart(2, '0')}`;
      }
    }
    
    // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™
    const shift = allShifts.find(s => s.id == shiftId) || currentShift;
    
    setShiftStats({
      shiftId,
      shiftNumber: shift?.shift_number || `ÿ¥ŸäŸÅÿ™ #${shiftId}`,
      employeeName: shift?.employee_name || shiftRentals[0]?.employee_name || 'ŸÖŸàÿ∏ŸÅ',
      branchName: shift?.branch_name || shiftRentals[0]?.branch_name || `ŸÅÿ±ÿπ ${shift?.branch_id || '?'}`,
      startTime: shift?.start_time || shiftRentals[0]?.start_time,
      endTime: shift?.end_time,
      duration,
      totalRentals: shiftRentals.length,
      activeRentals: active,
      completedRentals: completed,
      cancelledRentals: cancelled,
      totalRevenue,
      fixedRevenue,
      openRevenue,
      rentals: shiftRentals
    });
  };

  // ==================== ÿßŸÑŸÅŸÑÿ™ÿ±ÿ© ŸàÿßŸÑÿ®ÿ≠ÿ´ ====================
  useEffect(() => {
    if (rentals.length > 0) {
      applyFiltersAndSearch();
    }
  }, [rentals, filters, searchTerm]);

  const applyFiltersAndSearch = useCallback(() => {
    let result = [...rentals];
    
    // ŸÅŸÑÿ™ÿ±ÿ© ÿßŸÑÿ®ÿ≠ÿ´
    if (searchTerm.trim()) {
      const term = searchTerm.toLowerCase().trim();
      result = result.filter(rental => 
        rental.customer_name?.toLowerCase().includes(term) ||
        rental.customer_phone?.includes(term) ||
        rental.rental_number?.toLowerCase().includes(term) ||
        rental.game_name?.toLowerCase().includes(term) ||
        rental.employee_name?.toLowerCase().includes(term)
      );
    }
    
    // ŸÅŸÑÿ™ÿ±ÿ© ÿßŸÑÿ¥ŸäŸÅÿ™
    if (filters.shift_id) {
      result = result.filter(r => r.shift_id == filters.shift_id);
    }
    
    // ŸÅŸÑÿ™ÿ±ÿ© ÿßŸÑŸÖŸàÿ∏ŸÅ
    if (filters.employee_id) {
      result = result.filter(r => r.employee_id == filters.employee_id || r.user_id == filters.employee_id);
    }
    
    // ŸÅŸÑÿ™ÿ±ÿ© ÿßŸÑŸÑÿπÿ®ÿ©
    if (filters.game_id) {
      result = result.filter(r => r.game_id == filters.game_id);
    }
    
    // ŸÅŸÑÿ™ÿ±ÿ© ÿßŸÑÿ≠ÿßŸÑÿ©
    if (filters.status && filters.status !== 'all') {
      result = result.filter(r => r.status === filters.status);
    }
    
    // ŸÅŸÑÿ™ÿ±ÿ© ÿ≠ÿßŸÑÿ© ÿßŸÑÿØŸÅÿπ
    if (filters.payment_status && filters.payment_status !== 'all') {
      result = result.filter(r => r.payment_status === filters.payment_status);
    }
    
    // ŸÅŸÑÿ™ÿ±ÿ© ŸÜŸàÿπ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
    if (filters.rental_type && filters.rental_type !== 'all') {
      if (filters.rental_type === 'open') {
        result = result.filter(r => r.rental_type === 'open' || r.is_open_time === 1);
      } else if (filters.rental_type === 'fixed') {
        result = result.filter(r => r.rental_type === 'fixed' || r.is_open_time === 0);
      }
    }
    
    // ŸÅŸÑÿ™ÿ±ÿ© ÿßŸÑŸÅÿ±ÿπ
    if (filters.branch_id && filters.branch_id !== 'all') {
      result = result.filter(r => r.branch_id == filters.branch_id);
    }
    
    // ŸÅŸÑÿ™ÿ±ÿ© ÿßŸÑÿ™ÿßÿ±ŸäÿÆ
    if (filters.dateFrom) {
      result = result.filter(r => {
        if (!r.start_time) return false;
        return r.start_time.split('T')[0] >= filters.dateFrom;
      });
    }
    
    if (filters.dateTo) {
      result = result.filter(r => {
        if (!r.start_time) return false;
        return r.start_time.split('T')[0] <= filters.dateTo;
      });
    }
    
    setFilteredRentals(result);
    setCurrentPage(1);
  }, [rentals, filters, searchTerm]);

  const resetFilters = () => {
    setFilters({
      status: 'all',
      branch_id: 'all',
      payment_status: 'all',
      dateFrom: '',
      dateTo: '',
      shift_id: selectedShift?.id || null,
      employee_id: null,
      game_id: null,
      rental_type: 'all'
    });
    setSearchTerm('');
  };

  // ==================== ÿ™ÿ£ÿ´Ÿäÿ±ÿßÿ™ ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ ====================
  useEffect(() => {
    loadInitialData();
  }, []);

  // ÿ™ÿ≠ÿØŸäÿ´ ÿπŸÜÿØ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÖÿ≠ÿØÿØ
  useEffect(() => {
    if (selectedShift) {
      loadRentalsForShift(selectedShift.id);
      setFilters(prev => ({ ...prev, shift_id: selectedShift.id }));
    } else {
      setFilters(prev => ({ ...prev, shift_id: null }));
    }
  }, [selectedShift]);

  // ==================== ÿØŸàÿßŸÑ ŸÖÿ≥ÿßÿπÿØÿ© ====================
  const formatCurrency = (amount) => {
    const num = parseFloat(amount) || 0;
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0,
      maximumFractionDigits: 2
    }).format(num);
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return '-';
    try {
      const date = new Date(dateString);
      return date.toLocaleString('ar-EG', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '-';
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('ar-EG', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit'
      });
    } catch {
      return '-';
    }
  };

  const formatTime = (dateString) => {
    if (!dateString) return '-';
    try {
      const date = new Date(dateString);
      return date.toLocaleTimeString('ar-EG', {
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '-';
    }
  };

  const getStatusBadge = (status) => {
    const config = {
      'ŸÜÿ¥ÿ∑': { class: 'active', icon: 'üî•', label: 'ŸÜÿ¥ÿ∑' },
      'ŸÖŸÉÿ™ŸÖŸÑ': { class: 'completed', icon: '‚úÖ', label: 'ŸÖŸÉÿ™ŸÖŸÑ' },
      'ŸÖŸÑÿ∫Ÿä': { class: 'cancelled', icon: '‚ùå', label: 'ŸÖŸÑÿ∫Ÿä' }
    }[status] || { class: 'default', icon: '‚ùì', label: status };
    
    return (
      <span className={`status-badge ${config.class}`}>
        <span className="badge-icon">{config.icon}</span>
        {config.label}
      </span>
    );
  };

  const getPaymentStatusBadge = (status) => {
    const config = {
      'ŸÖÿØŸÅŸàÿπ': { class: 'paid', icon: 'üí∞', label: 'ŸÖÿØŸÅŸàÿπ' },
      'ÿ∫Ÿäÿ± ŸÖÿØŸÅŸàÿπ': { class: 'unpaid', icon: '‚ö†Ô∏è', label: 'ÿ∫Ÿäÿ± ŸÖÿØŸÅŸàÿπ' },
      'ÿ¨ÿ≤ÿ¶Ÿä': { class: 'partial', icon: 'üìä', label: 'ÿ¨ÿ≤ÿ¶Ÿä' },
      'ÿπŸÜÿØ ÿßŸÑÿ•ŸÜŸáÿßÿ°': { class: 'pending', icon: '‚è≥', label: 'ÿπŸÜÿØ ÿßŸÑÿ•ŸÜŸáÿßÿ°' },
      'ŸÖÿØŸÅŸàÿπ ŸÖÿ≥ÿ®ŸÇÿßŸã': { class: 'prepaid', icon: 'üíµ', label: 'ŸÖÿØŸÅŸàÿπ ŸÖÿ≥ÿ®ŸÇÿßŸã' }
    }[status] || { class: 'default', icon: '‚ùì', label: status };
    
    return (
      <span className={`payment-badge ${config.class}`}>
        <span className="badge-icon">{config.icon}</span>
        {config.label}
      </span>
    );
  };

  const getRentalTypeBadge = (rental) => {
    const isOpenTime = rental.is_open_time === 1 || rental.rental_type === 'open';
    
    return (
      <span className={`type-badge ${isOpenTime ? 'open' : 'fixed'}`}>
        {isOpenTime ? 'üîÑ ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠' : '‚è∞ ŸàŸÇÿ™ ÿ´ÿßÿ®ÿ™'}
      </span>
    );
  };

  const showNotification = (message, type = 'info') => {
    const notification = document.createElement('div');
    notification.className = `notification ${type}`;
    notification.innerHTML = `
      <div class="notification-content">
        <span>${message}</span>
        <button class="notification-close">‚úï</button>
      </div>
    `;
    
    document.body.appendChild(notification);
    
    notification.querySelector('.notification-close').onclick = () => {
      notification.remove();
    };
    
    setTimeout(() => {
      if (notification.parentNode) {
        notification.remove();
      }
    }, 5000);
  };

  const calculateTimeElapsed = (startTime) => {
    if (!startTime) return 'ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ';
    
    try {
      const start = new Date(startTime);
      const now = new Date();
      const diffMs = now - start;
      const diffMins = Math.floor(diffMs / 60000);
      
      if (diffMins < 60) {
        return `${diffMins} ÿØŸÇŸäŸÇÿ©`;
      } else {
        const hours = Math.floor(diffMins / 60);
        const mins = diffMins % 60;
        return `${hours}:${mins.toString().padStart(2, '0')}`;
      }
    } catch {
      return 'ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ';
    }
  };

  // ==================== ÿßŸÑÿ™ÿ±ŸÇŸäŸÖ ====================
  const totalPages = Math.ceil(filteredRentals.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const paginatedRentals = filteredRentals.slice(startIndex, endIndex);

  // ==================== Ÿàÿßÿ¨Ÿáÿ© ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ====================
  if (loading && rentals.length === 0) {
    return (
      <div className="loading-screen">
        <div className="spinner"></div>
        <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ŸÜÿ∏ÿßŸÖ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™...</p>
        {currentUser && <p className="loading-sub">ŸÖÿ±ÿ≠ÿ®ÿßŸã {currentUser.name}</p>}
      </div>
    );
  }

  return (
    <div className="rentals-management">
      {/* ========== ÿ±ÿ£ÿ≥ ÿßŸÑÿµŸÅÿ≠ÿ© ========== */}
      <div className="page-header modern-header">
        <div className="header-left">
          <div className="title-with-badges">
            <h1>
              <span className="header-icon">üéÆ</span>
              ŸÜÿ∏ÿßŸÖ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖÿ™ŸÇÿØŸÖ
            </h1>
            <div className="badge-container">
              <span className="badge badge-primary">
                <Layers size={14} />
                {globalStats.totalRentals} ÿ•ÿ¨ŸÖÿßŸÑŸä
              </span>
              <span className="badge badge-success">
                <CheckCircle size={14} />
                {globalStats.completedRentals} ŸÖŸÉÿ™ŸÖŸÑ
              </span>
              <span className="badge badge-warning">
                <Activity size={14} />
                {globalStats.activeRentals} ŸÜÿ¥ÿ∑
              </span>
            </div>
          </div>
          <p className="subtitle">
            {selectedShift 
              ? `ÿπÿ±ÿ∂ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™ #${selectedShift.id} - ${selectedShift.employee_name || 'ŸÖŸàÿ∏ŸÅ'}`
              : 'ŸÑŸàÿ≠ÿ© ÿ™ÿ≠ŸÉŸÖ ŸÖÿ™ŸÉÿßŸÖŸÑÿ© ÿ™ÿπÿ±ÿ∂ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÑŸÉŸÑ ÿ¥ŸäŸÅÿ™'
            }
          </p>
        </div>
        
        <div className="header-right">
          <div className="search-box">
            <Search size={18} />
            <input
              type="text"
              placeholder="ÿßÿ®ÿ≠ÿ´ ÿπŸÜ ÿπŸÖŸäŸÑÿå Ÿáÿßÿ™ŸÅÿå ŸÑÿπÿ®ÿ©..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
          </div>
          
          <button 
            className="btn btn-icon"
            onClick={loadInitialData}
            title="ÿ™ÿ≠ÿØŸäÿ´"
            disabled={loading}
          >
            <RefreshCw size={18} className={loading ? 'spinning' : ''} />
          </button>
          
          <button 
            className="btn btn-secondary"
            onClick={() => {
              // ÿ™ÿµÿØŸäÿ± ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
              showNotification('ÿ™ŸÖ ÿ™ÿµÿØŸäÿ± ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ®ŸÜÿ¨ÿßÿ≠', 'success');
            }}
