// frontend/src/pages/employee/Rentals.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  const { user } = useAuth();
  const [loading, setLoading] = useState(true);
  const [currentShift, setCurrentShift] = useState(null);
  const [shiftStats, setShiftStats] = useState(null);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
  const [availableGames, setAvailableGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  
  // ØªØ£Ø¬ÙŠØ± Ù…ØªØ¹Ø¯Ø¯
  const [selectedGames, setSelectedGames] = useState([]);
  const [quantities, setQuantities] = useState({});
  const [showRentalForm, setShowRentalForm] = useState(false);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù†Ù…ÙˆØ°Ø¬
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(60);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [discount, setDiscount] = useState(0);
  
  const timersRef = useRef({});

  // ØªØ£Ø«ÙŠØ±Ø§Øª
  useEffect(() => {
    if (user) {
      initializeShift();
      loadData();
      
      const interval = setInterval(() => {
        if (currentShift) {
          loadData();
        }
      }, 30000);
      
      return () => {
        clearInterval(interval);
        Object.values(timersRef.current).forEach(timer => clearInterval(timer));
      };
    }
  }, [user, currentShift]);

  // ==================== SHIFT MANAGEMENT ====================


const initializeShift = async () => {
  try {
    console.log('ğŸ”„ Ø¬Ù„Ø¨ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ù†Ø´Ø·...');
    
    const response = await api.get('/shifts/active');
    
    // âŒ**Ø§Ù„Ø­Ù„ Ø§Ù„Ù…Ø¨Ø³Ø·**: ØªØ¬Ø§Ù‡Ù„ Ù‡ÙŠÙƒÙ„ Ø§Ù„Ø§Ø³ØªØ¬Ø§Ø¨Ø© ÙˆØ§Ù„Ø¨Ø­Ø« Ù…Ø¨Ø§Ø´Ø±Ø© Ø¹Ù† Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
    let shiftData = null;
    
    // Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø© 1: Ø§Ù„Ø¨Ø­Ø« Ø¹Ù† Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ù…Ø¨Ø§Ø´Ø±Ø©
    if (response.data && response.data.id) {
      shiftData = response.data;
    }
    // Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø© 2: Ø§Ù„Ø¨Ø­Ø« ÙÙŠ Ø­Ù‚Ù„ data
    else if (response.data && response.data.data && response.data.data.id) {
      shiftData = response.data.data;
    }
    // Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø© 3: ØªØ­ÙˆÙŠÙ„ Ø§Ù„Ø±Ø¯ Ø¥Ù„Ù‰ ÙƒØ§Ø¦Ù† ÙˆÙØ­ØµÙ‡
    else {
      const data = response.data;
      // ØªØ­Ù‚Ù‚ Ù…Ù† Ø£ÙŠ Ø­Ù‚Ù„ Ù‚Ø¯ ÙŠØ­ØªÙˆÙŠ Ø¹Ù„Ù‰ ID
      for (const key in data) {
        if (data[key] && typeof data[key] === 'object' && data[key].id) {
          shiftData = data[key];
          break;
        }
      }
    }
    
    if (shiftData && shiftData.id) {
      console.log('âœ… ØªÙ… Ø§Ù„Ø¹Ø«ÙˆØ± Ø¹Ù„Ù‰ Ø´ÙŠÙØª Ù†Ø´Ø·:', shiftData.id);
      console.log('ğŸ“Š Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØª:', shiftData);
      setCurrentShift(shiftData);
      await loadShiftStats(shiftData.id);
    } else {
      console.log('â„¹ï¸ Ù„Ø§ ÙŠÙˆØ¬Ø¯ Ø´ÙŠÙØª Ù†Ø´Ø·');
      console.log('ğŸ“‹ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø§Ø³ØªØ¬Ø§Ø¨Ø©:', response.data);
      setCurrentShift(null);
    }
    
  } catch (error) {
    console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ Ø¬Ù„Ø¨ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ù†Ø´Ø·:', error);
    setCurrentShift(null);
    
    if (error.response?.status === 401) {
      showNotification('error', 'Ø§Ù†ØªÙ‡Øª Ø¬Ù„Ø³ØªÙƒ. ÙŠØ±Ø¬Ù‰ ØªØ³Ø¬ÙŠÙ„ Ø§Ù„Ø¯Ø®ÙˆÙ„ Ù…Ø±Ø© Ø£Ø®Ø±Ù‰');
    } else {
      showNotification('error', 'ÙØ´Ù„ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØª');
    }
  }
};

// ÙÙŠ Rentals.jsx - Ø¥Ø¶Ø§ÙØ© Ø¯Ø§Ù„Ø© ØªØ´Ø®ÙŠØµ
const debugAPIResponse = async () => {
  try {
    console.log('ğŸ” ØªØ´Ø®ÙŠØµ Ø§Ø³ØªØ¬Ø§Ø¨Ø© API...');
    
    const response = await api.get('/shifts/active');
    
    console.log('ğŸ“‹ Ø§Ù„Ø§Ø³ØªØ¬Ø§Ø¨Ø© Ø§Ù„ÙƒØ§Ù…Ù„Ø©:', response);
    console.log('ğŸ“Š response.status:', response.status);
    console.log('ğŸ“Š response.statusText:', response.statusText);
    console.log('ğŸ“Š response.headers:', response.headers);
    console.log('ğŸ“Š response.config:', response.config);
    console.log('ğŸ“Š response.data:', response.data);
    console.log('ğŸ“Š Ù†ÙˆØ¹ response.data:', typeof response.data);
    console.log('ğŸ“Š Ù…ÙØ§ØªÙŠØ­ response.data:', Object.keys(response.data || {}));
    
    // ØªØ­Ù„ÙŠÙ„ Ø¹Ù…ÙŠÙ‚ Ù„Ù„Ø¨ÙŠØ§Ù†Ø§Øª
    for (const key in response.data) {
      console.log(`ğŸ”‘ ${key}:`, response.data[key], `(Ù†ÙˆØ¹: ${typeof response.data[key]})`);
    }
    
  } catch (error) {
    console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ Ø§Ù„ØªØ´Ø®ÙŠØµ:', error);
  }
};



  const startNewShift = async () => {
    try {
      const response = await api.post('/shifts/start');
      if (response.data.success) {
        showNotification('success', 'ØªÙ… Ø¨Ø¯Ø¡ Ø§Ù„Ø´ÙŠÙØª Ø¨Ù†Ø¬Ø§Ø­');
        await initializeShift();
        await loadData();
      }
    } catch (error) {
      showNotification('error', error.response?.data?.message || 'ÙØ´Ù„ ÙÙŠ Ø¨Ø¯Ø¡ Ø§Ù„Ø´ÙŠÙØª');
    }
  };

