// ==================== المكون الرئيسي ====================
const Rentals = () => {
  const { user } = useAuth();
  const isManager = user?.role === 'admin' || user?.role === 'branch_manager';

  // الحالات الرئيسية
  const [loading, setLoading] = useState(true);
  const [games, setGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [currentShift, setCurrentShift] = useState(null);
  
  // الحالات للواجهة
  const [selectedGames, setSelectedGames] = useState([]);
  const [showRentalModal, setShowRentalModal] = useState(false);
  
  // الإحصائيات
  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

  // ==================== دوال جلب البيانات ====================
  const loadGames = useCallback(async () => {
    try {
      const response = await api.get('/games', {
        params: { 
          branch_id: user.branch_id,
          is_active: 1
        }
      });
      
      if (response.success && response.data) {
        setGames(response.data);
        
        const availableGames = response.data.filter(g => 
          g.status === 'متاح' || g.status === 'available'
        ).length;
        
        setStats(prev => ({
          ...prev,
          availableGames: availableGames,
          totalGames: response.data.length
        }));
      }
    } catch (error) {
      console.error('خطأ في تحميل الألعاب:', error);
    }
  }, [user.branch_id]);

  const loadActiveRentals = useCallback(async () => {
    try {
      const response = await api.getActiveRentals(user.branch_id);
      
      if (response.success && response.data) {
        setActiveRentals(response.data);
        setStats(prev => ({
          ...prev,
          activeRentals: response.data.length
        }));
      }
    } catch (error) {
      console.error('خطأ في تحميل التأجيرات النشطة:', error);
    }
  }, [user.branch_id]);

  const loadCompletedRentals = useCallback(async () => {
    if (!isManager) return;
    
    try {
      const today = new Date().toISOString().split('T')[0];
      const response = await api.get('/rentals', {
        params: { 
          branch_id: user.branch_id,
          status: 'مكتمل',
          start_date: today,
          limit: 50
        }
      });
      
      if (response.success && response.data) {
        setCompletedRentals(response.data);
        
        const todayRentals = response.data.filter(r => 
          r.end_time && r.end_time.split('T')[0] === today
        );
        
        const todayRevenue = todayRentals.reduce((sum, rental) => 
          sum + (parseFloat(rental.total_amount) || 0), 0
        );
        
        setStats(prev => ({
          ...prev,
          todayRevenue: todayRevenue,
          todayRentals: todayRentals.length
        }));
      }
    } catch (error) {
      console.error('خطأ في تحميل التأجيرات المكتملة:', error);
    }
  }, [user.branch_id, isManager]);

  const loadCurrentShift = useCallback(async () => {
    try {
      const response = await api.getActiveShiftSafe();
      
      if (response.success && response.data) {
        setCurrentShift(response.data);
        localStorage.setItem('current_shift', JSON.stringify(response.data));
      } else {
        setCurrentShift(null);
      }
    } catch (error) {
      console.error('فشل جلب الشيفت:', error);
      setCurrentShift(null);
    }
  }, []);

  const startShift = async () => {
    try {
      const response = await api.startShiftSafe();
      
      if (response.success) {
        setCurrentShift(response.data);
        localStorage.setItem('current_shift', JSON.stringify(response.data));
        
        setTimeout(() => {
          loadAllData();
        }, 1000);
      }
    } catch (error) {
      console.error('خطأ في بدء الشيفت:', error);
    }
  };

  const endShift = async () => {
    if (!currentShift) return;
    
    if (!window.confirm('هل تريد إنهاء الشيفت الحالي؟')) return;
    
    try {
      if (currentShift.id) {
        await api.endShift(currentShift.id, {
          end_time: new Date().toISOString(),
          status: 'منتهي'
        });
      }
      
      setCurrentShift(null);
      setActiveRentals([]);
      localStorage.removeItem('current_shift');
      
      await loadAllData();
      
    } catch (error) {
      console.error('Error ending shift:', error);
      setCurrentShift(null);
      setActiveRentals([]);
      localStorage.removeItem('current_shift');
    }
  };

  // جلب جميع البيانات
  const loadAllData = useCallback(async () => {
    setLoading(true);
    try {
      await Promise.all([
        loadGames(),
        loadActiveRentals(),
        loadCurrentShift()
      ]);
      
      if (currentShift) {
        await loadCompletedRentals();
      }
    } catch (error) {
      console.error('خطأ في تحميل البيانات:', error);
    } finally {
      setLoading(false);
    }
  }, [loadGames, loadActiveRentals, loadCurrentShift, loadCompletedRentals, currentShift]);

  // ==================== دوال إدارة التأجيرات ====================
  const handleSelectGame = (game) => {
    if (!currentShift) {
      alert('يجب بدء شيفت أولاً');
      return;
    }
    
    const isAlreadySelected = selectedGames.some(g => g.id === game.id);
    
    if (!isAlreadySelected) {
      setSelectedGames([...selectedGames, game]);
    }
    
    setShowRentalModal(true);
  };

  const handleCreateRental = async (rentalData) => {
    try {
      const { customerInfo, rentalItems, paymentMethod, paymentStatus, notes } = rentalData;
      
      for (const item of rentalItems) {
        const rentalPayload = {
          rental_number: generateRentalNumber(),
          customer_name: customerInfo.name,
          customer_phone: customerInfo.phone,
          game_id: item.gameId,
          branch_id: user.branch_id,
          shift_id: currentShift.id,
          employee_id: user.id,
          employee_name: user.name,
          rental_type: item.rentalType,
          duration_minutes: item.rentalType === 'fixed' ? item.duration : 0,
          total_amount: item.rentalType === 'fixed' ? 
            (item.pricePer15min * Math.ceil(item.duration / 15) * item.quantity) : 0,
          payment_method: paymentMethod,
          payment_status: paymentStatus,
          start_time: new Date().toISOString(),
          status: 'نشط',
          notes: `${notes} ${item.notes}`.trim(),
          quantity: item.quantity
        };
        
        await api.createRental(rentalPayload);
      }
      
      alert(`تم إنشاء ${rentalItems.length} تأجير بنجاح`);
      setShowRentalModal(false);
      setSelectedGames([]);
      await loadAllData();
      
    } catch (error) {
      console.error('خطأ في إنشاء التأجير:', error);
      alert('حدث خطأ في إنشاء التأجير');
    }
  };

  const handleRentalAction = async (action, rental) => {
    switch(action) {
      case 'details':
        alert(`تفاصيل التأجير #${rental.rental_number}\nالعميل: ${rental.customer_name}\nاللعبة: ${rental.game_name}`);
        break;
        
      case 'end':
        try {
          const response = await api.completeRentalSimple(rental.id, {
            payment_method: rental.payment_method || 'كاش'
          });
          
          if (response.success) {
            alert('تم إنهاء التأجير بنجاح');
            await loadAllData();
          }
        } catch (error) {
          console.error('Error ending rental:', error);
          alert('فشل إنهاء التأجير');
        }
        break;
          
      case 'cancel':
        if (window.confirm(`هل تريد إلغاء تأجير ${rental.rental_number}؟`)) {
          try {
            const response = await api.cancelRental(rental.id, rental.total_amount || 0, 'إلغاء من قبل الموظف');
            
            if (response.success) {
              alert('تم إلغاء التأجير بنجاح');
              await loadAllData();
            }
          } catch (error) {
            console.error('Error cancelling rental:', error);
            alert('فشل إلغاء التأجير');
          }
        }
        break;
    }
  };

  const handleExportCompleted = (rentals, period) => {
    const dataStr = JSON.stringify(rentals, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);
    
    const exportFileDefaultName = `تأجيرات_${period}_${new Date().toISOString().split('T')[0]}.json`;
    
    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
    
    alert(`تم تصدير ${rentals.length} تأجير`);
  };

  const handlePrintCompleted = (rentals, period) => {
    const printWindow = window.open('', '_blank');
    const content = `
      <html>
        <head>
          <title>تقرير التأجيرات - ${period}</title>
          <style>
            body { font-family: 'Arial', sans-serif; direction: rtl; padding: 20px; }
            h1 { text-align: center; color: #333; }
            table { width: 100%; border-collapse: collapse; margin-top: 20px; }
            th, td { border: 1px solid #ddd; padding: 8px; text-align: center; }
            th { background-color: #f2f2f2; }
            .total { font-weight: bold; color: #2196F3; }
            @media print {
              body { padding: 0; }
              .no-print { display: none; }
            }
          </style>
        </head>
        <body>
          <h1>تقرير التأجيرات المكتملة</h1>
          <p><strong>الفترة:</strong> ${period}</p>
          <p><strong>عدد التأجيرات:</strong> ${rentals.length}</p>
          <p><strong>التاريخ:</strong> ${new Date().toLocaleDateString('ar-EG')}</p>
          
          <table>
            <thead>
              <tr>
                <th>رقم التأجير</th>
                <th>العميل</th>
                <th>اللعبة</th>
                <th>وقت البدء</th>
                <th>وقت الانتهاء</th>
                <th>المبلغ</th>
              </tr>
            </thead>
            <tbody>
              ${rentals.map(rental => `
                <tr>
                  <td>${rental.rental_number || rental.id}</td>
                  <td>${rental.customer_name}</td>
                  <td>${rental.game_name}</td>
                  <td>${formatTime(rental.start_time)}</td>
                  <td>${formatTime(rental.end_time)}</td>
                  <td>${formatCurrency(rental.total_amount)}</td>
                </tr>
              `).join('')}
            </tbody>
          </table>
          
          <div style="margin-top: 20px; padding: 10px; background: #f9f9f9;">
            <p class="total">الإجمالي: ${formatCurrency(rentals.reduce((sum, r) => sum + (parseFloat(r.total_amount) || 0), 0))}</p>
          </div>
          
          <button class="no-print" onclick="window.print()" style="margin-top: 20px; padding: 10px 20px; background: #2196F3; color: white; border: none; cursor: pointer;">
            طباعة التقرير
          </button>
        </body>
      </html>
    `;
    
    printWindow.document.write(content);
    printWindow.document.close();
  };

  // ==================== useEffect ====================
  useEffect(() => {
    loadAllData();
    
    const interval = setInterval(() => {
      if (currentShift) {
        loadActiveRentals();
      }
    }, 30000);
    
    return () => clearInterval(interval);
  }, [loadAllData, currentShift, loadActiveRentals]);

  // ==================== التصيير ====================
  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>جاري تحميل النظام...</p>
      </div>
    );
  }

  return (
    <div className="rentals-page">
      {/* الرأس */}
      <header className="page-header">
        <div className="header-content">
          <div className="header-title">
            <h1 className="page-title">
              <span className="title-icon">🎮</span>
              نظام إدارة التأجيرات
            </h1>
            <div className="page-subtitle">
              {user.branch_name}
            </div>
          </div>
          <div className="user-info">
            <div className="user-badge">
              <User size={18} />
              <div className="user-details">
                <span className="user-name">{user?.name}</span>
                <span className="user-role">
                  {isManager ? '👑 مدير' : '👷 موظف'}
                </span>
              </div>
            </div>
            <div className="branch-info">
              <Building size={16} />
              <span className="branch-name">{user?.branch_name}</span>
            </div>
          </div>
        </div>
        
        <div className="header-actions">
          {selectedGames.length > 0 && (
            <button 
              onClick={() => setShowRentalModal(true)}
              className="btn btn-primary"
            >
              <Zap size={18} />
              بدء تأجير ({selectedGames.length})
            </button>
          )}
          
          <button 
            onClick={loadAllData}
            className="btn btn-secondary"
          >
            <RefreshCw size={18} />
            تحديث البيانات
          </button>
          
          {currentShift && (
            <div className="shift-indicator">
              <Clock size={16} />
              <span>شيفت #{currentShift.id}</span>
              <span className="shift-time">
                {formatTime(currentShift.start_time)}
              </span>
            </div>
          )}
        </div>
      </header>
      
      {/* لوحة التحكم */}
      <DashboardStats 
        stats={stats}
        currentShift={currentShift}
        onStartShift={startShift}
        onEndShift={endShift}
      />
      
      {/* الرسالة عند عدم وجود شيفت */}
      {!currentShift && (
        <div className="no-shift-alert">
          <div className="alert-content">
            <div className="alert-icon">
              <AlertCircle size={24} />
            </div>
            <div className="alert-text">
              <h3>لا يوجد شيفت نشط</h3>
              <p>يجب بدء شيفت جديد للتمكن من إدارة التأجيرات</p>
            </div>
            <button onClick={startShift} className="btn btn-success">
              <Zap size={18} />
              بدء شيفت جديد
            </button>
          </div>
        </div>
      )}
      
      {/* المحتوى الرئيسي */}
      {currentShift && (
        <div className="main-content">
          {/* الألعاب المتاحة */}
          <GamesGrid 
            games={games}
            branchId={user.branch_id}
            onSelectGame={handleSelectGame}
          />
          
          {/* التأجيرات النشطة */}
          <ActiveRentalsTable 
            rentals={activeRentals}
            onAction={handleRentalAction}
            onRefresh={loadActiveRentals}
          />
          
          {/* التأجيرات المكتملة (للمدير فقط) */}
          {isManager && (
            <CompletedRentalsTable 
              rentals={completedRentals}
              onExport={handleExportCompleted}
              onPrint={handlePrintCompleted}
            />
          )}
        </div>
      )}
      
      {/* نافذة التأجير المتعدد */}
      <MultiRentalModal 
        show={showRentalModal}
        onClose={() => {
          setShowRentalModal(false);
          if (selectedGames.length === 0) {
            setSelectedGames([]);
          }
        }}
        selectedGames={selectedGames}
        currentShift={currentShift}
        onConfirmRental={handleCreateRental}
      />
    </div>
  );
};

export default Rentals;
