// frontend/src/services/api.js
import axios from 'axios';

// ⚠️ تأكد من أن الرابط صحيح
const API_URL = 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 10000, // 10 ثواني كحد أقصى للانتظار
});

// Interceptor لإضافة التوكن
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    console.error('Request error:', error);
    return Promise.reject(error);
  }
);

// Interceptor للتعامل مع الأخطاء
api.interceptors.response.use(
  (response) => response,
  (error) => {
    console.error('Response error:', error.message);
    
    if (error.response) {
      // الخادم رد برسالة خطأ
      console.error('Status:', error.response.status);
      console.error('Data:', error.response.data);
      
      if (error.response.status === 401) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        window.location.href = '/login';
      }
    } else if (error.request) {
      // تم إرسال الطلب ولكن لم يتم استلام رد
      console.error('No response received:', error.request);
      alert('⚠️ تعذر الاتصال بالخادم. تأكد من أن الخادم يعمل.');
    } else {
      // حدث خطأ أثناء إعداد الطلب
      console.error('Request setup error:', error.message);
    }
    
    return Promise.reject(error);
  }
);

export default api;
