import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  const { user } = useAuth();
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [loadingMessage, setLoadingMessage] = useState('ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...');
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸàÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
  const [games, setGames] = useState([]);
  const [gameDurations, setGameDurations] = useState({});
  const [activeRentals, setActiveRentals] = useState([]);
  const [showOpenTimeActions, setShowOpenTimeActions] = useState(false);
  const [selectedOpenTimeRental, setSelectedOpenTimeRental] = useState(null);
  const [completedRentals, setCompletedRentals] = useState([]);
  
  // ŸÜÿßŸÅÿ∞ÿ© ÿ®ÿØÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [selectedGames, setSelectedGames] = useState([]);
  const [quantities, setQuantities] = useState({});
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÜŸÖŸàÿ∞ÿ¨
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(15);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [showAddMoreGames, setShowAddMoreGames] = useState(false);
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™
  const [currentShift, setCurrentShift] = useState(null);
  const [shiftStats, setShiftStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    activeRentals: 0
  });
  
  // ŸÜÿßŸÅÿ∞ÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ©
  const [showEditModal, setShowEditModal] = useState(false);
  const [editRental, setEditRental] = useState(null);
  const [editGame, setEditGame] = useState(null);
  const [showCancelModal, setShowCancelModal] = useState(false);
  const [cancelRental, setCancelRental] = useState(null);
  const [cancelAmount, setCancelAmount] = useState('');
  const [showEndModal, setShowEndModal] = useState(false);
  const [endRental, setEndRental] = useState(null);
  const [endAmount, setEndAmount] = useState('');
  const [remainingAmount, setRemainingAmount] = useState(0);
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [selectedNewGame, setSelectedNewGame] = useState(null);
  const [showReRentalModal, setShowReRentalModal] = useState(false);
  const [reRentalData, setReRentalData] = useState(null);
  const [reRentalGame, setReRentalGame] = useState(null);
  const [reRentalDuration, setReRentalDuration] = useState(60);
  
  // ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
  const [statistics, setStatistics] = useState({
    availableGames: 0,
    totalGames: 0,
    todayRevenue: 0,
    todayRentals: 0
  });
  
  // ÿ™ŸÜÿ®ŸäŸáÿßÿ™ ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÜÿ™ŸáŸä
  const [expiringAlerts, setExpiringAlerts] = useState([]);
  
  // Refs ŸÑŸÑŸÖÿ§ŸÇÿ™ÿßÿ™
  const timersRef = useRef({});
  const countdownTimersRef = useRef({});
  
  // ŸÖÿØÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑÿ´ÿßÿ®ÿ™ÿ©
  const fixedDurations = [
    { value: 15, label: '15 ÿØŸÇŸäŸÇÿ©', priceMultiplier: 1 },
    { value: 30, label: '30 ÿØŸÇŸäŸÇÿ©', priceMultiplier: 2 },
    { value: 45, label: '45 ÿØŸÇŸäŸÇÿ©', priceMultiplier: 3 },
    { value: 60, label: 'ÿ≥ÿßÿπÿ© Ÿàÿßÿ≠ÿØÿ©', priceMultiplier: 4 },
    { value: 120, label: 'ÿ≥ÿßÿπÿ™ŸäŸÜ', priceMultiplier: 8 },
