  // 6. ÿ¨ÿØŸàŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
  const ActiveRentalsTable = ({ rentals, onAction, loading }) => {
    const [countdowns, setCountdowns] = useState({});

    useEffect(() => {
      const updateCountdowns = () => {
        const newCountdowns = {};
        
        rentals.forEach(rental => {
          if (!rental) return;
          
          const isOpenTime = rental.is_open_time || rental.rental_type === 'open';
          
          if (!isOpenTime) {
            const endTime = new Date(rental.start_time);
            endTime.setMinutes(endTime.getMinutes() + (rental.duration_minutes || 0));
            const now = new Date();
            
            if (now > endTime) {
              newCountdowns[rental.id] = '‚è∞ ÿßŸÜÿ™ŸáŸâ';
            } else {
              const diffMs = endTime - now;
              const minutes = Math.floor(diffMs / 60000);
              const seconds = Math.floor((diffMs % 60000) / 1000);
              newCountdowns[rental.id] = `${minutes}:${seconds.toString().padStart(2, '0')}`;
            }
          } else {
            const startTime = new Date(rental.start_time);
            const now = new Date();
            const diffMs = now - startTime;
            const minutes = Math.floor(diffMs / 60000);
            const seconds = Math.floor((diffMs % 60000) / 1000);
            newCountdowns[rental.id] = `‚è≥ ${minutes}:${seconds.toString().padStart(2, '0')}`;
          }
        });
        
        setCountdowns(newCountdowns);
      };

      updateCountdowns();
      const interval = setInterval(updateCountdowns, 1000);
      
      return () => clearInterval(interval);
    }, [rentals]);

    const getRentalTimeInfo = useCallback((rental) => {
      if (!rental) return { type: '', remaining: '' };
      
      const isOpenTime = rental.is_open_time || rental.rental_type === 'open';
      
      if (isOpenTime) {
        return {
          type: '‚è≥ ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠',
          remaining: countdowns[rental.id] || '‚è≥ 0:00'
        };
      }
      
      return {
        type: `${rental.duration_minutes} ÿØŸÇŸäŸÇÿ©`,
        remaining: countdowns[rental.id] || `${rental.duration_minutes || 0}:00`
      };
    }, [countdowns]);

    const calculateRentalAmount = useCallback((rental) => {
      if (!rental) return formatCurrency(0);
      
      if (rental.total_amount && rental.total_amount > 0) {
        return formatCurrency(rental.total_amount);
      }
      
      const isOpenTime = rental.is_open_time || rental.rental_type === 'open';
      if (isOpenTime) {
        const startTime = new Date(rental.start_time);
        const now = new Date();
        const minutes = Math.floor((now - startTime) / 60000);
        const units = Math.ceil(minutes / 15);
        const pricePer15Min = rental.price_per_15min || 100;
        
        return formatCurrency(units * pricePer15Min);
      }
      
      return formatCurrency(rental.total_amount || 0);
    }, []);

    const getPaymentStatus = useCallback((rental) => {
      const isOpenTime = rental.is_open_time || rental.rental_type === 'open';
      
      if (isOpenTime) {
        return '‚è≥ ÿßŸÑÿØŸÅÿπ ÿπŸÜÿØ ÿßŸÑÿ•ŸÜÿ™Ÿáÿßÿ°';
      } else {
        return rental.payment_status === 'ŸÖÿØŸÅŸàÿπ ŸÖÿ≥ÿ®ŸÇÿßŸã' ? 
          '‚úÖ ŸÖÿØŸÅŸàÿπ ŸÖÿ≥ÿ®ŸÇÿßŸã' : 
          '‚ö†Ô∏è ŸÇŸäÿØ ÿßŸÑÿØŸÅÿπ';
      }
    }, []);

    if (loading) {
      return (
        <div className="rentals-active-rentals-section">
          <div className="rentals-loading-state">
            <Loader2 className="rentals-spinner" size={32} />
            <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™...</p>
          </div>
        </div>
      );
    }

    return (
      <div className="rentals-active-rentals-section">
        <div className="rentals-section-header">
          <h3 className="rentals-section-title">
            <Zap size={24} />
            ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
            <span className="rentals-section-count">({rentals.length})</span>
          </h3>
        </div>
        
        {rentals.length === 0 ? (
          <div className="rentals-empty-state">
            <Clock size={48} />
            <h3>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÜÿ¥ÿ∑ÿ©</h3>
            <p>ÿßÿ®ÿØÿ£ ÿ™ÿ£ÿ¨Ÿäÿ± ÿ¨ÿØŸäÿØ ŸÖŸÜ ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ®</p>
          </div>
        ) : (
          <div className="rentals-table-container">
            <table className="rentals-rentals-table">
              <thead>
                <tr>
                  <th>ÿ±ŸÇŸÖ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±</th>
                  <th>ÿßŸÑÿπŸÖŸäŸÑ</th>
                  <th>ÿßŸÑŸÑÿπÿ®ÿ©</th>
                  <th>ŸÜŸàÿπ ÿßŸÑŸàŸÇÿ™</th>
                  <th>ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖÿ™ÿ®ŸÇŸä</th>
                  <th>ÿßŸÑŸÖÿ®ŸÑÿ∫</th>
                  <th>ÿ≠ÿßŸÑÿ© ÿßŸÑÿØŸÅÿπ</th>
                  <th>ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™</th>
                </tr>
              </thead>
              <tbody>
                {rentals.map(rental => {
                  const timeInfo = getRentalTimeInfo(rental);
                  const isOpenTime = rental.is_open_time || rental.rental_type === 'open';
                  const isFixedTime = !isOpenTime;
                  const elapsedMinutes = rental.start_time ? 
                    Math.floor((new Date() - new Date(rental.start_time)) / 60000) : 0;
                  
                  return (
                    <tr key={rental.id}>
                      <td>
                        <strong>#{rental.rental_number || rental.id}</strong>
                        {isOpenTime && <span className="rentals-badge rentals-open">ŸÖŸÅÿ™Ÿàÿ≠</span>}
                        {isFixedTime && <span className="rentals-badge rentals-fixed">ÿ´ÿßÿ®ÿ™</span>}
                      </td>
                      <td>
                        <div className="rentals-customer-cell">
                          <div className="rentals-customer-name">{rental.customer_name}</div>
                          <div className="rentals-customer-phone">{rental.customer_phone}</div>
                        </div>
                      </td>
                      <td>{rental.game_name}</td>
                      <td>
                        <div className="rentals-time-type-cell">
                          {isOpenTime ? (
                            <span className="rentals-time-type rentals-open">
                              <Unlock size={12} />
                              ŸÖŸÅÿ™Ÿàÿ≠
                            </span>
                          ) : (
                            <span className="rentals-time-type rentals-fixed">
                              <Lock size={12} />
                              ÿ´ÿßÿ®ÿ™ ({rental.duration_minutes} ÿØŸÇŸäŸÇÿ©)
                            </span>
                          )}
                        </div>
                      </td>
                      <td>
                        <div className="rentals-countdown-cell">
                          <Clock size={14} />
                          <span>{timeInfo.remaining}</span>
                          {isFixedTime && elapsedMinutes <= 3 && (
                            <span className="rentals-early-end-badge">ŸäŸÖŸÉŸÜ ÿßŸÑÿ•ŸÜŸáÿßÿ° ÿßŸÑŸÖÿ®ŸÉÿ±</span>
                          )}
                        </div>
                      </td>
                      <td className="rentals-amount-cell">
                        {calculateRentalAmount(rental)}
                      </td>
                      <td>
                        <span className={`rentals-payment-status ${isOpenTime ? 'rentals-pending' : 'rentals-paid'}`}>
                          {getPaymentStatus(rental)}
                        </span>
                      </td>
                      <td>
                        <div className="rentals-action-buttons">
                          <button
                            onClick={() => onAction('details', rental)}
                            className="rentals-action-btn rentals-view"
                            title="ÿπÿ±ÿ∂ ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ"
                          >
                            <Eye size={16} />
                          </button>
                          
                          {isOpenTime && (
                            <button
                              onClick={() => onAction('end', rental)}
                              className="rentals-action-btn rentals-end"
                              title="ÿ•ŸÜŸáÿßÿ° ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠"
                            >
                              <Clock size={16} />
                            </button>
                          )}
                          
                          {isFixedTime && elapsedMinutes <= 3 && (
                            <button
                              onClick={() => onAction('early_end', rental)}
                              className="rentals-action-btn rentals-early-end"
                              title="ÿ•ŸÜŸáÿßÿ° ŸÇÿ®ŸÑ 3 ÿØŸÇÿßÿ¶ŸÇ"
                            >
                              <Clock size={16} />
                            </button>
                          )}
                          
                          <button
                            onClick={() => onAction('change_game', rental)}
                            className="rentals-action-btn rentals-change"
                            title="ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ©"
                          >
                            <RefreshCw size={16} />
                          </button>
                          
                          <button
                            onClick={() => onAction('cancel', rental)}
                            className="rentals-action-btn rentals-cancel"
                            title="ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±"
                          >
                            <XCircle size={16} />
                          </button>
                        </div>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        )}
      </div>
    );
  };

  // 7. ÿ¨ÿØŸàŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©
  const CompletedRentalsTable = ({ rentals, onRerental, loading }) => {
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredRentals, setFilteredRentals] = useState([]);

    useEffect(() => {
      if (!rentals) return;
      
      if (searchTerm) {
        const filtered = rentals.filter(rental =>
          rental.customer_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
          rental.customer_phone?.includes(searchTerm) ||
          rental.game_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
          rental.rental_number?.includes(searchTerm)
        );
        setFilteredRentals(filtered);
      } else {
        setFilteredRentals(rentals.slice(0, 20));
      }
    }, [rentals, searchTerm]);

    if (!isManager) return null;

    if (loading) {
      return (
        <div className="rentals-completed-rentals-section">
          <div className="rentals-loading-state">
            <Loader2 className="rentals-spinner" size={32} />
            <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©...</p>
          </div>
        </div>
      );
    }

    return (
      <div className="rentals-completed-rentals-section">
        <div className="rentals-section-header">
          <h3 className="rentals-section-title">
            <CheckCircle size={24} />
            ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© ÿßŸÑŸäŸàŸÖ
            <span className="rentals-section-count">({rentals.length})</span>
          </h3>
          
          <div className="rentals-section-controls">
            <div className="rentals-search-box">
              <Search size={18} />
              <input
                type="text"
                placeholder="ÿßÿ®ÿ≠ÿ´ ŸÅŸä ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="rentals-search-input"
              />
              {searchTerm && (
                <button onClick={() => setSearchTerm('')} className="rentals-clear-search">
                  <X size={16} />
                </button>
              )}
            </div>
          </div>
        </div>
        
        {filteredRentals.length === 0 ? (
          <div className="rentals-empty-state">
            <CheckCircle size={48} />
            <h3>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÖŸÉÿ™ŸÖŸÑÿ©</h3>
            <p>ÿ≥Ÿäÿ∏Ÿáÿ± ŸáŸÜÿß ÿ¨ŸÖŸäÿπ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© ÿßŸÑŸäŸàŸÖ</p>
          </div>
        ) : (
          <div className="rentals-table-container">
            <table className="rentals-rentals-table rentals-completed">
              <thead>
                <tr>
                  <th>ÿ±ŸÇŸÖ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±</th>
                  <th>ÿßŸÑÿπŸÖŸäŸÑ</th>
                  <th>ÿßŸÑŸÑÿπÿ®ÿ©</th>
                  <th>ŸàŸÇÿ™ ÿßŸÑÿ®ÿØÿ°</th>
                  <th>ŸàŸÇÿ™ ÿßŸÑÿ•ŸÜÿ™Ÿáÿßÿ°</th>
                  <th>ÿßŸÑŸÖÿØÿ©</th>
                  <th>ÿßŸÑŸÖÿ®ŸÑÿ∫</th>
                  <th>ŸÜŸàÿπ ÿßŸÑŸàŸÇÿ™</th>
                  <th>ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™</th>
                </tr>
              </thead>
              <tbody>
                {filteredRentals.map(rental => {
                  const isOpenTime = rental.is_open_time || rental.rental_type === 'open';
                  const duration = rental.actual_duration_minutes || rental.duration_minutes || 0;
                  
                  return (
                    <tr key={rental.id}>
                      <td>
                        <strong>#{rental.rental_number || rental.id}</strong>
                      </td>
                      <td>
                        <div className="rentals-customer-cell">
                          <div className="rentals-customer-name">{rental.customer_name}</div>
                          <div className="rentals-customer-phone">{rental.customer_phone}</div>
                        </div>
                      </td>
                      <td>{rental.game_name}</td>
                      <td>{formatDateTime(rental.start_time)}</td>
                      <td>{formatDateTime(rental.end_time || rental.actual_end_time)}</td>
                      <td>{duration} ÿØŸÇŸäŸÇÿ©</td>
                      <td className="rentals-amount-cell">
                        {formatCurrency(rental.final_amount || rental.total_amount || 0)}
                      </td>
                      <td>
                        {isOpenTime ? (
                          <span className="rentals-badge rentals-open">ŸÖŸÅÿ™Ÿàÿ≠</span>
                        ) : (
                          <span className="rentals-badge rentals-fixed">ÿ´ÿßÿ®ÿ™</span>
                        )}
                      </td>
                      <td>
                        <div className="rentals-action-buttons">
                          <button
                            onClick={() => onRerental(rental)}
                            className="rentals-action-btn rentals-rerental"
                            title="ÿ•ÿπÿßÿØÿ© ÿ™ÿ£ÿ¨Ÿäÿ± ŸÑŸÜŸÅÿ≥ ÿßŸÑÿπŸÖŸäŸÑ"
                          >
                            <RotateCcw size={16} />
                            ÿ•ÿπÿßÿØÿ© ÿ™ÿ£ÿ¨Ÿäÿ±
                          </button>
                        </div>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        )}
      </div>
    );
  };

  // ÿØÿßŸÑÿ© ŸÑŸÑÿ™ÿ≠ŸÇŸÇ ÿßŸÑÿ™ŸÑŸÇÿßÿ¶Ÿä ŸÖŸÜ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
  const autoCheckRentals = useCallback(async () => {
    if (!currentShift || !user?.branch_id) return;
    
    try {
      console.log('‚è∞ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ÿßŸÑÿ™ŸÑŸÇÿßÿ¶Ÿä ŸÖŸÜ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™...');
      
      const response = await api.processCompletedRentals();
      
      if (response.success && response.data?.processed_count > 0) {
        console.log(`‚úÖ ÿ™ŸÖ ŸÖÿπÿßŸÑÿ¨ÿ© ${response.data.processed_count} ÿ™ÿ£ÿ¨Ÿäÿ±`);
        
        loadActiveRentals();
        loadCompletedRentals();
        
        const completedRentals = response.data.processed_rentals || [];
        if (completedRentals.length > 0) {
          console.log('üìã ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©:', 
            completedRentals.map(r => `${r.rental_number} (${r.type})`).join(', ')
          );
        }
      }
    } catch (error) {
      console.warn('‚ö†Ô∏è ŸÑÿß ŸäŸÖŸÉŸÜ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™:', error.message);
    }
  }, [currentShift, user?.branch_id, loadActiveRentals, loadCompletedRentals]);

  useEffect(() => {
    if (currentShift) {
      autoCheckRentals();
      
      const interval = setInterval(autoCheckRentals, 30000);
      
      return () => clearInterval(interval);
    }
  }, [currentShift, autoCheckRentals]);

  const handleManualProcessing = async () => {
    if (!currentShift) {
      alert('Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ£ŸàŸÑÿßŸã');
      return;
    }
    
    const confirmed = window.confirm(
      'ŸáŸÑ ÿ™ÿ±ŸäÿØ ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸãÿü\n' +
      'ÿ≥Ÿäÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿ¨ŸÖŸäÿπ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑÿ™Ÿä ÿßŸÜÿ™ŸáŸâ ŸàŸÇÿ™Ÿáÿß.'
    );
    
    if (!confirmed) return;
    
    try {
      const response = await api.processCompletedRentals();
      
      if (response.success) {
        const count = response.data?.processed_count || 0;
        
        if (count > 0) {
          alert(`‚úÖ ÿ™ŸÖ ŸÖÿπÿßŸÑÿ¨ÿ© ${count} ÿ™ÿ£ÿ¨Ÿäÿ± ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã`);
        } else {
          alert('‚ÑπÔ∏è ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿ™ÿ≠ÿ™ÿßÿ¨ ŸÑŸÑŸÖÿπÿßŸÑÿ¨ÿ©');
        }
        
        loadActiveRentals();
        loadCompletedRentals();
      } else {
        alert('‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿ©');
      }
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑŸäÿØŸàŸäÿ©:', error);
      alert('‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿ©');
    }
  };

  // ==================== ÿßŸÑÿ™ÿµŸäŸäÿ± ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä ====================
  if (loading.games && loading.rentals) {
    return (
      <div className="rentals-loading-container">
        <Loader2 className="rentals-spinner" size={48} />
        <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÜÿ∏ÿßŸÖ...</p>
      </div>
    );
  }

return (
  <div className="rentals-page">
    {/* ÿßŸÑÿπŸÜŸàÿßŸÜ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä */}
    <header className="rentals-page-header">
      <div className="rentals-header-content">
        <h1>
          <Gamepad2 size={32} />
          ŸÜÿ∏ÿßŸÖ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± L3bty_Store
        </h1>
        <div className="rentals-user-info">
          <User size={20} />
          <span>{user?.name} - {isManager ? 'ŸÖÿØŸäÿ±' : 'ŸÖŸàÿ∏ŸÅ'}</span>
          <Building size={20} />
          <span>{user?.branch_name}</span>
        </div>
      </div>
    </header>

    {/* ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ≥ÿ±Ÿäÿπÿ© */}
    <div className="quick-stats-section">
      <DashboardStats 
        stats={stats}
        currentShift={currentShift}
        onStartShift={startShift}
        onEndShift={endShift}
        loading={loading.shift}
      />
    </div>

    {!currentShift && (
      <div className="rentals-no-shift-alert">
        <AlertCircle size={24} />
        <div>
          <h3>ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑</h3>
          <p>Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØ ŸÑŸÑÿ™ŸÖŸÉŸÜ ŸÖŸÜ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™</p>
        </div>
        <button onClick={startShift} className="rentals-btn rentals-btn-success">
          ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØ
        </button>
      </div>
    )}

    {currentShift && (
      <>
        {/* ÿ£ÿ≤ÿ±ÿßÿ± ÿßŸÑÿ™ŸÜŸÇŸÑ ÿßŸÑÿ≥ÿ±Ÿäÿπ */}
        <div className="quick-navigation-section">
          <div className="navigation-buttons">
            <button 
              onClick={() => document.getElementById('active-rentals')?.scrollIntoView({behavior: 'smooth'})}
              className="nav-btn"
            >
              <Users size={20} />
              ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ© ({activeRentals?.length || 0})
            </button>
            
            <button 
              onClick={() => document.getElementById('completed-rentals')?.scrollIntoView({behavior: 'smooth'})}
              className="nav-btn"
            >
              <CheckCircle size={20} />
              ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© ({completedRentals?.length || 0})
            </button>
            
            <div className="header-actions">
              {selectedGames.length > 0 && (
                <button 
                  onClick={() => setShowRentalModal(true)}
                  className="rentals-btn rentals-btn-primary"
                >
                  <Users size={20} />
                  ÿ™ÿ£ÿ¨Ÿäÿ± ŸÖÿ™ÿπÿØÿØ ({selectedGames.length})
                </button>
              )}
              
              <button 
                onClick={loadAllData}
                className="rentals-btn rentals-btn-secondary"
              >
                <RefreshCw size={20} />
                ÿ™ÿ≠ÿØŸäÿ´
              </button>

              <button 
                onClick={handleManualProcessing}
                className="rentals-btn rentals-btn-secondary"
                title="ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã"
              >
                <RefreshCw size={20} />
                ŸÖÿπÿßŸÑÿ¨ÿ© ÿ™ŸÑŸÇÿßÿ¶Ÿäÿ©
              </button>
            </div>
          </div>
        </div>

        {/* ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© */}
        <section className="available-games-section">
          <div className="section-header">
            <h2>
              <Gamepad2 size={24} />
              ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© ŸÑŸÑÿ•Ÿäÿ¨ÿßÿ±
            </h2>
            <span className="games-count">({games?.length || 0} ŸÑÿπÿ®ÿ©)</span>
          </div>
          <GamesGrid 
            games={games}
            branchId={user?.branch_id}
            onSelectGame={handleSelectGame}
            loading={loading.games}
          />
        </section>

        {/* ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ© */}
        <section id="active-rentals" className="active-rentals-section">
          <div className="section-header">
            <h2>
              <Clock size={24} />
              ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
            </h2>
            <span className="rentals-count">({activeRentals?.length || 0} ÿ™ÿ£ÿ¨Ÿäÿ±)</span>
          </div>
          <ActiveRentalsTable 
            rentals={activeRentals}
            onAction={handleRentalAction}
            loading={loading.rentals}
          />
        </section>

        {/* ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© */}
        <section id="completed-rentals" className="completed-rentals-section">
          <div className="section-header">
            <h2>
              <CheckCircle size={24} />
              ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©
            </h2>
            <span className="rentals-count">({completedRentals?.length || 0} ÿ™ÿ£ÿ¨Ÿäÿ±)</span>
          </div>
          <CompletedRentalsTable 
            rentals={completedRentals}
            onRerental={handleRerental}
            loading={loading.stats}
          />
        </section>
      </>
    )}

    {/* ÿßŸÑŸÖŸàÿØÿßŸÑÿßÿ™ */}
    <RentalModal 
      show={showRentalModal}
      onClose={() => {
        setShowRentalModal(false);
        setSelectedGames([]);
      }}
      selectedGames={selectedGames}
      currentShift={currentShift}
      onConfirmRental={handleCreateRental}
      allGames={allGames}
    />
    
    <ChangeGameModal 
      show={showChangeGameModal}
      onClose={() => {
        setShowChangeGameModal(false);
        setSelectedRentalForChange(null);
      }}
      rental={selectedRentalForChange}
      games={games}
      onConfirm={confirmChangeGame}
    />
    
    <RerentalModal 
      show={showRerentalModal}
      onClose={() => {
        setShowRerentalModal(false);
        setSelectedCompletedRental(null);
      }}
      rental={selectedCompletedRental}
      games={games}
      onConfirm={confirmRerental}
    />
  </div>
);
};

export default Rentals;
