import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { 
  Home, Building, Users, Clock, Settings, 
  LogOut, ChevronLeft, ChevronRight, Menu, FileText
} from 'lucide-react';
import './Layout.css';

// Custom hook for click outside detection
const useClickOutside = (ref, handler) => {
  useEffect(() => {
    const listener = (event) => {
      if (!ref.current || ref.current.contains(event.target)) return;
      handler(event);
    };

    document.addEventListener('mousedown', listener);
    document.addEventListener('touchstart', listener);

    return () => {
      document.removeEventListener('mousedown', listener);
      document.removeEventListener('touchstart', listener);
    };
  }, [ref, handler]);
};

// نقش الأيقونات لكل دور
const getNavIcon = (iconName) => {
  const icons = {
    Home: <Home size={20} />,
    Building: <Building size={20} />,
    Users: <Users size={20} />,
    Clock: <Clock size={20} />,
    Settings: <Settings size={20} />,
    FileText: <FileText size={20} />,
  };
  return icons[iconName] || <Home size={20} />;
};

// أدوار المستخدمين مع ترجماتها
const USER_ROLES = {
  admin: { text: 'المدير العام', color: 'role-admin' },
  branch_manager: { text: 'مدير فرع', color: 'role-branch-manager' },
  employee: { text: 'موظف', color: 'role-employee' },
  default: { text: 'مستخدم', color: 'role-default' }
};

// عناصر التنقل لكل دور
const NAV_ITEMS = {
  admin: [
    { title: 'لوحة التحكم', icon: 'Home', path: '/admin', exact: true },
    { title: 'المستخدمين', icon: 'Users', path: '/admin/users' },
    { title: 'الفروع', icon: 'Building', path: '/admin/branches' },
    { title: 'التأجيرات', icon: 'Clock', path: '/admin/rentals', badge: { count: 5, color: 'warning' } },
    { title: 'التحليل المالي', icon: 'FileText', path: '/admin/revenus' },
    { title: 'الإعدادات', icon: 'Settings', path: '/admin/settings' }
  ],
  branch_manager: [
    { title: 'لوحة التحكم', icon: 'Home', path: '/employee', exact: true },
    { title: 'التأجيرات', icon: 'Clock', path: '/employee/rentals' },
    { title: 'التقارير', icon: 'FileText', path: '/employee/reports' },
    { title: 'إعدادات الفرع', icon: 'Settings', path: '/employee/settings' }
  ],
  employee: [
    { title: 'لوحة التحكم', icon: 'Home', path: '/employee', exact: true },
    { title: 'التأجيرات', icon: 'Clock', path: '/employee/rentals' }
  ]
};

const Layout = ({ children }) => {
  const { user, logout, isAuthenticated } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();
  
  // الحالات
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [isUserDropdownOpen, setIsUserDropdownOpen] = useState(false);
  
  // المراجع
  const menuRef = useRef(null);
  const userDropdownRef = useRef(null);
  const sidebarRef = useRef(null);

  // استخدام الهوك المخصص
  useClickOutside(menuRef, () => setIsMenuOpen(false));
  useClickOutside(userDropdownRef, () => setIsUserDropdownOpen(false));

  // إغلاق القوائم عند تغيير المسار
  useEffect(() => {
    setIsMenuOpen(false);
    setIsUserDropdownOpen(false);
  }, [location]);

  // التعامل مع تسجيل الخروج
  const handleLogout = useCallback(() => {
    logout();
    navigate('/login');
  }, [logout, navigate]);

  // تبديل القوائم
  const toggleMenu = useCallback(() => setIsMenuOpen(prev => !prev), []);
  const toggleSidebar = useCallback(() => setIsSidebarCollapsed(prev => !prev), []);
  const toggleUserDropdown = useCallback(() => setIsUserDropdownOpen(prev => !prev), []);

  // معلومات الدور
  const userRole = useMemo(() => USER_ROLES[user?.role] || USER_ROLES.default, [user?.role]);
  
  // عناصر التنقل للمستخدم الحالي
  const userNavItems = useMemo(() => 
    NAV_ITEMS[user?.role] || NAV_ITEMS.employee, 
    [user?.role]
  );

  // المسار الأساسي
  const basePath = useMemo(() => 
    user?.role === 'admin' ? '/admin' : '/employee', 
    [user?.role]
  );

  // إذا لم يكن المستخدم مسجلاً، عرض المحتوى فقط
  if (!isAuthenticated) return children;

  return (
    <div className="app-container">
      {/* الشريط الجانبي الثابت */}
      <aside 
        ref={sidebarRef}
        className={`sidebar-fixed ${isSidebarCollapsed ? 'collapsed' : ''}`}
      >
        {/* الشعار */}
        <div className="sidebar-logo">
          {!isSidebarCollapsed ? (
            <div className="logo-content">
              <div className="logo-icon">🏬</div>
              <div className="logo-text">
                <h2>L3BTY Store</h2>
                <p>نظام إدارة</p>
              </div>
            </div>
          ) : (
            <div className="logo-collapsed">
              <span className="logo-icon-small">🏬</span>
            </div>
          )}
          
          <button 
            className="collapse-btn"
            onClick={toggleSidebar}
            title={isSidebarCollapsed ? 'إظهار القائمة' : 'طي القائمة'}
            aria-label={isSidebarCollapsed ? 'إظهار القائمة' : 'طي القائمة'}
          >
            {isSidebarCollapsed ? <ChevronRight size={18} /> : <ChevronLeft size={18} />}
          </button>
        </div>
        
        {/* معلومات المستخدم */}
        <div className="user-section">
          {!isSidebarCollapsed && (
            <div className="user-info">
              <div className="user-avatar">
                <Users size={24} />
              </div>
              <div className="user-details">
                <h4>{user?.name || 'المستخدم'}</h4>
                <p>{userRole.text}</p>
              </div>
            </div>
          )}
        </div>
        
        {/* قائمة التنقل */}
        <nav className="sidebar-nav-fixed">
          <ul className="nav-menu-fixed">
            {userNavItems.map((item) => {
              const isActive = item.exact 
                ? location.pathname === item.path
                : location.pathname.startsWith(item.path);
              
              return (
                <li key={item.path}>
                  <Link 
                    to={item.path} 
                    className={`nav-item-fixed ${isActive ? 'active' : ''}`}
                    title={isSidebarCollapsed ? item.title : ''}
                    aria-current={isActive ? 'page' : undefined}
                  >
                    <span className="nav-icon-fixed">
                      {getNavIcon(item.icon)}
                      {item.badge && (
                        <span className={`nav-badge-fixed ${item.badge.color}`}>
                          {item.badge.count}
                        </span>
                      )}
                    </span>
                    {!isSidebarCollapsed && (
                      <>
                        <span className="nav-label-fixed">{item.title}</span>
                        {isActive && <span className="nav-indicator-fixed"></span>}
                      </>
                    )}
                  </Link>
                </li>
              );
            })}
          </ul>
        </nav>
        
        {/* تذييل الشريط الجانبي */}
        <div className="sidebar-footer-fixed">
          <button 
            className="logout-btn-fixed"
            onClick={handleLogout}
            title={isSidebarCollapsed ? 'تسجيل الخروج' : ''}
            aria-label="تسجيل الخروج"
          >
            <LogOut size={20} />
            {!isSidebarCollapsed && <span>تسجيل الخروج</span>}
          </button>
          
          {!isSidebarCollapsed && (
            <div className="system-info-fixed">
              <div className="system-status-fixed">
                <span className="status-dot-fixed online"></span>
                النظام يعمل
              </div>
              <div className="system-version-fixed">الإصدار 3.0.0</div>
            </div>
          )}
        </div>
      </aside>

      {/* المحتوى الرئيسي مع شريط التنقل */}
      <div className={`main-content-wrapper ${isSidebarCollapsed ? 'sidebar-collapsed' : ''}`}>
        {/* شريط التنقل العلوي */}
        <nav className="navbar">
          <div className="navbar-container">
            {/* القسم الأيسر */}
            <div className="navbar-left">
              <button 
                className="menu-toggle"
                onClick={toggleMenu}
                aria-label="فتح/إغلاق القائمة المتنقلة"
                aria-expanded={isMenuOpen}
              >
                <Menu size={24} />
              </button>
              
              <Link to={basePath} className="navbar-brand">
                <div className="logo-container">
                  <div className="logo">
                    <img 
                      src="/images/l3bty.png" 
                      alt="L3BTY Store Logo" 
                      className="logo-image"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = "data:image/svg+xml,%3Csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 100 100'%3E%3Crect width='100' height='100' fill='%234361ee'/%3E%3Ctext x='50' y='50' font-family='Arial' font-size='40' fill='white' text-anchor='middle' dy='.3em'%3EL%3C/text%3E%3C/svg%3E";
                      }}
                    />
                  </div>
                  <div className="brand-info">
                    <div className="brand-text">L3BTY Store</div>
                    <div className="brand-subtitle">نظام إدارة التأجيرات</div>
                  </div>
                </div>
              </Link>
            </div>

            {/* القسم الأيمن */}
            <div className="navbar-right">
              <div className="user-container" ref={userDropdownRef}>
                <button
                  className="user-button"
                  onClick={toggleUserDropdown}
                  aria-label="فتح قائمة المستخدم"
                  aria-expanded={isUserDropdownOpen}
                >
                  <div className="user-info">
                    <div className="user-name">{user?.name || 'مستخدم'}</div>
                    <div className="user-role">{userRole.text}</div>
                  </div>
                  <div className={`user-avatar ${userRole.color}`}>
                    {user?.name?.charAt(0) || 'U'}
                  </div>
                  <span className={`dropdown-arrow ${isUserDropdownOpen ? 'open' : ''}`}>
                    ▼
                  </span>
                </button>

                {/* القائمة المنسدلة */}
                {isUserDropdownOpen && (
                  <div className="user-dropdown" role="menu">
                    <div className="user-dropdown-header">
                      <div className="user-dropdown-name">{user?.name}</div>
                      <div className="user-dropdown-email">{user?.email}</div>
                      <div className="user-dropdown-role">{userRole.text}</div>
                      {user?.branch_name && (
                        <div className="user-dropdown-branch">
                          <span className="branch-icon">🏬</span>
                          {user.branch_name}
                        </div>
                      )}
                    </div>
                    
                    <div className="dropdown-divider"></div>
                    
                    <Link
                      to={`${basePath}/profile`}
                      className="dropdown-item"
                      onClick={() => setIsUserDropdownOpen(false)}
                      role="menuitem"
                    >
                      <span className="dropdown-icon">👤</span>
                      الملف الشخصي
                    </Link>
                    
                    <Link
                      to={`${basePath}/settings`}
                      className="dropdown-item"
                      onClick={() => setIsUserDropdownOpen(false)}
                      role="menuitem"
                    >
                      <span className="dropdown-icon">⚙️</span>
                      الإعدادات
                    </Link>
                    
                    <div className="dropdown-divider"></div>
                    
                    <button
                      onClick={handleLogout}
                      className="dropdown-item logout"
                      role="menuitem"
                    >
                      <span className="dropdown-icon">🚪</span>
                      تسجيل الخروج
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* القائمة الجانبية المتنقلة */}
          <div 
            className={`sidebar-mobile ${isMenuOpen ? 'open' : ''}`} 
            ref={menuRef}
            role="dialog"
            aria-modal="true"
          >
            <div className="sidebar-header">
              <h3 className="sidebar-title">القائمة الرئيسية</h3>
              <button 
                className="sidebar-close"
                onClick={() => setIsMenuOpen(false)}
                aria-label="إغلاق القائمة"
              >
                ✕
              </button>
            </div>
            
            <nav className="sidebar-nav-mobile" role="navigation">
              {userNavItems.map((item) => {
                const isActive = item.exact 
                  ? location.pathname === item.path
                  : location.pathname.startsWith(item.path);
                
                return (
                  <Link
                    key={item.path}
                    to={item.path}
                    className={`nav-link-mobile ${isActive ? 'active' : ''}`}
                    onClick={() => setIsMenuOpen(false)}
                    aria-current={isActive ? 'page' : undefined}
                  >
                    <span className="nav-icon-mobile">{getNavIcon(item.icon)}</span>
                    <span className="nav-label-mobile">{item.title}</span>
                    {item.badge && (
                      <span className="nav-badge-mobile">{item.badge.count}</span>
                    )}
                    {isActive && <span className="nav-indicator-mobile"></span>}
                  </Link>
                );
              })}
            </nav>
            
            <div className="sidebar-footer-mobile">
              <div className="user-sidebar-info">
                <div className="sidebar-user-avatar">
                  {user?.name?.charAt(0) || 'U'}
                </div>
                <div className="sidebar-user-details">
                  <div className="sidebar-user-name">{user?.name}</div>
                  <div className="sidebar-user-role">{userRole.text}</div>
                </div>
              </div>
            </div>
          </div>

          {/* طبقة التغطية */}
          {isMenuOpen && (
            <div 
              className="sidebar-overlay"
              onClick={() => setIsMenuOpen(false)}
              aria-hidden="true"
            />
          )}
        </nav>

        {/* المحتوى الرئيسي */}
        <main className="main-content" role="main">
          <div className="page-container">
            {children}
          </div>
        </main>

        {/* التذييل */}
        <footer className="footer" role="contentinfo">
          <div className="footer-content">
            <div className="footer-section">
              <div className="footer-logo">
                <div className="footer-logo-icon">🎮</div>
                <div>
                  <h3 className="footer-title">L3BTY Store</h3>
                  <p className="footer-description">
                    نظام إدارة تأجير الألعاب المتكامل
                  </p>
                </div>
              </div>
              <p className="footer-about">
                نظام متكامل لإدارة تأجير الألعاب الإلكترونية، مصمم لتبسيط عمليات الإدارة، 
                المتابعة، والإحصائيات لتحقيق أفضل تجربة للمستخدمين.
              </p>
            </div>

            <div className="footer-section">
              <h3 className="footer-section-title">معلومات الاتصال</h3>
              <ul className="footer-contact">
                <li className="contact-item">
                  <span className="contact-icon">📍</span>
                  <span>القاهرة، مصر</span>
                </li>
                <li className="contact-item">
                  <span className="contact-icon">📞</span>
                  <span>01016904292</span>
                </li>
                <li className="contact-item">
                  <span className="contact-icon">✉️</span>
                  <span>support@l3bty.com</span>
                </li>
                <li className="contact-item">
                  <span className="contact-icon">🕒</span>
                  <span>10:00 ص - 10:00 م</span>
                </li>
              </ul>
            </div>
          </div>
          
          <div className="footer-bottom">
            <div className="footer-copyright">
              © {new Date().getFullYear()} L3BTY Store. جميع الحقوق محفوظة.
            </div>
            <div className="footer-meta">
              <span className="meta-item">
                <span className="meta-icon">🔄</span>
                آخر تحديث: {new Date().toLocaleDateString('ar-EG')}
              </span>
              <span className="meta-item">
                <span className="meta-icon">⚡</span>
                الإصدار: 3.0.0
              </span>
            </div>
          </div>
        </footer>
      </div>
    </div>
  );
};

export default React.memo(Layout);
