    const roleHierarchy = {
      'admin': 3,
      'branch_manager': 2,
      'employee': 1
    };
    
    const userLevel = roleHierarchy[user.role] || 0;
    const requiredLevel = roleHierarchy[requiredRole] || 0;
    
    return userLevel >= requiredLevel;
  }
