        imageUrl: getGameImageUrl(game),
        isActive: true,
        notes: '',
        rentalNumber: `TEMP-${Date.now()}-${game.id}`
      };
      
      setRentalList(prev => [...prev, newRental]);
    }
  };
  
  const removeGameFromRentalList = (rentalId) => {
    setRentalList(prev => prev.filter(r => r.id !== rentalId));
  };
  
  const updateRentalQuantity = (rentalId, newQuantity) => {
    if (newQuantity < 1) {
      removeGameFromRentalList(rentalId);
      return;
    }
    
    setRentalList(prev => prev.map(rental => 
      rental.id === rentalId 
        ? { ...rental, quantity: newQuantity }
        : rental
    ));
  };
  
  const updateRentalType = (rentalId, newType) => {
    setRentalList(prev => prev.map(rental => 
      rental.id === rentalId 
        ? { 
            ...rental, 
            rentalType: newType,
            selectedTime: newType === 'fixed' ? 15 : rental.selectedTime,
            customMinutes: newType === 'custom' ? 15 : rental.customMinutes
          }
        : rental
    ));
  };
  
  const updateRentalTime = (rentalId, minutes) => {
    setRentalList(prev => prev.map(rental => 
      rental.id === rentalId 
        ? { ...rental, selectedTime: minutes }
        : rental
    ));
  };
  
  const updateCustomTime = (rentalId, minutes) => {
    if (minutes < 1) minutes = 1;
    if (minutes > 480) minutes = 480;
    
    setRentalList(prev => prev.map(rental => 
      rental.id === rentalId 
