        : rental
    ));
  };
  
  const calculateRentalPrice = (rental) => {
    if (!rental) return 0;
    
    if (rental.rentalType === 'open') {
      return 0;
    }
    
    const minutes = rental.rentalType === 'custom' 
      ? rental.customMinutes 
      : rental.selectedTime;
    
    const pricePer15min = rental.pricePer15min || 100;
    const units = Math.ceil(minutes / 15);
    
    return pricePer15min * units * rental.quantity;
  };
  
  const calculateTotalPrice = () => {
    return rentalList.reduce((total, rental) => {
      if (rental.rentalType === 'open') return total;
      return total + calculateRentalPrice(rental);
