    if (rental.rentalType === 'open') return 0;
    if (rental.rentalType === 'custom') return rental.customMinutes;
    return rental.selectedTime;
  };
  
  const formatRentalDuration = (rental) => {
    const minutes = getRentalMinutes(rental);
    
    if (rental.rentalType === 'open') {
