// src/services/api.js
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000'; // ‚Üê ÿ•ÿ≤ÿßŸÑÿ© /api ŸÖŸÜ ŸáŸÜÿß

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 30000,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json',
    'Accept-Language': 'ar'
  },
  withCredentials: true // ŸÖŸáŸÖ ŸÑŸÄ CORS
});

// Interceptor ŸÑŸÑÿ∑ŸÑÿ®ÿßÿ™
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    
    console.log(`üì§ [${config.method?.toUpperCase()}] ${config.baseURL}${config.url}`);
    console.log('üì§ Request Headers:', config.headers);
    
    return config;
  },
  (error) => {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ∑ŸÑÿ® API:', error);
    return Promise.reject(error);
  }
);

// Interceptor ŸÑŸÑÿ±ÿØŸàÿØ
api.interceptors.response.use(
  (response) => {
    console.log(`‚úÖ [${response.status}] ${response.config.url}`);
    return response.data; // ÿ•ÿ±ÿ¨ÿßÿπ data ŸÖÿ®ÿßÿ¥ÿ±ÿ©
  },
  (error) => {
    if (error.code === 'ERR_NETWORK') {
      console.error('üåê ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ¥ÿ®ŸÉÿ©:', {
        url: error.config?.url,
        message: 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ',
        tip: 'ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑÿÆÿßÿØŸÖ ÿπŸÑŸâ http://localhost:5000'
      });
    } else {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© API:', {
        url: error.config?.url,
        status: error.response?.status,
        message: error.message,
        response: error.response?.data
      });
    }
    
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    
    return Promise.reject(error);
  }
);

// ÿØÿßŸÑÿ© ŸÑÿ™ÿπŸäŸäŸÜ ÿßŸÑÿ™ŸàŸÉŸÜ
api.setToken = (token) => {
  if (token) {
    api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
  } else {
    delete api.defaults.headers.common['Authorization'];
  }
};

