// üîê AUTHENTICATION
api.login = async (email, password) => {
  try {
    console.log('üîê ŸÖÿ≠ÿßŸàŸÑÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ:', email);
    const response = await api.post('/auth/login', { 
      email, 
      password,
      device: navigator.userAgent
    });
    
    console.log('üîë ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ:', response);
    
    if (response.success && response.token) {
      api.setToken(response.token);
      
      // ÿ™ÿ£ŸÉÿØ ŸÖŸÜ Ÿàÿ¨ŸàÿØ user ŸÅŸä response
      if (response.user) {
        localStorage.setItem('user', JSON.stringify(response.user));
        localStorage.setItem('lastLogin', new Date().toISOString());
      } else {
        console.warn('‚ö†Ô∏è  ÿ™ÿ≠ÿ∞Ÿäÿ±: ŸÑŸÖ Ÿäÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÅŸä ÿßŸÑÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ©');
        // ÿ•ŸÜÿ¥ÿßÿ° user ÿßŸÅÿ™ÿ±ÿßÿ∂Ÿä ŸÖŸÜ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©
        const defaultUser = {
          id: 1,
          name: email.split('@')[0],
          email: email,
          role: 'admin',
          username: email.split('@')[0]
        };
        localStorage.setItem('user', JSON.stringify(defaultUser));
      }
    }
    
    return response;
  } catch (error) {
    console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ:', error);
    return {
      success: false,
      message: error.response?.data?.message || 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ'
    };
  }
};

