import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { 
  Gamepad2, Clock, User, Phone, ShoppingCart, DollarSign, 
  CheckCircle, XCircle, Eye, Plus, Minus, Trash2, Calendar,
  Printer, Download, RefreshCw, AlertCircle, Check, X,
  Building, Zap, Lock, Unlock, Loader2, Filter, Search,
  ChevronRight, MapPin, Mail, Users, Package, RotateCcw,Calculator 
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const formatCurrency = (amount) => {
  if (!amount && amount !== 0) return '0 ุฌ.ู';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => {
  if (!phone || phone.trim() === '') return true;
  const cleaned = phone.replace(/\D/g, '');
  return cleaned.length === 11 && cleaned.startsWith('01');
};

const calculateDuration = (startTime, endTime) => {
  if (!startTime || !endTime) return 0;
  const start = new Date(startTime);
  const end = new Date(endTime);
  return Math.round((end - start) / (1000 * 60));
};

const Rentals = () => {
  const { user } = useAuth();
  const isManager = user?.role === 'admin' || user?.role === 'branch_manager';
  
  const [loading, setLoading] = useState({
    games: true,
    rentals: true,
    shift: true,
    stats: true
  });
  
  const [games, setGames] = useState([]);
  const [allGames, setAllGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [currentShift, setCurrentShift] = useState(null);
  const [selectedGames, setSelectedGames] = useState([]);
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [selectedRentalForChange, setSelectedRentalForChange] = useState(null);
  const [showRerentalModal, setShowRerentalModal] = useState(false);
  const [selectedCompletedRental, setSelectedCompletedRental] = useState(null);

  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

  const refreshAllData = useCallback(async (force = false) => {
    if (loading.games && loading.rentals && loading.shift && !force) {
      console.log('โณ ุงูุชุญุฏูุซ ููุฏ ุงูุชูููุฐ ุจุงููุนู...');
      return;
    }
    
    console.log('๐ ุชุญุฏูุซ ุดุงูู ููุจูุงูุงุช...');
    
    setLoading({ games: true, rentals: true, shift: true, stats: true });
    
    try {
      await loadGames();
      await loadCurrentShift();
      
      if (currentShift && currentShift.id) {
        await Promise.all([
          loadActiveRentalsForCurrentShift(),
          isManager ? loadCompletedRentals() : Promise.resolve()
        ]);
      }
    } catch (error) {
      console.error('๐ฅ ุฎุทุฃ ูู ุชุญุฏูุซ ุงูุจูุงูุงุช:', error);
    } finally {
      setTimeout(() => {
        setLoading({ games: false, rentals: false, shift: false, stats: false });
      }, 300);
    }
  }, [currentShift?.id, isManager]);

  const loadGames = useCallback(async () => {
    try {
      console.log('๐ฎ ุชุญููู ุงูุฃูุนุงุจ ูููุฑุน', user?.branch_id);
      
      if (!user?.branch_id) {
        console.log('โ๏ธ ูุง ููุฌุฏ ูุฑุน ูุญุฏุฏ');
        setGames([]);
        return;
      }
      
      const response = await api.loadGamesSmart(user.branch_id);
        
      if (response.success) {
        console.log(`โ ุชู ุชุญููู ${response.data.length} ูุนุจุฉ`);
        setGames(response.data);
        setAllGames(response.data);
        setStats(prev => ({
          ...prev,
          availableGames: response.data.length,
          totalGames: response.data.length
        }));
      } else {
        console.warn('โ๏ธ ูุดู ุชุญููู ุงูุฃูุนุงุจ:', response.message);
        setGames([]);
      }
    } catch (error) {
      console.error('โ ุฎุทุฃ ูู ุชุญููู ุงูุฃูุนุงุจ:', error);
      setGames([]);
    } finally {
      setLoading(prev => ({ ...prev, games: false }));
    }
  }, [user?.branch_id]);

  const loadCurrentShift = useCallback(async (forceRefresh = false) => {
    try {
      const response = await api.getCurrentShift();
      
      if (response.success && response.data) {
        const shift = response.data;
        
        if (shift.employee_id === user?.id || shift.employee_name === user?.name) {
          setCurrentShift(shift);
          localStorage.setItem('current_shift', JSON.stringify(shift));
          return shift;
        } else {
          console.warn('โ๏ธ ุงูุดููุช ุงููุดุท ูุง ููุชูู ูููุณุชุฎุฏู ุงูุญุงูู');
          setCurrentShift(null);
          localStorage.removeItem('current_shift');
          return null;
        }
      } else {
        setCurrentShift(null);
        localStorage.removeItem('current_shift');
        return null;
      }
    } catch (error) {
      console.error('๐ฅ ุฎุทุฃ ูู ุชุญููู ุงูุดููุช:', error);
      setCurrentShift(null);
      localStorage.removeItem('current_shift');
      return null;
    } finally {
      setLoading(prev => ({ ...prev, shift: false }));
    }
  }, [user?.id, user?.name]);

const loadActiveRentalsForCurrentShift = useCallback(async () => {
  if (!user?.branch_id || !currentShift?.id) {
    console.log('โ๏ธ ูุง ููุฌุฏ ูุฑุน ูุญุฏุฏ ุฃู ุดููุช ูุดุท');
    setActiveRentals([]);
    return;
  }
  
  try {
    setLoading(prev => ({ ...prev, rentals: true }));
    
    console.log(`๐ ุฌูุจ ุงูุชุฃุฌูุฑุงุช ุงููุดุทุฉ ููุดููุช #${currentShift.id}`);
    
    // ุงุณุชุฎุฏู ุงูููุทุฉ ุงูุนุงูุฉ ูุน ุงูููุชุฑุฉ
    const response = await api.get('/rentals', {
      params: {
        shift_id: currentShift.id,
        branch_id: user.branch_id,
        status: 'ูุดุท'
      }
    });
    
    if (response.success && response.data) {
      console.log(`โ ุชู ุชุญููู ${response.data.length} ุชุฃุฌูุฑ ูุดุท ููุดููุช ุงูุญุงูู`);
      setActiveRentals(response.data);
    } else {
      console.warn('โ๏ธ ูุดู ุฌูุจ ุงูุชุฃุฌูุฑุงุช:', response.message);
      setActiveRentals([]);
    }
    
  } catch (error) {
    console.error('๐ฅ ุฎุทุฃ ูู ุชุญููู ุงูุชุฃุฌูุฑุงุช ุงููุดุทุฉ:', error);
    setActiveRentals([]);
  } finally {
    setLoading(prev => ({ ...prev, rentals: false }));
  }
}, [currentShift?.id, user?.branch_id]);

const loadCompletedRentals = useCallback(async () => {
  if (!isManager || !currentShift?.id) return;
  
  try {
    // ุงุณุชุฎุฏู ุงูููุทุฉ ุงูุนุงูุฉ ูุน ุงูููุชุฑุฉ
    const response = await api.get('/rentals', {
      params: {
        shift_id: currentShift.id,
        branch_id: user?.branch_id,
        status: 'ููุชูู'
      }
    });
    
    if (response.success && response.data) {
      const completedRentalsData = response.data;
      
      setCompletedRentals(completedRentalsData);
      
      // โ ุญุณุงุจ ุงูุฅูุฑุงุฏ ุจุดูู ุตุญูุญ
      let completedRevenue = 0;
      completedRentalsData.forEach(rental => {
        if (rental.status === 'ููุชูู') {
          if (rental.rental_type === 'open' || rental.is_open_time === 1) {
            // ุงูููุช ุงูููุชูุญ: ุฃุถู final_amount ููุท (ุชู ุฏูุนู ุนูุฏ ุงูุฅููุงุก)
            completedRevenue += parseFloat(rental.final_amount || 0);
          } else {
            // ุงูููุช ุงูุซุงุจุช: ุฃุถู paid_amount (ุชู ุฏูุนู ุนูุฏ ุงูุฅูุดุงุก)
            completedRevenue += parseFloat(rental.paid_amount || rental.total_amount || 0);
          }
        }
      });
      
      setStats(prev => ({
        ...prev,
        todayRevenue: completedRevenue,
        todayRentals: completedRentalsData.length
      }));
    }
  } catch (error) {
    console.error('โ ุฎุทุฃ ูู ุฌูุจ ุงูุชุฃุฌูุฑุงุช ุงูููุชููุฉ:', error);
    setCompletedRentals([]);
  }
}, [currentShift?.id, isManager, user?.branch_id]);

  const processCompletedRentalsManual = useCallback(async () => {
    try {
      console.log('๐ ูุนุงูุฌุฉ ูุฏููุฉ ููุชุฃุฌูุฑุงุช ุงูููุชููุฉ...');
      
      if (!activeRentals || activeRentals.length === 0) {
        console.log('โ ูุง ุชูุฌุฏ ุชุฃุฌูุฑุงุช ูุดุทุฉ ุชุญุชุงุฌ ูุนุงูุฌุฉ');
        return { success: true, data: { processed_count: 0 } };
      }
      
      const now = new Date();
      let hasExpired = false;
      
      for (const rental of activeRentals) {
        if (!rental.start_time) continue;
        
        const rentalType = getRentalType(rental);
        const startTime = new Date(rental.start_time);
        
        if (rentalType === 'fixed') {
          const duration = rental.duration_minutes || 15;
          const endTime = new Date(startTime.getTime() + duration * 60000);
          
          if (now > endTime) {
            hasExpired = true;
            console.log(`โฐ ุชุฃุฌูุฑ ${rental.id} ุงูุชูู ููุชู (ุซุงุจุช)`);
          }
        }
      }
      
      if (!hasExpired) {
        console.log('โ ูุง ุชูุฌุฏ ุชุฃุฌูุฑุงุช ุงูุชูู ููุชูุง ุญุงููุงู');
        return { success: true, data: { processed_count: 0 } };
      }
      
      try {
        const response = await api.post('/rentals/process-completed-simple');
        console.log('โ ูุชูุฌุฉ ุงููุนุงูุฌุฉ ุงูุจุณูุทุฉ:', response);
        return response;
      } catch (error) {
        console.warn('โ๏ธ ูุดูุช ุงููุนุงูุฌุฉ ุงูุจุณูุทุฉ:', error.message);
        
        const expiredRentals = activeRentals.filter(rental => {
          if (!rental.start_time) return false;
          const startTime = new Date(rental.start_time);
          const rentalType = getRentalType(rental);
          
          if (rentalType === 'fixed') {
            const duration = rental.duration_minutes || 15;
            const endTime = new Date(startTime.getTime() + duration * 60000);
            return now > endTime;
          }
          return false;
        });
        
        if (expiredRentals.length === 0) {
          return { success: true, data: { processed_count: 0 } };
        }
        
        console.log(`๐ง ูุนุงูุฌุฉ ${expiredRentals.length} ุชุฃุฌูุฑ ูุฏููุงู`);
        
        for (const rental of expiredRentals) {
          try {
            if (rental.rental_type === 'open' || rental.is_open_time === 1) {
              await api.completeRental(rental.id, {
                payment_method: 'ูุงุด'
              });
            } else {
              await api.completeRental(rental.id, {
                payment_method: 'ูุงุด',
                final_amount: rental.paid_amount || rental.total_amount
              });
            }
            
            console.log(`โ ุชู ูุนุงูุฌุฉ ุงูุชุฃุฌูุฑ ${rental.id}`);
          } catch (rentalError) {
            console.warn(`โ๏ธ ูุดู ูุนุงูุฌุฉ ุงูุชุฃุฌูุฑ ${rental.id}:`, rentalError.message);
          }
          
          await new Promise(resolve => setTimeout(resolve, 300));
        }
        
        setTimeout(() => {
          refreshAllData();
        }, 2000);
        
        return { 
          success: true, 
          data: { 
            processed_count: expiredRentals.length,
            manual_processing: true 
          } 
        };
      }
      
    } catch (error) {
      console.error('๐ฅ ุฎุทุฃ ูู ุงููุนุงูุฌุฉ ุงููุฏููุฉ:', error);
      return {
        success: false,
        message: 'ุฎุทุฃ ูู ุงููุนุงูุฌุฉ: ' + error.message
      };
    }
  }, [activeRentals, refreshAllData]);

  const getRentalType = useCallback((rental) => {
    if (!rental) return 'fixed';
    
    // ุงูุชุญูู ูู ููุน ุงูุชุฃุฌูุฑ ูุฑุฉ ูุงุญุฏุฉ ููุท
    if (rental.rental_type === 'open') {
      return 'open';
    }
    
    if (rental.rental_type === 'fixed') {
      return 'fixed';
    }
    
    if (rental.is_open_time === 1) {
      return 'open';
    }
    
    if (rental.is_open_time === 0) {
      return 'fixed';
    }
    
    // ุงูุชุญูู ูู ุญุงูุฉ ุงูุฏูุน ูุจุฏูู
    if (rental.payment_status === 'ุนูุฏ ุงูุฅููุงุก' || rental.payment_status === 'pending') {
      return 'open';
    }
    
    if (rental.payment_status === 'ูุฏููุน ูุณุจูุงู' || rental.payment_status === 'paid') {
      return 'fixed';
    }
    
    return 'fixed';
  }, []);

const calculateRealRevenue = useCallback(async () => {
  if (!currentShift || !currentShift.id) return 0;
  
  try {
    // ุงุณุชุฎุฏู ุงูููุทุฉ ุงูุตุญูุญุฉ ูุฌูุจ ุงูุชุฃุฌูุฑุงุช
    const response = await api.get('/rentals', {
      params: {
        shift_id: currentShift.id,
        branch_id: user?.branch_id
      }
    });
    
    if (response.success && response.data) {
      const rentals = Array.isArray(response.data) ? response.data : [];
      
      let totalRevenue = 0;
      
      rentals.forEach(rental => {
        // ุงูููุช ุงูุซุงุจุช: ุฅูุฑุงุฏ ุนูุฏ ุงูุฅูุดุงุก
        // ุงูููุช ุงูููุชูุญ: ุฅูุฑุงุฏ ุนูุฏ ุงูุฅูุชูุงุก ููุท
        if (rental.status === 'ููุชูู') {
          // โ ุงูููุช ุงูููุชูุญ: ุฃุถู final_amount
          if (rental.rental_type === 'open' || rental.is_open_time === 1) {
            totalRevenue += parseFloat(rental.final_amount || 0);
          } 
          // โ ุงูููุช ุงูุซุงุจุช: ุฃุถู paid_amount (ุชู ุฅุถุงูุชู ููุฅูุฑุงุฏ ุนูุฏ ุงูุฅูุดุงุก)
          else {
            totalRevenue += parseFloat(rental.paid_amount || rental.total_amount || 0);
          }
        }
      });
      
      return totalRevenue;
    }
  } catch (error) {
    console.error('โ ุฎุทุฃ ูู ุญุณุงุจ ุงูุฅูุฑุงุฏ ุงูุญูููู:', error);
