              ุชุฃููุฏ ุฅุนุงุฏุฉ ุงูุชุฃุฌูุฑ
            </button>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="rentals-container">
      <div className="rentals-header">
        <div className="rentals-title-section">
          <h1>
            <Gamepad2 size={28} />
            ูุธุงู ุฅุฏุงุฑุฉ ุงูุชุฃุฌูุฑ
          </h1>
          <p className="rentals-subtitle">
            {user?.branch_name ? `ูุฑุน: ${user.branch_name}` : 'ุงูุฑุฌุงุก ุชุญุฏูุฏ ูุฑุน'}
          </p>
        </div>
        
        <div className="rentals-header-actions">
          <button
            onClick={() => refreshAllData(true)}
            className="rentals-btn rentals-btn-outline"
            disabled={loading.rentals || loading.games}
          >
            <RefreshCw size={18} className={loading.rentals || loading.games ? 'rentals-spinner' : ''} />
            ุชุญุฏูุซ ุงูุจูุงูุงุช
          </button>
        </div>
      </div>

      <DashboardStats
        stats={stats}
        currentShift={currentShift}
        onStartShift={handleStartShift}
        onEndShift={handleEndShift}
        loading={loading.shift || loading.stats}
      />

      <div className="rentals-content-grid">
        <div className="rentals-main-content">
          <GamesGrid
            games={games}
            branchId={user?.branch_id}
            onSelectGame={handleSelectGame}
            loading={loading.games}
          />
          
          <ActiveRentalsTable
            rentals={activeRentals}
            onRentalAction={handleRentalAction}
            loading={loading.rentals}
          />
        </div>
      </div>

      {showRentalModal && (
        <RentalModal
          show={showRentalModal}
          onClose={() => setShowRentalModal(false)}
          selectedGames={selectedGames}
          onConfirmRental={handleCreateRental}
          currentShift={currentShift}
          allGames={allGames}
        />
      )}

      {showChangeGameModal && (
        <ChangeGameModal
          show={showChangeGameModal}
          onClose={() => {
            setShowChangeGameModal(false);
            setSelectedRentalForChange(null);
          }}
          rental={selectedRentalForChange}
          games={games}
          onConfirm={confirmChangeGame}
        />
      )}

      {showRerentalModal && (
        <RerentalModal
          show={showRerentalModal}
          onClose={() => {
            setShowRerentalModal(false);
            setSelectedCompletedRental(null);
          }}
          rental={selectedCompletedRental}
          games={games}
          onConfirm={confirmRerental}
        />
      )}
    </div>
  );
};

export default Rentals;
