// frontend/src/components/Layout.jsx
import React, { useState, useEffect } from 'react';
import { Link, Outlet, useNavigate } from 'react-router-dom';
import authService from '../services/authService';
import './Layout.css';

const Layout = () => {
  const [user, setUser] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖŸÜ localStorage
    const currentUser = authService.getCurrentUser();
    if (!currentUser) {
      // ÿ•ÿ∞ÿß ŸÑŸÖ ŸäŸÉŸÜ ŸáŸÜÿßŸÉ ŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿ≥ÿ¨ŸÑÿå ÿ™Ÿàÿ¨ŸäŸá ÿ•ŸÑŸâ ÿµŸÅÿ≠ÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ
      navigate('/login');
      return;
    }
    
    setUser(currentUser);
    console.log('üë§ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿ≠ÿßŸÑŸä:', currentUser);
  }, [navigate]);

  const handleLogout = () => {
    authService.logout();
  };

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  // ÿ™ÿ≠ÿØŸäÿØ ÿßŸÑÿ£ÿØŸàÿßÿ± ÿßŸÑŸÖÿ≥ŸÖŸàÿ≠ ÿ®Ÿáÿß
  const isAdmin = user?.role === 'admin';
  const isManager = user?.role === 'branch_manager';
  const isEmployee = user?.role === 'employee';

  if (!user) {
    return <div>ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ...</div>;
  }

  return (
    <div className={`layout ${sidebarOpen ? 'sidebar-open' : 'sidebar-closed'}`}>
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="sidebar-header">
          <h2>L3BTY</h2>
          <button className="sidebar-toggle" onClick={toggleSidebar}>
            {sidebarOpen ? '‚óÄ' : '‚ñ∂'}
          </button>
        </div>
        
        <div className="user-info">
          <div className="user-avatar">
            {user.name.charAt(0)}
          </div>
          <div className="user-details">
